import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Manager = () => {
    const [transactions, setTransactions] = useState([]);
    const [filteredTransactions, setFilteredTransactions] = useState([]);
    const [dateFilter, setDateFilter] = useState('');
    const [loading, setLoading] = useState(true);

    // Fungsi untuk mengautentikasi manajer (login)
    const handleLogin = async () => {
        // Implementasi login, bisa menggunakan form login
        // Contoh:
        try {
            const response = await axios.post('http://localhost:8000/login', { /* data login */ });
            // Simpan token/response jika diperlukan
            console.log("Login successful:", response.data);
        } catch (error) {
            console.error("Login failed:", error);
        }
    };

    // Mengambil data transaksi
    const fetchTransactions = async () => {
        try {
            const response = await axios.get('http://localhost:8000/transaksi');
            console.log("Fetched transactions:", response.data); // Log untuk memeriksa data yang diterima
            
            if (Array.isArray(response.data)) {
                setTransactions(response.data);
                setFilteredTransactions(response.data);
            } else {
                console.error("Expected an array but received:", response.data);
            }
            setLoading(false);
        } catch (error) {
            console.error("Error fetching transactions:", error);
            setLoading(false);
        }
    };

    // Filter transaksi berdasarkan tanggal
    const handleDateFilter = () => {
        if (dateFilter) {
            const filtered = transactions.filter(transaction => 
                new Date(transaction.date).toLocaleDateString() === new Date(dateFilter).toLocaleDateString()
            );
            setFilteredTransactions(filtered);
        } else {
            setFilteredTransactions(transactions);
        }
    };

    useEffect(() => {
        fetchTransactions();
    }, []);

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">Manager Dashboard</h1>
            <button onClick={handleLogin} className="mb-4 p-2 bg-blue-500 text-white rounded">Login</button>

            <div className="mb-4">
                <input
                    type="date"
                    value={dateFilter}
                    onChange={(e) => setDateFilter(e.target.value)}
                    className="border p-2 rounded"
                />
                <button onClick={handleDateFilter} className="ml-2 p-2 bg-green-500 text-white rounded">Filter</button>
            </div>

            {loading ? (
                <p>Loading transactions...</p>
            ) : (
                <table className="min-w-full border">
                    <thead>
                        <tr>
                            <th className="border">ID</th>
                            <th className="border">Karyawan</th>
                            <th className="border">Tanggal</th>
                            <th className="border">Jumlah</th>
                        </tr>
                    </thead>
                    <tbody>
                        {Array.isArray(filteredTransactions) && filteredTransactions.length > 0 ? (
                            filteredTransactions.map(transaction => (
                                <tr key={transaction.id}>
                                    <td className="border">{transaction.id}</td>
                                    <td className="border">{transaction.karyawan}</td>
                                    <td className="border">{transaction.date}</td>
                                    <td className="border">{transaction.jumlah}</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="4" className="border text-center">No transactions found</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Manager;
