import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManageFoods = () => {
    const [foods, setFoods] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const [foodData, setFoodData] = useState({ id_menu: '', nama_menu: '', harga: '', deskripsi: '', gambar: '' });
    const [showForm, setShowForm] = useState(false);
     const [showDetails, setShowDetails] = useState(false);  // Untuk mengontrol modal
  const [selectedFood, setSelectedFood] = useState(null); // Menyimpan data makanan yang dipilih


    useEffect(() => {
        fetchFoods();
    }, []);

    const fetchFoods = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/menu', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setFoods(data.data || []);
            } else {
                console.error('Failed to fetch foods');
            }
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    const handleDeleteFood = async (foodId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8000/menu/${foodId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.ok) {
                fetchFoods();
            } else {
                console.error('Failed to delete food');
            }
        } catch (error) {
            console.error('Error deleting food:', error);
        }
    };

    const handleSaveFood = async () => {
        try {
            const token = localStorage.getItem('token');
            const method = isEditing ? 'PUT' : 'POST';
            const url = isEditing
                ? `http://localhost:8000/menu/${foodData.id_menu}`
                : 'http://localhost:8000/menu';

            const response = await fetch(url, {
                method: method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({
                    nama_menu: foodData.nama_menu,
                    harga: parseInt(foodData.harga),
                    deskripsi: foodData.deskripsi,
                    gambar: foodData.gambar,
                }),
            });

            if (response.ok) {
                setShowForm(false);
                setFoodData({ id_menu: '', nama_menu: '', harga: '', deskripsi: '', gambar: '' });
                fetchFoods();
            } else {
                console.error('Failed to save food');
            }
        } catch (error) {
            console.error('Error saving food:', error);
        }
    };

    const openFormToEdit = (food) => {
        setIsEditing(true);
        setFoodData(food);
        setShowForm(true);
    };

    const openFormToAdd = () => {
        setIsEditing(false);
        setFoodData({ id_menu: '', nama_menu: '', harga: '', deskripsi: '', gambar: '' });
        setShowForm(true);
    };

    const handleViewDetails = (food) => {
        // Contoh: Menampilkan modal atau alert dengan detail informasi makanan
        alert(`
          Food Name: ${food.nama_menu}
          Price: ${food.harga.toLocaleString('id-ID', {
            style: 'currency',
            currency: 'IDR',
          })}
          Description: ${food.deskripsi}
        `);
      
        // Atau, Anda dapat menampilkan modal atau mengarahkan ke halaman detail makanan
        // Misalnya, setShowDetailsModal(true) untuk menampilkan modal
      };
      

    return (
        <>
          <Sidebar />
          <div className="mt-4">
            <div className="flex-grow p-8 bg-white shadow-lg rounded-lg mx-6 mt-4 mb-4">
              <h1 className="text-4xl font-semibold text-pastelDarkRed mb-10 text-center">
                Manage Foods
              </h1>
      
              {/* Button Tambah Menu */}
              <button
                className="bg-green-500 text-white rounded-lg px-4 py-2 mb-4"
                onClick={openFormToAdd}
              >
                Add Food
              </button>
      
              {/* Container mapping untuk setiap food item */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {foods.map((food) => (
                  <div
                    key={food.id_menu}
                    className="bg-white shadow-md rounded-lg p-4 flex flex-col items-center"
                  >
                    <img
                      src={'http://localhost:8000/menu-image/' + food.gambar}
                      alt={food.nama_menu}
                      className="w-32 h-32 object-cover rounded-lg mb-4"
                    />
                    <h2 className="text-xl font-semibold mb-2">{food.nama_menu}</h2>
                    <p className="text-gray-700 mb-2">
                      {food.harga.toLocaleString('id-ID', {
                        style: 'currency',
                        currency: 'IDR',
                      })}
                    </p>
                    <button
                      className="bg-blue-500 text-white rounded-lg px-4 py-2 mb-2"
                      onClick={() => handleViewDetails(food)}
                    >
                      View Details
                    </button>
                    <div className="flex space-x-2">
                      <button
                        className="bg-blue-500 text-white rounded-lg px-4 py-2"
                        onClick={() => openFormToEdit(food)}
                      >
                        Edit
                      </button>
                      <button
                        className="bg-red-500 text-white rounded-lg px-4 py-2"
                        onClick={() => handleDeleteFood(food.id_menu)}
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
      
            {/* Modal for Add/Edit Form */}
            {showForm && (
              <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
                <div className="bg-white p-6 rounded-lg shadow-lg">
                  <h2 className="text-2xl font-semibold mb-4">
                    {isEditing ? 'Edit Food' : 'Add Food'}
                  </h2>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">
                      Food Name
                    </label>
                    <input
                      type="text"
                      className="border border-gray-300 p-2 rounded-lg w-full"
                      value={foodData.nama_menu}
                      onChange={(e) =>
                        setFoodData({ ...foodData, nama_menu: e.target.value })
                      }
                    />
                  </div>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">
                      Price
                    </label>
                    <input
                      type="number"
                      className="border border-gray-300 p-2 rounded-lg w-full"
                      value={foodData.harga}
                      onChange={(e) =>
                        setFoodData({ ...foodData, harga: e.target.value })
                      }
                    />
                  </div>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">
                      Description
                    </label>
                    <textarea
                      className="border border-gray-300 p-2 rounded-lg w-full"
                      value={foodData.deskripsi}
                      onChange={(e) =>
                        setFoodData({ ...foodData, deskripsi: e.target.value })
                      }
                    />
                  </div>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">
                      Image URL
                    </label>
                    <input
                      type="text"
                      className="border border-gray-300 p-2 rounded-lg w-full"
                      value={foodData.gambar}
                      onChange={(e) =>
                        setFoodData({ ...foodData, gambar: e.target.value })
                      }
                    />
                  </div>
                  <div className="flex justify-end space-x-4">
                    <button
                      className="bg-gray-500 text-white rounded-lg px-4 py-2"
                      onClick={() => setShowForm(false)}
                    >
                      Cancel
                    </button>
                    <button
                      className="bg-blue-500 text-white rounded-lg px-4 py-2"
                      onClick={handleSaveFood}
                    >
                      {isEditing ? 'Save Changes' : 'Add Food'}
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </>
      );
      };
      
      export default ManageFoods;
