import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManagerPage = ({ user }) => {
    const [selectedTransaction, setSelectedTransaction] = useState(null);
    const [transactions, setTransactions] = useState([]);
    const [filteredTransactions, setFilteredTransactions] = useState([]);
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchAllTransactions();
    }, []);

    const fetchAllTransactions = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/transaksi', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.ok) {
                const data = await response.json();
                setTransactions(data.data || []);
                setFilteredTransactions(data.data || []);
            } else {
                console.error('Failed to fetch transactions');
            }
        } catch (error) {
            console.error('Error fetching transactions:', error);
        }
    };

    const handleDateFilter = () => {
        if (!startDate || !endDate) {
            setErrorMessage('Both start and end date must be selected!');
            return;
        }

        const filtered = transactions.filter(transaction => {
            const transactionDate = new Date(transaction.tgl_transaksi);
            return transactionDate >= new Date(startDate) && transactionDate <= new Date(endDate);
        });

        setFilteredTransactions(filtered);
        setErrorMessage('');
    };

    const formatDate = (date) => {
        return new Date(date).toLocaleDateString();
    };

    const calculateTotalTransaction = (transaction) => {
        return transaction.detail_transaksi.reduce((total, item) => {
            return total + (item.harga * item.quantity);
        }, 0);
    };

        const getPaymentStatus = (isPaid) => {
            return isPaid ? 'Sudah Dibayar' : 'Belum Dibayar';
        };
    
        return (
            <>
            <Sidebar />
            <div className="flex min-h-screen bg-pastelPink">
    
                <div className="flex-grow p-8 bg-white shadow-lg rounded-lg mx-6 mt-4 mb-4">
                    <h1 className="text-4xl font-semibold text-pastelDarkRed mb-10 text-center">
                        Manager Reports
                    </h1>
    
                    {/* Date Filtering */}
                    <div className="flex items-center justify-center mb-8 space-x-4">
                        <div className="flex flex-col">
                            <label htmlFor="startDate" className="text-pastelDarkRed font-medium mb-1">Start Date</label>
                            <input
                                type="date"
                                id="startDate"
                                className="border border-pastelRose rounded-lg p-2 bg-pastelLightPink text-pastelDarkRed focus:ring-pastelRose focus:border-pastelRose transition duration-200"
                                value={startDate}
                                onChange={(e) => setStartDate(e.target.value)}
                            />
                        </div>
    
                        <div className="flex flex-col">
                            <label htmlFor="endDate" className="text-pastelDarkRed font-medium mb-1">End Date</label>
                            <input
                                type="date"
                                id="endDate"
                                className="border border-pastelRose rounded-lg p-2 bg-pastelLightPink text-pastelDarkRed focus:ring-pastelRose focus:border-pastelRose transition duration-200"
                                value={endDate}
                                onChange={(e) => setEndDate(e.target.value)}
                            />
                        </div>
    
                        <button
                            className="ml-4 bg-pastelRose text-white px-6 py-2 rounded-lg shadow-lg hover:bg-pastelRed transition duration-300 transform hover:scale-105"
                            onClick={handleDateFilter}
                        >
                            Filter
                        </button>
                    </div>
    
                    {errorMessage && <p className="text-pastelRed text-center mb-4">{errorMessage}</p>}
    
                    {/* Transaction List as Containers */}
                    {filteredTransactions.length > 0 ? (
                        <div className="space-y-6">
                            {filteredTransactions.map((transaction, index) => (
                                <div
                                    key={index}
                                    className="p-6 bg-white rounded-lg shadow-lg flex flex-col space-y-3 hover:shadow-2xl transition-shadow duration-300"
                                >
                                    <div className="flex justify-between items-center">
                                        <h3 className="text-xl font-semibold text-pastelDarkRed">
                                            Tanggal: {formatDate(transaction.tgl_transaksi)}
                                        </h3>
                                        <button
                                            onClick={() => setSelectedTransaction(transaction)}
                                            className="bg-pastelRose text-white px-4 py-2 rounded-lg shadow-lg hover:bg-pastelRed transition duration-300 transform hover:scale-105"
                                        >
                                            View Details
                                        </button>
                                    </div>
    
                                    <p className="text-gray-700">Pelanggan: {transaction.nama_pelanggan}</p>
                                    <p className="text-gray-700">Meja: {transaction.id_meja}</p>
                                    <p className="text-gray-700">Karyawan: {transaction.id_user}</p>
                                    <p className="text-gray-700">Menu Dipesan: {transaction.menu.map((item) => item.nama_menu).join(', ')}</p>
                                    <p className="text-gray-700">Status Pembayaran: {getPaymentStatus(transaction.isPaid)}</p>
                                    <p className="text-gray-700 font-bold">
                                        Total: Rp {calculateTotalTransaction(transaction).toLocaleString()}
                                    </p>
                                </div>
                            ))}
                        </div>
                    ) : (
                        <p className="text-pastelDarkRed mt-6 text-center">Tidak ada transaksi ditemukan</p>
                    )}
    
                    {/* Modal for Selected Transaction Details */}
                    {selectedTransaction && (
                        <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex justify-center items-center z-50">
                            <div className="bg-white p-6 rounded-lg shadow-lg w-96">
                                <h2 className="text-2xl font-semibold text-pastelDarkRed mb-4">
                                    Transaction Details
                                </h2>
                                <p>Tanggal: {formatDate(selectedTransaction.tgl_transaksi)}</p>
                                <p>Pelanggan: {selectedTransaction.nama_pelanggan}</p>
                                <p>Meja: {selectedTransaction.id_meja}</p>
                                <p>Karyawan: {selectedTransaction.id_user}</p>
                                <p>Menu Dipesan: {selectedTransaction.menu.map((item) => item.nama_menu).join(', ')}</p>
                                <p>Status Pembayaran: {getPaymentStatus(selectedTransaction.isPaid)}</p>
                                <p>Total: Rp {calculateTotalTransaction(selectedTransaction).toLocaleString()}</p>
                                <button
                                    onClick={() => setSelectedTransaction(null)}
                                    className="mt-4 bg-pastelRose text-white px-4 py-2 rounded-lg shadow-lg hover:bg-pastelRed transition duration-300 transform hover:scale-105"
                                >
                                    Close
                                </button>
                            </div>
                        </div>
                    )}
                </div>
            </div>
            </>
        );
    };
    
    export default ManagerPage;
