import React from 'react';
import { Link } from 'react-router-dom';
import 'tailwindcss/tailwind.css';


const Sidebar = ({ role, username }) => {
  return (
    <div className="bg-pink-200 w-64 h-full shadow-md">
      <div className="p-4">
        <h3 className="text-xl font-semibold text-gray-700">Hi, {username}</h3>
        <p className="text-gray-600">Role: {role}</p>
      </div>
      <ul className="mt-4">
        {role === 'kasir' && (
          <>
            <li className="p-4 hover:bg-pink-300"><Link to="/cashier">Transaksi Kasir</Link></li>
            <li className="p-4 hover:bg-pink-300"><Link to="/transactions">Daftar Transaksi</Link></li>
          </>
        )}
        {role === 'manajer' && (
          <>
            <li className="p-4 hover:bg-pink-300"><Link to="/manager">Monitoring Transaksi</Link></li>
          </>
        )}
        {role === 'admin' && (
          <>
            <li className="p-4 hover:bg-pink-300"><Link to="/admin">Pengelolaan Data User</Link></li>
            <li className="p-4 hover:bg-pink-300"><Link to="/menu">Pengelolaan Menu</Link></li>
            <li className="p-4 hover:bg-pink-300"><Link to="/tables">Pengelolaan Meja</Link></li>
          </>
        )}
      </ul>
    </div>
  );
};

export default Sidebar;
