import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import 'tailwindcss/tailwind.css';
import Sidebar from './Sidebar'; // Import Sidebar
import CafeLogo from '../assets/logo.jpg';

const Cashier = ({ user }) => {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const [tables, setTables] = useState([]);
    const [selectedMenu, setSelectedMenu] = useState([]);
    const [selectedMenuItem, setSelectedMenuItem] = useState(null);
    const [isTableOccupied, setIsTableOccupied] = useState(false);
    const [customerName, setCustomerName] = useState('');
    const [tableNumber, setTableNumber] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [transactionReceipt, setTransactionReceipt] = useState(null);
    const [isPaid, setIsPaid] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchMenuItems();
        fetchTableNumbers();
    }, []);

    const fetchMenuItems = async () => {
        try {
            const response = await fetch('http://localhost:8000/menu');
            const data = await response.json();
            setMenuItems(data.data || []);
        } catch (error) {
            console.error('Error fetching menu items:', error);
        }
    };

    const fetchTableNumbers = async () => {
        try {
            const response = await fetch('http://localhost:8000/meja');
            const data = await response.json();
            setTables(data.data || []);
        } catch (error) {
            console.error('Error fetching table numbers:', error);
        }
    };

    const handleTableCheck = (selectedTable) => {
        const table = tables.find((t) => t.nomor_meja === selectedTable);
        const occupied = table ? table.status === 'terisi' : false;
        setIsTableOccupied(occupied);
        setTableNumber(selectedTable);
    };

    const handleAddMenu = () => {
        if (!selectedMenuItem || quantity < 1) {
            setErrorMessage('Silakan pilih menu dan isi kuantitas terlebih dahulu.');
            return;
        }

        const existingMenu = selectedMenu.find(item => item.id_menu === selectedMenuItem.id_menu);
        if (existingMenu) {
            existingMenu.quantity += quantity; // Tambah kuantitas jika menu sudah ada
        } else {
            const newMenu = {
                ...selectedMenuItem,
                quantity: quantity,
            };
            setSelectedMenu((prev) => [...prev, newMenu]); // Tambahkan menu baru
        }

        setQuantity(1); // Reset kuantitas
        setErrorMessage('');
    };

    const handleMenuChange = (e) => {
        const selectedId = e.target.value;
        const menuItem = menuItems.find(item => item.id_menu === parseInt(selectedId));
        setSelectedMenuItem(menuItem || null); // Set selected menu item
    };

    const handleQuantityChange = (type) => {
        if (type === 'increment') {
            setQuantity((prevQuantity) => prevQuantity + 1);
        } else if (type === 'decrement' && quantity > 1) {
            setQuantity((prevQuantity) => prevQuantity - 1);
        }
    };

    const handleSubmitTransaction = async () => {
        if (!customerName || !tableNumber) {
            setErrorMessage('Nama pelanggan dan nomor meja harus diisi!');
            return;
        }

        if (selectedMenu.length === 0) {
            setErrorMessage('Silakan tambahkan setidaknya satu menu sebelum submit!');
            return;
        }

        const newTransaction = {
            tgl_transaksi: new Date().toISOString(),
            id_meja: tableNumber,
            nama_pelanggan: customerName,
            status: 'belum_bayar',
            detail_transaksi: selectedMenu.map(item => ({
                id_menu: item.id_menu,
                quantity: item.quantity,
            })),
        };

        try {
            const response = await fetch('http://localhost:8000/transaksi', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(newTransaction),
            });

            if (response.ok) {
                alert('Transaksi berhasil ditambahkan!');

                // Update table status to "terisi"
                await updateTableStatus(tableNumber, 'terisi');

                setTransactionReceipt(newTransaction);
                setSelectedMenu([]);
                setCustomerName('');
                setTableNumber('');
            } else {
                const errorText = await response.text(); // Ambil teks dari respon
                console.error('Failed to add transaction:', errorText); // Tampilkan pesan kesalahan di console
                setErrorMessage('Gagal menambahkan transaksi. Coba lagi!');
            }
        } catch (error) {
            console.error('Error submitting transaction:', error);
        }
    };

    const updateTableStatus = async (tableNumber, status) => {
        try {
            const response = await fetch(`http://localhost:8000/meja/${tableNumber}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ status }),
            });

            if (!response.ok) {
                throw new Error('Failed to update table status');
            }
        } catch (error) {
            console.error('Error updating table status:', error);
        }
    };

    const handlePayment = async () => {
        const updatedReceipt = {
            ...transactionReceipt,
            status: "lunas"
        };

        try {
            const response = await fetch(`http://localhost:8000/transaksi/${transactionReceipt.id_meja}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(updatedReceipt),
            });

            if (response.ok) {
                alert('Pembayaran berhasil!');
                setIsPaid(true);
                setTransactionReceipt(updatedReceipt);
            }
        } catch (error) {
            console.error('Error processing payment:', error);
        }
    };

    const handleDone = () => {
        setSelectedMenu([]);
        setTransactionReceipt(null);
        setIsPaid(false);
        window.location.reload();
    };

    const calculateReceiptTotalHarga = () => {
        if (!transactionReceipt) return 0;

        return transactionReceipt.detail_transaksi.reduce((total, item) => {
            const menu = menuItems.find(menuItem => menuItem.id_menu === item.id_menu);
            return total + (menu ? menu.harga * item.quantity : 0);
        }, 0);
    };

    const formatDate = (date) => {
        return new Date(date).toLocaleString();
    };

    const renderReceipt = () => (
        <div className="mt-6 p-6 bg-white rounded-lg shadow-lg">
            <h2 className="text-2xl font-bold text-[#b7122d] text-center mb-4">Belle Fiori</h2>
            <div className="flex justify-center mb-4">
                <img src={CafeLogo} alt="Cafe Logo" className="w-24 h-24" /> {/* Replace with actual logo */}
            </div>
            <p className="text-center text-gray-700 mb-4">114 Elliot, Riverdale, Jakarta</p>
            <p className="text-center text-gray-700 mb-2">Date: {formatDate(transactionReceipt.tgl_transaksi)}</p>
            <p className="text-center text-gray-700 mb-4">Customer Name: {transactionReceipt.nama_pelanggan}</p>
            
            <table className="w-full mb-4">
                <thead>
                    <tr className="text-left">
                        <th className="border-b-2 border-gray-300 py-2">Menu</th>
                        <th className="border-b-2 border-gray-300 py-2">Quantity</th>
                        <th className="border-b-2 border-gray-300 py-2">Price</th>
                    </tr>
                </thead>
                <tbody>
                    {transactionReceipt.detail_transaksi.map((item, index) => {
                        const menu = menuItems.find(menuItem => menuItem.id_menu === item.id_menu);
                        return (
                            <tr key={index}>
                                <td className="py-2">{menu?.nama_menu}</td>
                                <td className="py-2">{item.quantity}</td>
                                <td className="py-2">{menu?.harga} IDR</td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>

            <div className="flex justify-between font-bold text-[#b7122d]">
                <span>Subtotal:</span>
                <span>{calculateReceiptTotalHarga()} IDR</span>
            </div>

            <div className="text-center mt-4">
                <p>Status: <span className={`font-bold ${isPaid ? 'text-green-500' : 'text-red-500'}`}>{isPaid ? 'Lunas' : 'Belum Bayar'}</span></p>
            </div>

            <div className="mt-6 text-center">
                <button
                    type="button"
                    onClick={handleDone}
                    className="bg-[#b7122d] text-white py-2 px-4 rounded-lg hover:bg-[#c44c5e] transition duration-300"
                >
                    Selesai
                </button>
            </div>
        </div>
    );

    return (
        <div className="flex h-screen">
            <Sidebar user={user} />
            <div className="flex-grow p-6 overflow-auto bg-[#fee3e2]">
                <h1 className="text-3xl font-bold mb-4 text-center text-[#b7122d]">Halaman Kasir</h1>
                <div className="bg-white p-6 rounded-lg shadow-md">
                    <h2 className="text-xl font-semibold mb-4">Transaksi Baru</h2>
                    <form>
                        <div className="mb-4">
                            <label className="block text-gray-700 mb-2">Nama Pelanggan:</label>
                            <input
                                type="text"
                                value={customerName}
                                onChange={(e) => setCustomerName(e.target.value)}
                                className="border rounded-lg p-2 w-full"
                                placeholder="Masukkan nama pelanggan"
                            />
                        </div>
                        <div className="mb-4">
                            <label className="block text-gray-700 mb-2">Nomor Meja:</label>
                            <select
                                value={tableNumber}
                                onChange={(e) => handleTableCheck(e.target.value)}
                                className="border rounded-lg p-2 w-full"
                            >
                                <option value="">Pilih Nomor Meja</option>
                                {tables.map((table, index) => (
                                    <option key={index} value={table.nomor_meja}>
                                        {table.nomor_meja} - {table.status === 'terisi' ? 'Terisi' : 'Kosong'}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="mb-4">
                            <label className="block text-gray-700 mb-2">Pilih Menu:</label>
                            <select
                                value={selectedMenuItem ? selectedMenuItem.id_menu : ''}
                                onChange={handleMenuChange}
                                className="border rounded-lg p-2 w-full"
                            >
                                <option value="">Pilih Menu</option>
                                {menuItems.map((menuItem) => (
                                    <option key={menuItem.id_menu} value={menuItem.id_menu}>
                                        {menuItem.nama_menu}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="mb-4">
                            <label className="block text-gray-700 mb-2">Kuantitas:</label>
                            <div className="flex items-center">
                                <button
                                    type="button"
                                    onClick={() => handleQuantityChange('decrement')}
                                    className="bg-[#b7122d] text-white rounded-l-lg px-4"
                                >
                                    -
                                </button>
                                <input
                                    type="number"
                                    value={quantity}
                                    readOnly
                                    className="border-t border-b text-center w-12"
                                />
                                <button
                                    type="button"
                                    onClick={() => handleQuantityChange('increment')}
                                    className="bg-[#b7122d] text-white rounded-r-lg px-4"
                                >
                                    +
                                </button>
                            </div>
                        </div>
                        {errorMessage && <p className="text-red-500">{errorMessage}</p>}
                        <button
                            type="button"
                            onClick={handleAddMenu}
                            className="bg-[#b7122d] text-white py-2 px-4 rounded-lg hover:bg-[#c44c5e] transition duration-300"
                        >
                            Tambah Menu
                        </button>
                        <button
                            type="button"
                            onClick={handleSubmitTransaction}
                            className="bg-[#b7122d] text-white py-2 px-4 rounded-lg hover:bg-[#c44c5e] transition duration-300 mt-4"
                        >
                            Proses Transaksi
                        </button>
                    </form>
                </div>

                {transactionReceipt && renderReceipt()}
            </div>
        </div>
    );
};

export default Cashier;
