import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import 'tailwindcss/tailwind.css';
import Sidebar from './Sidebar'; // Import Sidebar

const CashierPage = ({ user }) => {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const [tables, setTables] = useState([]);
    const [selectedMenu, setSelectedMenu] = useState([]);
    const [selectedMenuItem, setSelectedMenuItem] = useState(null);
    const [isTableOccupied, setIsTableOccupied] = useState(false);
    const [customerName, setCustomerName] = useState('');
    const [tableNumber, setTableNumber] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [transactionReceipt, setTransactionReceipt] = useState(null);
    const [isPaid, setIsPaid] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchMenuItems();
        fetchTableNumbers();
    }, []);

    const fetchMenuItems = async () => {
        try {
            const response = await fetch('http://localhost:22077/menu');
            const data = await response.json();
            setMenuItems(data.data || []);
        } catch (error) {
            console.error('Error fetching menu items:', error);
        }
    };

    const fetchTableNumbers = async () => {
        try {
            const response = await fetch('http://localhost:22077/meja');
            const data = await response.json();
            setTables(data.data || []);
        } catch (error) {
            console.error('Error fetching table numbers:', error);
        }
    };

    const handleTableCheck = (selectedTable) => {
        const table = tables.find((t) => t.nomor_meja === selectedTable);
        const occupied = table ? table.status === 'terisi' : false;
        setIsTableOccupied(occupied);
        setTableNumber(selectedTable);
    };

    const handleAddMenu = () => {
        if (!selectedMenuItem || quantity < 1) {
            setErrorMessage('Silakan pilih menu dan isi kuantitas terlebih dahulu.');
            return;
        }

        const existingMenu = selectedMenu.find(item => item.id_menu === selectedMenuItem.id_menu);
        if (existingMenu) {
            existingMenu.quantity += quantity; // Tambah kuantitas jika menu sudah ada
        } else {
            const newMenu = {
                ...selectedMenuItem,
                quantity: quantity,
            };
            setSelectedMenu((prev) => [...prev, newMenu]); // Tambahkan menu baru
        }

        setQuantity(1); // Reset kuantitas
        setErrorMessage('');
    };

    const handleOrderSubmit = (e) => {
        const selectedId = e.target.value;
        const menuItem = menuItems.find(item => item.id_menu === parseInt(selectedId));
        setSelectedMenuItem(menuItem || null); // Set selected menu item
    };

    const handleQuantityChange = (type) => {
        if (type === 'increment') {
            setQuantity((prevQuantity) => prevQuantity + 1);
        } else if (type === 'decrement' && quantity > 1) {
            setQuantity((prevQuantity) => prevQuantity - 1);
        }
    };

    const handleSubmitTransaction = async () => {
        if (!customerName || !tableNumber) {
            setErrorMessage('Nama pelanggan dan nomor meja harus diisi!');
            return;
        }

        if (selectedMenu.length === 0) {
            setErrorMessage('Silakan tambahkan setidaknya satu menu sebelum submit!');
            return;
        }

        const newTransaction = {
            tgl_transaksi: new Date().toISOString(),
            id_user:localStorage.getItem("id_user"),
            id_meja: tableNumber,
            nama_pelanggan: customerName,
            status: 'belum_bayar',
            detail_transaksi: selectedMenu.map(item => ({
                id_menu: item.id_menu,
                quantity: item.quantity,
            })),
        };

        const handleMenuChange = (e) => {
          const selectedId = b
        }
        try {
            const response = await fetch('http://localhost:22077/transaksi', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(newTransaction),
            });

            if (response.ok) {
                alert('Transaksi berhasil ditambahkan!');

                // Update table status to "terisi"
                await updateTableStatus(tableNumber, 'terisi');

                setTransactionReceipt(newTransaction);
                setSelectedMenu([]);
                setCustomerName('');
                setTableNumber('');
            } else {
                const errorText = await response.text(); // Ambil teks dari respon
                console.error('Failed to add transaction:', errorText); // Tampilkan pesan kesalahan di console
                setErrorMessage('Gagal menambahkan transaksi. Coba lagi!');
            }
        } catch (error) {
            console.error('Error submitting transaction:', error);
        }
    };

    const updateTableStatus = async (tableNumber, status) => {
        try {
            const response = await fetch(`http://localhost:22077/meja/${tableNumber}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ status }),
            });

            if (!response.ok) {
                throw new Error('Failed to update table status');
            }
        } catch (error) {
            console.error('Error updating table status:', error);
        }
    };

    const handlePayment = async () => {
        const updatedReceipt = {
            ...transactionReceipt,
            status: "lunas"
        };

        try {
            const response = await fetch(`http://localhost:22077/transaksi/${transactionReceipt.id_meja}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(updatedReceipt),
            });

            if (response.ok) {
                alert('Pembayaran berhasil!');
                setIsPaid(true);
                setTransactionReceipt(updatedReceipt);
            }
        } catch (error) {
            console.error('Error processing payment:', error);
        }
    };

    const handleDone = () => {
        setSelectedMenu([]);
        setTransactionReceipt(null);
        setIsPaid(false);
        window.location.reload();
    };

    const calculateReceiptTotalHarga = () => {
        if (!transactionReceipt) return 0;

        return transactionReceipt.detail_transaksi.reduce((total, item) => {
            const menu = menuItems.find(menuItem => menuItem.id_menu === item.id_menu);
            return total + (menu ? menu.harga * item.quantity : 0);
        }, 0);
    };

    const formatDate = (date) => {
        return new Date(date).toLocaleString();
    };

    return (
      <div className="flex bg-pastelPink min-h-screen">
        <Sidebar user={user} />
        <div className="flex-1 p-5">
          
            <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-2xl mx-auto">
              <h2 className="text-2xl font-bold mb-6">Transaksi Pemesanan</h2>

              <div className="mb-6 grid grid-cols-1 gap-4">
                <input
                  type="text"
                  value={customerName}
                  onChange={(e) => setCustomerName(e.target.value)}
                  placeholder="Nama Pelanggan"
                  className="border border-gray-300 p-3 rounded-lg w-full"
                  required
                />
                <select
                  value={tableNumber}
                  onChange={(e) => setTableNumber(e.target.value)}
                  className="border border-gray-300 p-3 rounded-lg w-full"
                  required
                >
                  <option value="">Pilih Nomor Meja</option>
                  {Array.isArray(tables) && tables.map((table) => (
                    <option key={table.id_meja} value={table.nomor_meja}>
                      {table.nomor_meja}
                    </option>
                  ))}
                </select>
              </div>

              <form onSubmit={handleOrderSubmit} className="mb-6 grid grid-cols-1 gap-4">
                <div className="flex space-x-4">
                  <select
                    value={selectedMenu ? selectedMenu.id_menu : ''}
                    onChange={handleMenuChange}
                    className="border border-gray-300 p-3 rounded-lg flex-1"
                    required
                  >
                    <option value="">Pilih Menu...</option>
                    {menuItems.map((item) => (
                      <option key={item.id_menu} value={item.nama_menu}>
                        {item.nama_menu}
                      </option>
                    ))}
                  </select>
                  <input
                    type="number"
                    value={quantityInput}
                    onChange={(e) => setQuantityInput(e.target.value)}
                    placeholder="Jumlah"
                    className="border border-gray-300 p-3 rounded-lg w-24"
                    required
                    min="1"
                  />
                </div>
                <button
                  type="submit"
                  className="w-full bg-pastelRose text-white py-2 rounded-lg hover:bg-pastelLightPink transition duration-300"
                >
                  Tambah Pesanan
                </button>
              </form>

              <h3 className="text-xl font-semibold mb-4">Daftar Pesanan:</h3>
              <ul className="list-disc pl-5 mb-4">
                {orders.map((order, index) => (
                  <li key={index}>
                    {order.menu} (x{order.quantity}) - Rp{(order.harga * order.quantity).toLocaleString()}
                  </li>
                ))}
              </ul>

              <h3 className="text-xl font-semibold my-4">Total: Rp{total.toLocaleString() || 0}</h3>

              <button
                onClick={handlePrintReceipt}
                className="w-full bg-green-400 text-white py-2 rounded-lg hover:bg-green-500 transition duration-300"
              >
                Cetak Nota
              </button>

              <button
                onClick={handlePayment}
                className="w-full bg-blue-400 text-white py-2 rounded-lg hover:bg-blue-500 transition duration-300 mt-2"
              >
                Bayar
              </button>
            </div>
          )}

  {showReceipt && (
    <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-2xl mx-auto mt-10">
      <div className="flex justify-between items-center mb-4">
        <div className="flex items-center space-x-4">
          <img src={CafeLogo} alt="Cafe Logo" className="w-16 h-16" />
          <div>
            <h2 className="text-2xl font-bold">Belle Fiori</h2>
            <p className="text-gray-500">112 Elliot, Riverside, Jakarta</p>
            <p className="text-gray-500">Telp: 0819-9779-9924</p>
          </div>
        </div>
        <p className="text-gray-500">{new Date().toLocaleDateString()}</p>
      </div>

      <div className="border-t border-b border-gray-300 py-4">
        <div className="flex justify-between mb-2">
          <p>Customer Name:</p>
          <p>{customerName}</p>
        </div>
        <div className="flex justify-between mb-2">
          <p>Table Number:</p>
          <p>{tableNumber}</p>
        </div>
        <div className="flex justify-between mb-2">
          <p>Status:</p>
          <p className={`${status === 'Lunas' ? 'text-green-500' : 'text-red-500'}`}>{status}</p>
        </div>
      </div>

      <h3 className="text-xl font-semibold my-4">Daftar Pesanan:</h3>
      <div className="mb-4">
        <div className="flex font-bold border-b pb-2">
          <span className="flex-1">Menu</span>
          <span className="w-20 text-center">Quantity</span>
          <span className="w-32 text-right">Price (Rp)</span>
        </div>
        {orders.map((order, index) => (
          <div key={index} className="flex py-2">
            <span className="flex-1">{order.menu}</span>
            <span className="w-20 flex justify-center">{order.quantity}</span> {/* Center quantity */}
            <span className="w-32 text-right">{order.harga.toLocaleString()}</span>
          </div>
        ))}
      </div>

      <h3 className="text-xl font-semibold my-4">Total: Rp{total.toLocaleString() || 0}</h3>

      <button
        onClick={() => {
          setShowReceipt(false);
          window.location.reload(); // Auto refresh on this button click
        }}
        className="w-full bg-pastelRose text-white py-2 rounded-lg hover:bg-pastelLightPink transition duration-300 mt-4"
      >
        Selesai
      </button>
    </div>
  )}

        </div>
      </div>
    );
  };

  export default Cashier;
