import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManageTables = () => {
    const [tables, setTables] = useState([]);
    const [editTable, setEditTable] = useState(null); // State for the table being edited
    const [showEditModal, setShowEditModal] = useState(false); // State for modal visibility
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    useEffect(() => {
        fetchTables();
    }, []);

    const fetchTables = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/tables', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setTables(data.data || []);
            } else {
                console.error('Failed to fetch tables');
            }
        } catch (error) {
            console.error('Error fetching tables:', error);
        }
    };

    const handleEditTable = (table) => {
        setEditTable(table);
        setShowEditModal(true); // Show the edit modal
    };

    const handleUpdateTable = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');

        try {
            const response = await fetch(`http://localhost:8000/meja/${editTable.id_table}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({ status: editTable.status }), // Updating status only
            });

            const data = await response.json();

            if (response.ok) {
                fetchTables(); // Refresh the list
                setShowEditModal(false);
                setErrorMessage('');
                setSuccessMessage('Table status successfully updated!');
            } else {
                setErrorMessage(data.message || 'Failed to update table status');
            }
        } catch (error) {
            console.error('Error updating table status:', error);
            setErrorMessage('Something went wrong. Please try again.');
        }
    };

    const handleDeleteTable = async (id_table) => {
        const token = localStorage.getItem('token');
        
        try {
            const response = await fetch(`http://localhost:8000/tables/${id_table}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.ok) {
                fetchTables(); // Refresh the list after deleting a table
                setSuccessMessage('Table successfully deleted!');
            } else {
                console.error('Failed to delete table');
            }
        } catch (error) {
            console.error('Error deleting table:', error);
        }
    };

    return (
        <>
            <Sidebar />
            <div className="container mx-auto p-6">
                <h1 className="text-5xl font-bold text-center text-gray-800 mb-10">Manage Tables</h1>

                {/* Tables Display */}
                <div className="overflow-x-auto shadow-lg rounded-lg">
                    <table className="min-w-full bg-white border border-black rounded-lg">
                        <thead>
                            <tr className="bg-pastelRose text-white border-black">
                                <th className="py-4 px-6 border-b border-black">Table ID</th>
                                <th className="py-4 px-6 border-b border-black">Status</th>
                                <th className="py-4 px-6 border-b border-black">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {tables.map((table, index) => (
                                <tr
                                    key={table.id_table}
                                    className={`${
                                        index % 2 === 0 ? 'bg-white' : 'bg-gray-100'
                                    } hover:bg-pastelLightPink transition duration-200`}
                                >
                                    <td className="py-4 px-6 border-b border-black">Table {table.id_table}</td>
                                    <td className="py-4 px-6 border-b border-black">{table.status ? 'Occupied' : 'Empty'}</td>
                                    <td className="py-4 px-6 border-b border-black">
                                        <button
                                            className="bg-blue-500 text-white px-4 py-2 rounded-lg mr-2 hover:bg-blue-700"
                                            onClick={() => handleEditTable(table)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-700"
                                            onClick={() => handleDeleteTable(table.id_table)}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                {/* Edit Table Modal */}
                {showEditModal && editTable && (
                    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
                        <div className="bg-white p-6 rounded-lg shadow-lg w-1/2">
                            <h2 className="text-2xl font-semibold mb-4 text-pastelRose">Edit Table {editTable.id_table}</h2>
                            <form onSubmit={handleUpdateTable} className="space-y-4">
                                <div>
                                    <label className="block text-gray-700">Status:</label>
                                    <select
                                        value={editTable.status}
                                        onChange={(e) => setEditTable({ ...editTable, status: e.target.value })}
                                        className="w-full p-2 border border-gray-300 rounded-lg"
                                    >
                                        <option value={true}>Occupied</option>
                                        <option value={false}>Empty</option>
                                    </select>
                                </div>
                                <div className="flex justify-end">
                                    <button
                                        type="button"
                                        className="bg-gray-500 text-white px-4 py-2 rounded-lg mr-2 hover:bg-gray-700"
                                        onClick={() => setShowEditModal(false)}
                                    >
                                        Cancel
                                    </button>
                                    <button
                                        type="submit"
                                        className="bg-pastelRose text-white px-4 py-2 rounded-lg hover:bg-pastelDarkRed"
                                    >
                                        Update Status
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                )}
            </div>
        </>
    );
};

export default ManageTables;
