import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManageTables = () => {
    const [tables, setTables] = useState([]);

    useEffect(() => {
        fetchTables();
    }, []);

    const fetchTables = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/tables', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setTables(data.data || []);
            } else {
                console.error('Failed to fetch tables');
            }
        } catch (error) {
            console.error('Error fetching tables:', error);
        }
    };

    const handleDeleteTable = async (tableId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8000/tables/${tableId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.ok) {
                // Refresh the table list after deletion
                fetchTables();
            } else {
                console.error('Failed to delete table');
            }
        } catch (error) {
            console.error('Error deleting table:', error);
        }
    };

    return (
        <div className="flex">
            {/* Sidebar */}
            <Sidebar />

            {/* Main Content */}
            <div className="flex-grow p-8 bg-white shadow-lg rounded-lg mx-6 mt-4 mb-4">
                <h1 className="text-4xl font-semibold text-pastelDarkRed mb-10 text-center">Manage Tables</h1>
                <div className="overflow-x-auto shadow-lg rounded-lg mb-6">
                    <table className="min-w-full bg-white rounded-lg shadow-lg">
                        <thead>
                            <tr className="bg-pastelRose text-white">
                                <th className="py-4 px-6 border-b">Table ID</th>
                                <th className="py-4 px-6 border-b">Table Number</th>
                                <th className="py-4 px-6 border-b">Capacity</th>
                                <th className="py-4 px-6 border-b">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {tables.map((table, index) => (
                                <tr
                                    key={table.id}
                                    className={`${index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'} hover:bg-pastelRose transition duration-200`}
                                >
                                    <td className="py-4 px-6 border-b">{table.id}</td>
                                    <td className="py-4 px-6 border-b">{table.number}</td>
                                    <td className="py-4 px-6 border-b">{table.capacity}</td>
                                    <td className="py-4 px-6 border-b">
                                        <button
                                            className="bg-red-500 text-white rounded-lg px-4 py-2"
                                            onClick={() => handleDeleteTable(table.id)}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default ManageTables;
