import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManageUsers = () => {
    const [users, setUsers] = useState([]);
    const [newUser, setNewUser] = useState({
        nama_user: '',
        username: '',
        password: '',
        role: 'kasir',
    });
    const [showAddUserForm, setShowAddUserForm] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/user', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setUsers(data.data || []);
            } else {
                console.error('Failed to fetch users');
            }
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const handleAddUser = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');

        // Cek apakah username sudah ada
        const existingUser = users.find(user => user.username === newUser.username);
        if (existingUser) {
            setErrorMessage('Username already exists. Please choose a different one.');
            return;
        }

        try {
            const response = await fetch('http://localhost:8000/user', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify(newUser),
            });

            if (response.ok) {
                fetchUsers(); // Refresh list setelah menambahkan user baru
                setNewUser({ nama_user: '', username: '', password: '', role: 'kasir' });
                setShowAddUserForm(false); // Tutup form setelah berhasil menambahkan user
                setErrorMessage(''); // Reset error message
            } else {
                console.error('Failed to add user');
            }
        } catch (error) {
            console.error('Error adding user:', error);
        }
    };

    const handleEditUser = (user) => {
        setNewUser(user);
        setShowAddUserForm(true);
    };

    const handleDeleteUser = (userId) => {
        console.log(`Deleting user ${userId}`);
        // TODO: Implement API call to delete user
    };

    return (
        <>
            <Sidebar />
            <div className="container mx-auto p-6">
                <h1 className="text-5xl font-bold text-center text-gray-800 mb-10">Manage Users</h1>

                {/* Button to show Add User Form */}
                <button
                    className="bg-pastelRose text-white px-4 py-2 rounded-lg mb-6 hover:bg-pastelDarkRed"
                    onClick={() => setShowAddUserForm(!showAddUserForm)}
                >
                    {showAddUserForm ? 'Cancel' : 'Add User'}
                </button>

                {/* Add/Edit User Form */}
                {showAddUserForm && (
                    <div className="bg-white p-6 rounded-lg shadow-lg mb-10 border border-gray-300">
                        <h2 className="text-2xl font-semibold mb-4 text-pastelRose">
                            {newUser.id_user ? 'Edit User' : 'Add New User'}
                        </h2>

                        {errorMessage && (
                            <p className="text-red-500 mb-4">{errorMessage}</p>
                        )}

                        <form onSubmit={handleAddUser} className="space-y-4">
                            <div>
                                <label className="block text-gray-700">Nama User:</label>
                                <input
                                    type="text"
                                    value={newUser.nama_user}
                                    onChange={(e) => setNewUser({ ...newUser, nama_user: e.target.value })}
                                    className="w-full p-2 border border-gray-300 rounded-lg"
                                    required
                                />
                            </div>
                            <div>
                                <label className="block text-gray-700">Username:</label>
                                <input
                                    type="text"
                                    value={newUser.username}
                                    onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
                                    className="w-full p-2 border border-gray-300 rounded-lg"
                                    required
                                />
                            </div>
                            <div>
                                <label className="block text-gray-700">Password:</label>
                                <input
                                    type="password"
                                    value={newUser.password}
                                    onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
                                    className="w-full p-2 border border-gray-300 rounded-lg"
                                    required
                                />
                            </div>
                            <div>
                                <label className="block text-gray-700">Role:</label>
                                <select
                                    value={newUser.role}
                                    onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
                                    className="w-full p-2 border border-gray-300 rounded-lg"
                                >
                                    <option value="kasir">Kasir</option>
                                    <option value="manager">Manager</option>
                                    <option value="admin">Admin</option>
                                </select>
                            </div>
                            <button
                                type="submit"
                                className="bg-pastelRose text-white p-2 rounded-lg w-full hover:bg-pastelDarkRed"
                            >
                                {newUser.id_user ? 'Update User' : 'Add User'}
                            </button>
                        </form>
                    </div>
                )}

                {/* Users Table */}
                <div className="overflow-x-auto shadow-lg rounded-lg">
                    <table className="min-w-full bg-white border border-black rounded-lg">
                        <thead>
                            <tr className="bg-pastelRose text-white border-black">
                                <th className="py-4 px-6 border-b border-black">User ID</th>
                                <th className="py-4 px-6 border-b border-black">Nama User</th>
                                <th className="py-4 px-6 border-b border-black">Username</th>
                                <th className="py-4 px-6 border-b border-black">Password</th>
                                <th className="py-4 px-6 border-b border-black">Role</th>
                                <th className="py-4 px-6 border-b border-black">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map((user, index) => (
                                <tr
                                    key={user.id_user}
                                    className={`${
                                        index % 2 === 0 ? 'bg-white' : 'bg-gray-100'
                                    } hover:bg-pastelLightPink transition duration-200`}
                                >
                                    <td className="py-4 px-6 border-b border-black text-center">{user.id_user}</td>
                                    <td className="py-4 px-6 border-b border-black text-center">{user.nama_user}</td>
                                    <td className="py-4 px-6 border-b border-black text-center">{user.username}</td>
                                    <td className="py-4 px-6 border-b border-black text-center">{user.password}</td>
                                    <td className="py-4 px-6 border-b border-black text-center">{user.role}</td>
                                    <td className="py-4 px-6 border-b border-black text-center">
                                        <button
                                            className="bg-pastelRose text-white px-4 py-2 rounded-lg mr-2 hover:bg-pastelDarkRed"
                                            onClick={() => handleEditUser(user)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-700"
                                            onClick={() => handleDeleteUser(user.id_user)}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
};

export default ManageUsers;
