import React, { useState } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from './Sidebar';

const Cashier = ({ user }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); 
  const [customerName, setCustomerName] = useState('');
  const [tableNumber, setTableNumber] = useState('');
  const [orders, setOrders] = useState([]);
  const [menuInput, setMenuInput] = useState('');
  const [quantityInput, setQuantityInput] = useState('');
  const [showReceipt, setShowReceipt] = useState(false);
  const [status, setStatus] = useState('Belum Bayar');
  
  const total = orders.reduce((sum, order) => sum + order.quantity * 10000, 0); // Contoh total harga
  
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleOrderSubmit = (e) => {
    e.preventDefault();
    const order = {
      menu: menuInput,
      quantity: parseInt(quantityInput),
    };
    setOrders([...orders, order]);
    setMenuInput('');
    setQuantityInput('');
  };

  const handlePrintReceipt = () => {
    setShowReceipt(true);
  };

  const handlePayment = () => {
    setStatus('Lunas');
  };

  return (
    <div className="flex bg-pink-100 min-h-screen"> 
      <Sidebar user={user} />
      <div className="flex-1 p-5">
        {!isLoggedIn ? (
          <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-md mx-auto bg-pink-50">
            <h2 className="text-2xl mb-4">Login Kasir</h2>
            <button
              onClick={handleLogin}
              className="w-full bg-pink-400 text-white py-2 rounded-lg hover:bg-pink-500 transition duration-300"
            >
              Login
            </button>
          </div>
        ) : (
          <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-2xl mx-auto bg-pink-50">
            <h2 className="text-2xl font-bold mb-6">Transaksi Pemesanan</h2>

            <div className="mb-6 grid grid-cols-1 gap-4">
              <input
                type="text"
                value={customerName}
                onChange={(e) => setCustomerName(e.target.value)}
                placeholder="Nama Pelanggan"
                className="border border-gray-300 p-3 rounded-lg w-full"
                required
              />
              <input
                type="text"
                value={tableNumber}
                onChange={(e) => setTableNumber(e.target.value)}
                placeholder="Nomor Meja"
                className="border border-gray-300 p-3 rounded-lg w-full"
                required
              />
            </div>

            <form onSubmit={handleOrderSubmit} className="mb-6 grid grid-cols-1 gap-4">
              <div className="flex space-x-4">
                <input
                  type="text"
                  value={menuInput}
                  onChange={(e) => setMenuInput(e.target.value)}
                  placeholder="Masukkan menu..."
                  className="border border-gray-300 p-3 rounded-lg flex-1"
                  required
                />
                <input
                  type="number"
                  value={quantityInput}
                  onChange={(e) => setQuantityInput(e.target.value)}
                  placeholder="Jumlah"
                  className="border border-gray-300 p-3 rounded-lg w-24"
                  required
                  min="1"
                />
              </div>
              <button
                type="submit"
                className="w-full bg-pink-400 text-white py-2 rounded-lg hover:bg-pink-500 transition duration-300"
              >
                Tambah Pesanan
              </button>
            </form>

            <h3 className="text-xl font-semibold mb-4">Daftar Pesanan:</h3>
            <ul className="list-disc pl-5 mb-4">
              {orders.map((order, index) => (
                <li key={index}>
                  {order.menu} (x{order.quantity})
                </li>
              ))}
            </ul>

            <button
              onClick={handlePrintReceipt}
              className="w-full bg-green-400 text-white py-2 rounded-lg hover:bg-green-500 transition duration-300"
            >
              Cetak Nota
            </button>

            <button
              onClick={handlePayment}
              className="w-full bg-blue-400 text-white py-2 rounded-lg hover:bg-blue-500 transition duration-300 mt-2"
            >
              Bayar
            </button>
          </div>
        )}

        {showReceipt && (
          <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-2xl mx-auto mt-10 bg-pink-50">
            <div className="flex justify-between items-center mb-4">
              {/* Bagian header nota dengan logo dan nama cafe */}
              <div className="flex items-center space-x-4">
                <img src="/logo.png" alt="Cafe Logo" className="w-16 h-16" />
                <div>
                  <h2 className="text-2xl font-bold">Cofesho Cafe</h2>
                  <p className="text-gray-500">Jl. Eliot No. 10, Malang</p>
                  <p className="text-gray-500">Telp: 0819-9779-9924</p>
                </div>
              </div>
              <p className="text-gray-500">{new Date().toLocaleDateString()}</p>
            </div>

            {/* Detail nota dengan grid layout */}
            <div className="border-t border-b border-gray-300 py-4">
              <div className="flex justify-between mb-2">
                <p>Nama Pelanggan:</p>
                <p>{customerName}</p>
              </div>
              <div className="flex justify-between mb-2">
                <p>Nomor Meja:</p>
                <p>{tableNumber}</p>
              </div>
              <div className="flex justify-between mb-2">
                <p>Status:</p>
                <p className={`${status === 'Lunas' ? 'text-green-500' : 'text-red-500'}`}>{status}</p>
              </div>
            </div>

            <h3 className="text-xl font-semibold my-4">Daftar Pesanan:</h3>
            <ul className="list-none mb-4">
              {orders.map((order, index) => (
                <li key={index} className="flex justify-between mb-2">
                  <span>{order.menu} (x{order.quantity})</span>
                  <span>Rp {order.quantity * 10000}</span> {/* Harga contoh Rp10.000 per item */}
                </li>
              ))}
            </ul>

            {/* Total dan informasi tambahan */}
            <div className="border-t border-gray-300 pt-4">
              <div className="flex justify-between mb-2">
                <p>Total:</p>
                <p className="font-bold">Rp {total}</p>
              </div>
              <p className="text-center text-gray-500 mt-4">Terima kasih telah berkunjung ke Cofesho Cafe!</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cashier;
