import React, { useState } from 'react';
import 'tailwindcss/tailwind.css';
import login_bg from '../assets/login_bg.png';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [name, setName] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [role, setRole] = useState('kasir'); // Default role can be set to 'kasir'
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const register = async (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      setError("Passwords don't match");
      return;
    }

    try {
      const response = await fetch('http://localhost:8000/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, username, password, role }), // Include name and role in the request
      });

      const data = await response.json();

      if (data.message === 'Registration Success') {
        navigate('/'); // Redirect to login page after successful registration
      } else {
        setError(data.message || 'Registration failed');
      }
    } catch (err) {
      setError('Error registering');
    }
  };

   return (
    <div 
      className="min-h-screen flex items-center justify-center bg-cover bg-center" 
      style={{ backgroundImage: `url(${login_bg})` }}
    >
      <div className="bg-white shadow-md rounded-lg flex flex-col w-full max-w-md p-8">
        <h2 className="text-3xl font-bold text-center text-pastelRose mb-4">Hello Fellas!</h2>
        <h3 className="text-2xl font-semibold text-center text-gray-700 mb-2">Register</h3>
        <p className="text-center text-gray-500 mb-6">Create your account below.</p>

        {/* Input Fields */}
        <form onSubmit={register} className="space-y-4">
          <input
            type="text"
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pastelPink"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Name"
            required
          />
          <input
            type="text"
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pastelPink"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
            required
          />
          <input
            type="password"
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pastelPink"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
          />
          <input
            type="password"
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pastelPink"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="Confirm Password"
            required
          />
          <select
            value={role}
            onChange={(e) => setRole(e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pastelPink"
          >
            <option value="kasir">Kasir</option>
            <option value="manajer">Manajer</option>
            <option value="admin">Admin</option>
          </select>

          {/* Error Message */}
          {error && <p className="text-red-500 text-center mt-3">{error}</p>}

          {/* Register Button */}
          <div className="mt-6">
            <button
              type="submit"
              className="w-full bg-pastelRose text-white py-3 rounded-lg hover:bg-pastelLightPink transition"
            >
              Register
            </button>
          </div>
        </form>

        {/* Login Link */}
        <p className="text-center mt-6 text-pastelPink">
          Already have an account? <a href="/" className="text-pastelPink hover:underline">Log in</a>
        </p>
      </div>
    </div>
  );
};

export default Register;
