import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManagerPage = ({ user }) => {
    const [transactions, setTransactions] = useState([]);
    const [filteredTransactions, setFilteredTransactions] = useState([]);
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchAllTransactions();
    }, []);

    const fetchAllTransactions = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/transaksi', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.ok) {
                const data = await response.json();
                setTransactions(data.data || []);
                setFilteredTransactions(data.data || []);
            } else {
                console.error('Failed to fetch transactions');
            }
        } catch (error) {
            console.error('Error fetching transactions:', error);
        }
    };

    const handleDateFilter = () => {
        if (!startDate || !endDate) {
            setErrorMessage('Both start and end date must be selected!');
            return;
        }

        const filtered = transactions.filter(transaction => {
            const transactionDate = new Date(transaction.tgl_transaksi);
            return transactionDate >= new Date(startDate) && transactionDate <= new Date(endDate);
        });

        setFilteredTransactions(filtered);
        setErrorMessage('');
    };

    const formatDate = (date) => {
        return new Date(date).toLocaleDateString();
    };

    const calculateTotalTransaction = (transaction) => {
        return transaction.detail_transaksi.reduce((total, item) => {
            return total + (item.harga * item.quantity);
        }, 0);
    };

    return (
        <>
        <Sidebar/>
        <div className="flex min-h-screen bg-pastelPink">

            <div className="flex-grow p-8 bg-white shadow-lg rounded-lg mx-6 mt-4 mb-4">
                <h1 className="text-4xl font-semibold text-pastelDarkRed mb-10 text-center">
                    Manager Reports
                </h1>

                {/* Date Filtering */}
                <div className="flex items-center justify-center mb-8 space-x-4">
                    <div className="flex flex-col">
                        <label htmlFor="startDate" className="text-pastelDarkRed font-medium mb-1">Start Date</label>
                        <input
                            type="date"
                            id="startDate"
                            className="border border-pastelRose rounded-lg p-2 bg-pastelLightPink text-pastelDarkRed focus:ring-pastelRose focus:border-pastelRose transition duration-200"
                            value={startDate}
                            onChange={(e) => setStartDate(e.target.value)}
                        />
                    </div>

                    <div className="flex flex-col">
                        <label htmlFor="endDate" className="text-pastelDarkRed font-medium mb-1">End Date</label>
                        <input
                            type="date"
                            id="endDate"
                            className="border border-pastelRose rounded-lg p-2 bg-pastelLightPink text-pastelDarkRed focus:ring-pastelRose focus:border-pastelRose transition duration-200"
                            value={endDate}
                            onChange={(e) => setEndDate(e.target.value)}
                        />
                    </div>

                    <button
                        className="ml-4 bg-pastelRose text-white px-6 py-2 rounded-lg shadow-lg hover:bg-pastelRed transition duration-300 transform hover:scale-105"
                        onClick={handleDateFilter}
                    >
                        Filter
                    </button>
                </div>

                {errorMessage && <p className="text-pastelRed text-center mb-4">{errorMessage}</p>}

                {/* Transaction List */}
                {filteredTransactions.length > 0 ? (
                    <div className="overflow-x-auto shadow-lg rounded-lg">
                        <table className="min-w-full bg-white rounded-lg shadow-lg">
                            <thead>
                                <tr className="bg-pastelRose text-white">
                                    <th className="py-4 px-6 border-b">Tanggal Transaksi</th>
                                    <th className="py-4 px-6 border-b">Nama Pelanggan</th>
                                    <th className="py-4 px-6 border-b">Meja</th>
                                    <th className="py-4 px-6 border-b">Karyawan</th>
                                    <th className="py-4 px-6 border-b">Total Transaksi</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filteredTransactions.map((transaction, index) => (
                                    <tr
                                        key={index}
                                        className={`${
                                            index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'
                                        } hover:bg-pastelRose transition duration-200`}
                                    >
                                        <td className="py-4 px-6 border-b">{formatDate(transaction.tgl_transaksi)}</td>
                                        <td className="py-4 px-6 border-b">{transaction.nama_pelanggan}</td>
                                        <td className="py-4 px-6 border-b">{transaction.id_meja}</td>
                                        <td className="py-4 px-6 border-b">{transaction.id_user}</td>
                                        <td className="py-4 px-6 border-b">
                                            Rp {calculateTotalTransaction(transaction).toLocaleString()}
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                ) : (
                    <p className="text-pastelDarkRed mt-6 text-center">Tidak ada transaksi ditemukan</p>
                )}
            </div>
        </div>
        </>
    );
};

export default ManagerPage;
