import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar';
import 'tailwindcss/tailwind.css';

const ViewTransaction = () => {
  const [transactions, setTransactions] = useState([]);
  const [menus, setMenus] = useState([]);
  const [transactionDetails, setTransactionDetails] = useState({});
  const [loadingDetails, setLoadingDetails] = useState({});
  const [searchText, setSearchText] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [filterMonth, setFilterMonth] = useState('');

  // Fetch transactions
  const fetchTransactions = async () => {
    try {
      const response = await fetch('http://localhost:8000/transaksi');
      const data = await response.json();
      console.log('Fetched Transactions:', data); // Debugging: Check the structure of the response
      setTransactions(data.data || []); // Adjust this based on your API response structure
    } catch (error) {
      console.error('Error fetching transactions:', error);
    }
  };

  // Fetch menus
  const fetchMenus = async () => {
    try {
      const response = await fetch('http://localhost:8000/menu');
      const data = await response.json();
      console.log('Fetched Menus:', data); // Debugging: Check the structure of the response
      setMenus(data.data || []); // Adjust this based on your API response structure
    } catch (error) {
      console.error('Error fetching menus:', error);
    }
  };

  // Fetch transaction details by ID
  const fetchTransactionDetails = async (id) => {
    setLoadingDetails((prev) => ({ ...prev, [id]: true })); // Set loading state for specific transaction
    try {
      const response = await fetch(`http://localhost:8000/transaksi/${id}`);
      const data = await response.json();
      console.log(`Fetched Transaction Details for ID ${id}:`, data); // Debugging: Check the response
      setTransactionDetails((prev) => ({ ...prev, [id]: data.data })); // Store the details for the specific transaction
    } catch (error) {
      console.error('Error fetching transaction details:', error);
    } finally {
      setLoadingDetails((prev) => ({ ...prev, [id]: false })); // Reset loading state
    }
  };

  // Filter by date
  const handleDateFilter = async () => {
    if (filterDate) {
      try {
        const response = await fetch(`http://localhost:8000/transaksi/${filterDate}`);
        const data = await response.json();
        console.log(`Filtered Transactions by Date ${filterDate}:`, data); // Debugging: Check the response
        setTransactions(data.data || []);
      } catch (error) {
        console.error('Error filtering transactions by date:', error);
      }
    }
  };

  // Filter by month
  const handleMonthFilter = async () => {
    if (filterMonth) {
      try {
        const response = await fetch(`http://localhost:8000/transaksi/bulan/${filterMonth}`);
        const data = await response.json();
        console.log(`Filtered Transactions by Month ${filterMonth}:`, data); // Debugging: Check the response
        setTransactions(data.data || []);
      } catch (error) {
        console.error('Error filtering transactions by month:', error);
      }
    }
  };

  // Use effect to load data on component mount
  useEffect(() => {
    fetchTransactions();
    fetchMenus();
  }, []);

  const handleSearchText = (e) => {
    setSearchText(e.target.value);
  };

  const handleSearchDate = (e) => {
    setFilterDate(e.target.value);
  };

  // Filter transactions by searchText and optionally by searchDate
  const filteredTransactions = transactions.filter((transaction) => {
    const matchesText = transaction.name?.toLowerCase().includes(searchText.toLowerCase()) || false; // Safeguard for undefined name
    const matchesDate = filterDate ? transaction.date.includes(filterDate) : true;
    return matchesText && matchesDate;
  });

  return (
    <div className="flex min-h-screen bg-pastelPink">
      <Sidebar />
      <div className="flex-grow p-8">
        <h1 className="text-4xl font-bold mb-6 text-pastelDarkRed">Transaction List</h1>

        {/* Search Bar */}
        <div className="flex mb-6 space-x-4">
          <input
            type="text"
            placeholder="Search by text"
            value={searchText}
            onChange={handleSearchText}
            className="px-4 py-2 border border-pastelRose rounded-lg focus:outline-none focus:ring-2 focus:ring-pastelRed"
          />
          <input
            type="date"
            placeholder="Search by date"
            value={filterDate}
            onChange={handleSearchDate}
            className="px-4 py-2 border border-pastelRose rounded-lg focus:outline-none focus:ring-2 focus:ring-pastelRed"
          />
          <input
            type="month"
            placeholder="Search by month"
            value={filterMonth}
            onChange={(e) => setFilterMonth(e.target.value)}
            className="px-4 py-2 border border-pastelRose rounded-lg focus:outline-none focus:ring-2 focus:ring-pastelRed"
          />
          <button
            onClick={handleDateFilter}
            className="px-4 py-2 bg-pastelRed text-white rounded-lg"
          >
            Filter by Date
          </button>
          <button
            onClick={handleMonthFilter}
            className="px-4 py-2 bg-pastelRed text-white rounded-lg"
          >
            Filter by Month
          </button>
        </div>

        {/* Transactions Table */}
        <table className="min-w-full bg-cream rounded-lg shadow-md">
          <thead>
            <tr className="text-left bg-pastelLightPink">
              <th className="py-2 px-4 text-pastelDarkRed">ID</th>
              <th className="py-2 px-4 text-pastelDarkRed">Name</th>
              <th className="py-2 px-4 text-pastelDarkRed">Date</th>
              <th className="py-2 px-4 text-pastelDarkRed">Amount</th>
              <th className="py-2 px-4 text-pastelDarkRed">Details</th>
            </tr>
          </thead>
          <tbody>
            {filteredTransactions.map((transaction) => (
              <tr key={transaction.id} className="border-t border-pastelRose">
                <td className="py-2 px-4">{transaction.id_transaksi}</td>
                <td className="py-2 px-4">{transaction.nama_pelanggan}</td>
                <td className="py-2 px-4">{transaction.date}</td>
                <td className="py-2 px-4">{transaction.amount}</td>
                <td className="py-2 px-4">
                  <button
                    onClick={() => fetchTransactionDetails(transaction.id)}
                    className="px-4 py-2 bg-pastelRed text-white rounded-lg"
                  >
                    {loadingDetails[transaction.id] ? 'Loading...' : 'View Details'}
                  </button>
                  {transactionDetails[transaction.id] && (
                    <div className="mt-2 text-sm text-gray-600">
                      {JSON.stringify(transactionDetails[transaction.id], null, 2)}
                    </div>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ViewTransaction;
