import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManageFood = ({ user }) => {
    const [menuItems, setMenuItems] = useState([]);
    const [newMenuItem, setNewMenuItem] = useState({ nama_menu: '', harga: '' });
    const [editMenuItem, setEditMenuItem] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchMenuItems();
    }, []);

    const fetchMenuItems = async () => {
        try {
            const response = await fetch('http://localhost:8000/menu');
            const data = await response.json();
            setMenuItems(data.data || []);
        } catch (error) {
            console.error('Error fetching menu items:', error);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewMenuItem((prev) => ({ ...prev, [name]: value }));
    };

    const handleAddMenuItem = async () => {
        if (!newMenuItem.nama_menu || !newMenuItem.harga) {
            setErrorMessage('Nama menu dan harga harus diisi!');
            return;
        }

        try {
            let token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/menu', {
                method: 'POST',
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(newMenuItem),
            });

            if (response.ok) {
                alert('Menu berhasil ditambahkan!');
                setNewMenuItem({ nama_menu: '', harga: '' });
                fetchMenuItems(); // Refresh menu items
            } else {
                const errorText = await response.text();
                console.error('Failed to add menu:', errorText);
                setErrorMessage('Gagal menambahkan menu!');
            }
        } catch (error) {
            console.error('Error adding menu item:', error);
        }
    };

    const handleEditMenuItem = (menuItem) => {
        setEditMenuItem(menuItem);
    };

    const handleUpdateMenuItem = async () => {
        if (!editMenuItem.nama_menu || !editMenuItem.harga) {
            setErrorMessage('Nama menu dan harga harus diisi!');
            return;
        }

        try {
            let token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8000/menu/${editMenuItem.id_menu}`, {
                method: 'PUT',
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(editMenuItem),
            });

            if (response.ok) {
                alert('Menu berhasil diperbarui!');
                setEditMenuItem(null);
                fetchMenuItems(); // Refresh menu items
            } else {
                const errorText = await response.text();
                console.error('Failed to update menu:', errorText);
                setErrorMessage('Gagal memperbarui menu!');
            }
        } catch (error) {
            console.error('Error updating menu item:', error);
        }
    };

    const handleDeleteMenuItem = async (menuId) => {
        try {
            let token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8000/menu/${menuId}`, {
                method: 'DELETE',
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
            });

            if (response.ok) {
                alert('Menu berhasil dihapus!');
                fetchMenuItems(); // Refresh menu items
            } else {
                const errorText = await response.text();
                console.error('Failed to delete menu:', errorText);
                setErrorMessage('Gagal menghapus menu!');
            }
        } catch (error) {
            console.error('Error deleting menu item:', error);
        }
    };

    return (
        <div className="flex min-h-screen bg-pastelLightPink">
            <Sidebar user={user} /> {/* Render Sidebar */}

            <div className="flex-grow p-4">
                <h1 className="text-3xl font-bold text-white mb-6 text-center">Kelola Menu</h1>

                {/* Form untuk menambah menu */}
                <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
                    <h2 className="text-2xl font-bold mb-4">Tambah Menu Baru</h2>
                    <input
                        type="text"
                        name="nama_menu"
                        value={newMenuItem.nama_menu}
                        onChange={handleInputChange}
                        placeholder="Nama Menu"
                        className="block w-full p-2 border border-gray-300 rounded-lg mb-4"
                    />
                    <input
                        type="number"
                        name="harga"
                        value={newMenuItem.harga}
                        onChange={handleInputChange}
                        placeholder="Harga Menu"
                        className="block w-full p-2 border border-gray-300 rounded-lg mb-4"
                    />
                    <button
                        className="bg-pastelRose text-white px-4 py-2 rounded-lg hover:bg-[#697565]"
                        onClick={handleAddMenuItem}
                    >
                        Tambah Menu
                    </button>
                    {errorMessage && <p className="text-red-500 mt-4">{errorMessage}</p>}
                </div>

                {/* Tabel daftar menu */}
                <div className="bg-white rounded-lg shadow-lg p-6">
                    <h2 className="text-2xl font-bold mb-4">Daftar Menu</h2>
                    <table className="w-full border-collapse border border-gray-300">
                        <thead>
                            <tr>
                                <th className="border py-2 px-4">Nama Menu</th>
                                <th className="border py-2 px-4">Harga</th>
                                <th className="border py-2 px-4">Aksi</th>
                            </tr>
                        </thead>
                        <tbody>
                            {menuItems.map((menuItem) => (
                                <tr key={menuItem.id_menu}>
                                    <td className="border py-2 px-4">{menuItem.nama_menu}</td>
                                    <td className="border py-2 px-4">Rp {menuItem.harga.toLocaleString()}</td>
                                    <td className="border py-2 px-4">
                                        <button
                                            className="bg-blue-500 text-white px-2 py-1 rounded mr-2"
                                            onClick={() => handleEditMenuItem(menuItem)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className="bg-red-500 text-white px-2 py-1 rounded"
                                            onClick={() => handleDeleteMenuItem(menuItem.id_menu)}
                                        >
                                            Hapus
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                {/* Form edit menu */}
                {editMenuItem && (
                    <div className="bg-white rounded-lg shadow-lg p-6 mt-6">
                        <h2 className="text-2xl font-bold mb-4">Edit Menu</h2>
                        <input
                            type="text"
                            name="nama_menu"
                            value={editMenuItem.nama_menu}
                            onChange={(e) => setEditMenuItem({ ...editMenuItem, nama_menu: e.target.value })}
                            placeholder="Nama Menu"
                            className="block w-full p-2 border border-gray-300 rounded-lg mb-4"
                        />
                        <input
                            type="number"
                            name="harga"
                            value={editMenuItem.harga}
                            onChange={(e) => setEditMenuItem({ ...editMenuItem, harga: e.target.value })}
                            placeholder="Harga Menu"
                            className="block w-full p-2 border border-gray-300 rounded-lg mb-4"
                        />
                        <button
                            className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-700"
                            onClick={handleUpdateMenuItem}
                        >
                            Update Menu
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ManageFood;
