import React, { useEffect, useState } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from './Sidebar'; // Pastikan jalur impor sudah benar

const ViewTransaction = () => {
    const [transactions, setTransactions] = useState([]);
    const [menus, setMenus] = useState([]); // State untuk menyimpan data menu
    const [searchTerm, setSearchTerm] = useState('');
    const [filterDate, setFilterDate] = useState('');
    const [filterMonth, setFilterMonth] = useState('');
    const [openDetails, setOpenDetails] = useState({}); // Melacak detail yang terbuka berdasarkan ID transaksi
    const [loadingDetails, setLoadingDetails] = useState({}); // Melacak status loading untuk detail
    const [transactionDetails, setTransactionDetails] = useState({}); // Menyimpan detail transaksi yang diambil

    useEffect(() => {
        fetchTransactions();
        fetchMenus(); // Ambil data menu saat komponen mount
    }, []);

    const fetchTransactions = async () => {
        try {
            const response = await fetch('http://localhost:8000/transaksi');
            const data = await response.json();
            setTransactions(data.data || []);
        } catch (error) {
            console.error('Error fetching transactions:', error);
        }
    };

    const fetchMenus = async () => {
        try {
            const response = await fetch('http://localhost:8000/menu'); // Sesuaikan endpoint jika diperlukan
            const data = await response.json();
            setMenus(data.data || []); // Asumsikan data menu ada di dalam data.data
        } catch (error) {
            console.error('Error fetching menus:', error);
        }
    };

    const fetchTransactionDetails = async (id) => {
        setLoadingDetails((prev) => ({ ...prev, [id]: true })); // Set loading state
        try {
            const response = await fetch(`http://localhost:8000/transaksi/${id}`); // Ambil detail transaksi berdasarkan ID
            const data = await response.json();
            setTransactionDetails((prev) => ({ ...prev, [id]: data.data.detail_transaksi })); // Simpan detail transaksi
        } catch (error) {
            console.error('Error fetching transaction details:', error);
        } finally {
            setLoadingDetails((prev) => ({ ...prev, [id]: false })); // Reset loading state
        }
    };

    const handleSearch = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleDateFilter = async () => {
        if (filterDate) {
            const response = await fetch(`http://localhost:8000/transaksi/${filterDate}`);
            const data = await response.json();
            setTransactions(data.data || []);
        }
    };

    const handleMonthFilter = async () => {
        if (filterMonth) {
            const response = await fetch(`http://localhost:8000/transaksi/bulan/${filterMonth}`);
            const data = await response.json();
            setTransactions(data.data || []);
        }
    };

    const filteredTransactions = transactions.filter(transaction =>
        transaction.nama_pelanggan.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const toggleDetails = (id) => {
        if (!openDetails[id]) {
            fetchTransactionDetails(id); // Ambil detail jika belum terbuka
        }
        setOpenDetails((prev) => ({
            ...prev,
            [id]: !prev[id], // Toggle visibilitas
        }));
    };

    return (
        <div className="flex min-h-screen bg-pastelPink">
            <Sidebar className="w-1/4" /> {/* Atur lebar sidebar */}

            <div className="flex-1 p-4">
                <h1 className="text-3xl font-bold text-pastelRose text-center mb-6">All Transactions</h1>
                <div className="bg-pastelLightPink rounded-lg shadow-lg p-6 mb-4">
                    <div className="mb-4">
                        <input
                            type="text"
                            placeholder="Search by Customer Name"
                            className="mt-1 block w-full p-2 border border-pastelRose rounded-lg focus:ring-2 focus:ring-pastelRose"
                            value={searchTerm}
                            onChange={handleSearch}
                        />
                    </div>
                    <div className="mb-4 flex space-x-2">
                        <input
                            type="date"
                            className="block w-1/2 p-2 border border-pastelRose rounded-lg focus:ring-2 focus:ring-pastelRose"
                            value={filterDate}
                            onChange={(e) => setFilterDate(e.target.value)}
                        />
                        <button 
                            onClick={handleDateFilter} 
                            className="bg-pastelRed text-white rounded-lg px-4 py-2 hover:bg-pastelRose transition-colors duration-200 w-1/2"
                        >
                            Filter by Date
                        </button>
                    </div>
                    <div className="mb-4 flex space-x-2">
                        <input
                            type="month"
                            className="block w-1/2 p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#A6E3E9]"
                            value={filterMonth}
                            onChange={(e) => setFilterMonth(e.target.value)}
                        />
                        <button
                            onClick={handleMonthFilter}
                            className="bg-[#8b5742] text-white rounded-lg px-4 py-2 hover:bg-[#697565] transition-colors duration-200 w-1/2"
                        >
                            Filter by Month
                        </button>
                    </div>
                </div>

                <div className="bg-pastelLightPink rounded-lg shadow-lg p-6">
                    <h2 className="text-xl font-semibold mb-4 text-white">Transaction List</h2>
                    <ul>
                        {filteredTransactions.length > 0 ? (
                            filteredTransactions.map((transaction, index) => (
                                <li key={index} className="border-b border-pastelRose py-2 relative">
                                    <p className="text-white">
                                        <strong>Customer Name:</strong> {transaction.nama_pelanggan}
                                    </p>
                                    <p className="text-white"><strong>Table Number:</strong> {transaction.id_meja}</p>
                                    <p className="text-white"><strong>Date:</strong> {new Date(transaction.tgl_transaksi).toLocaleString()}</p>

                                    {/* Tombol untuk melihat detail di kanan bawah */}
                                    <button
                                        className="absolute right-0 bottom-0 text-pastelRose mt-2"
                                        onClick={() => toggleDetails(transaction.id_transaksi)} // Ganti dengan ID yang benar
                                    >
                                        See Detail
                                    </button>

                                    {openDetails[transaction.id_transaksi] && ( // Tampilkan detail jika terbuka
                                        <div className="mt-2 p-4 bg-gray-100 rounded-lg">
                                            {loadingDetails[transaction.id_transaksi] ? (
                                                <p>Loading Details...</p>
                                            ) : (
                                                <>
                                                    <h3 className="font-semibold">Selected Menus:</h3>
                                                    <ul>
                                                        {transactionDetails[transaction.id_transaksi]?.map((detail, i) => (
                                                            <li key={i}>
                                                                <p><strong>Menu:</strong> {detail.menu.nama_menu}</p>
                                                                <p><strong>Quantity:</strong> {detail.quantity}</p>
                                                                <p><strong>Price:</strong> {detail.harga}</p>
                                                            </li>
                                                        )) || <p>No menus found.</p>}
                                                    </ul>
                                                    <p><strong>Total Price:</strong> {transaction.total_harga}</p>
                                                    <p><strong>Transaction Date:</strong> {new Date(transaction.tgl_transaksi).toLocaleString()}</p>
                                                </>
                                            )}
                                        </div>
                                    )}

                                </li>
                            ))
                        ) : (
                            <p className="text-red-500">No transactions found.</p>
                        )}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default ViewTransaction;
