import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar'
import jsPDF from 'jspdf';
import axios from 'axios';

const CashierPage = ({ user }) => {
    const [menuItems, setMenuItems] = useState([]);
    const [tables, setTables] = useState([]);
    const [selectedMenu, setSelectedMenu] = useState([]);
    const [selectedMenuItem, setSelectedMenuItem] = useState(null);
    const [isTableOccupied, setIsTableOccupied] = useState(false);
    const [customerName, setCustomerName] = useState('');
    const [tableNumber, setTableNumber] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [transactionReceipt, setTransactionReceipt] = useState(null);
    const [isPaid, setIsPaid] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [dropdownOpen, setDropdownOpen] = useState(false);
    useEffect(() => {
        fetchMenuItems();
        fetchTableNumbers();
    }, []);

    const fetchMenuItems = async () => {
        try {
            const response = await fetch(`http://localhost:8000/menu`);
            const data = await response.json();
            setMenuItems(data.data || []);
        } catch (error) {
            console.error('Error fetching menu items:', error);
        }
    };

    const fetchTableNumbers = async () => {
        try {
            const response = await fetch('http://localhost:8000/meja');
            const data = await response.json();
            setTables(data.data || []);
        } catch (error) {
            console.error('Error fetching table numbers:', error);
        }
    };

    const handleTableCheck = (selectedTable) => {
        const table = tables.find((t) => t.nomor_meja === selectedTable);
        const occupied = table ? table.status === 'terisi' : false;
        setIsTableOccupied(occupied);
        setTableNumber(selectedTable);
    };

    const handleAddMenu = () => {
        if (!selectedMenuItem || quantity < 1) {
            setErrorMessage('Silakan pilih menu dan isi kuantitas terlebih dahulu.');
            return;
        }

        const existingMenu = selectedMenu.find(item => item.id_menu === selectedMenuItem.id_menu);
        if (existingMenu) {
            existingMenu.quantity += quantity; // Tambah kuantitas jika menu sudah ada
        } else {
            const newMenu = {
                ...selectedMenuItem,
                quantity: quantity,
            };
            setSelectedMenu((prev) => [...prev, newMenu]); // Tambahkan menu baru
        }

        setQuantity(1); // Reset kuantitas
        setErrorMessage('');
    };

    const handleMenuChange = (e) => {
        const selectedId = e.target.value;
        const menuItem = menuItems.find(item => item.id_menu === parseInt(selectedId));
        setSelectedMenuItem(menuItem || null); // Set selected menu item
    };

    const handleQuantityChange = (type) => {
        if (type === 'increment') {
            setQuantity((prevQuantity) => prevQuantity + 1);
        } else if (type === 'decrement' && quantity > 1) {
            setQuantity((prevQuantity) => prevQuantity - 1);
        }
    };

    const handleSubmitTransaction = async () => {
        if (!customerName || !tableNumber) {
            setErrorMessage('Nama pelanggan dan nomor meja harus diisi!');
            return;
        }

        if (selectedMenu.length === 0) {
            setErrorMessage('Silakan tambahkan setidaknya satu menu sebelum submit!');
            return;
        }

        const newTransaction = {
            tgl_transaksi: new Date().toISOString(),
            id_user: localStorage.getItem("id_user"),
            id_meja: tableNumber, // Misal tableNumber dari state atau props
            nama_pelanggan: customerName, // Nama pelanggan dari input field
            status: isPaid ? 'lunas' : 'belum_bayar', // Cek jika sudah dibayar, statusnya 'lunas', kalau tidak 'belum_bayar'
            detail_transaksi: selectedMenu.map(item => ({
                id_menu: item.id_menu,
                quantity: item.quantity,
            })),
        };

        try {
            let token = localStorage.getItem('token')
            const response = await fetch('http://localhost:8000/transaksi', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(newTransaction),
            });

            if (response.ok) {
                alert('Transaksi berhasil ditambahkan!');

                // Update table status to "terisi"
                await updateTableStatus(tableNumber, 'terisi');
                let result = await response.json()
                let receipt = { ...newTransaction, id_transaksi: result.insertTransaksi.id_transaksi }
                setTransactionReceipt(receipt);
                setSelectedMenu([]);
                setCustomerName('');
                setTableNumber('');
            } else {
                const errorText = await response.text(); // Ambil teks dari respon
                console.error('Failed to add transaction:', errorText); // Tampilkan pesan kesalahan di console
                setErrorMessage('Gagal menambahkan transaksi. Coba lagi!');
            }
        } catch (error) {
            console.error('Error submitting transaction:', error);
        }
    };

    const updateTableStatus = async (tableNumber, status) => {
        try {
            let token = localStorage.getItem('token')
            const response = await fetch(`http://localhost:8000/meja/${tableNumber}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ status }),
            });

            if (!response.ok) {
                throw new Error('Failed to update table status');
            }
        } catch (error) {
            console.error('Error updating table status:', error);
        }
    };

    
const SearchBar = ({ searchTerm, setSearchTerm }) => (
    <div className="flex items-center bg-white rounded-full shadow-md p-2 w-full md:w-1/2">
        <i className="fas fa-search text-gray-400 ml-2"></i>
        <input
            type="text"
            placeholder="Search..."
            className="ml-2 w-full outline-none"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
        />
    </div>
);

const FilterButton = ({ children }) => (
    <button className="bg-orange-400 text-white rounded-full px-4 py-2">{children}</button>
);

const MenuItem = ({ item, handleQuantityChange, quantity, addToCart }) => (
    <div className="bg-white rounded-lg shadow-md p-4">
        <img src="https://placehold.co/100x150" alt={item.nama_menu} className="w-full h-40 object-cover rounded-lg mb-4" />
        <h3 className="text-xl font-bold">{item.nama_menu} <span className="text-orange-400">Rp. {item.harga}</span></h3>
        <p className="text-gray-600 mb-4">{item.description}</p>
        <div className="flex items-center justify-between">
            <div className="flex items-center">
                <button
                    onClick={() => handleQuantityChange(item, 'decrement')}
                    className="bg-gray-200 text-gray-600 rounded-full px-2 py-1"
                >
                    -
                </button>
                <span className="mx-2">{quantity}</span>
                <button
                    onClick={() => handleQuantityChange(item, 'increment')}
                    className="bg-gray-200 text-gray-600 rounded-full px-2 py-1"
                >
                    +
                </button>
            </div>
            <button
                onClick={() => addToCart(item)}
                className="bg-orange-400 text-white rounded-full px-4 py-2"
            >
                Added to cart
            </button>
        </div>
    </div>
);

const Cart = ({ selectedMenu }) => {
    const totalAmount = selectedMenu.reduce((total, item) => total + item.harga * item.quantity, 0);
    return (
        <div className="w-full md:w-1/4 bg-white p-6">
            <h2 className="text-2xl font-bold mb-4">Cart</h2>
            {selectedMenu.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <div>
                    <ul>
                        {selectedMenu.map((item, index) => (
                            <li key={index} className="flex justify-between">
                                <span>{item.nama_menu} (x{item.quantity})</span>
                                <span>Rp. {item.harga * item.quantity}</span>
                            </li>
                        ))}
                    </ul>
                    <div className="flex justify-between font-bold text-lg mt-4">
                        <span>Total</span>
                        <span>Rp. {totalAmount}</span>
                    </div>
                    <button className="bg-orange-400 text-white rounded-full w-full py-2 mt-4">Place an order</button>
                </div>
            )}
        </div>
    );
};

    const handlePayment = async () => {
        const updatedReceipt = {
            ...transactionReceipt,
            status: "lunas"
        };

        try {
            let token = localStorage.getItem('token')
            const response = await fetch(`http://localhost:8000/transaksi/${transactionReceipt.id_transaksi}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(updatedReceipt),

            });

            if (response.ok) {
                alert('Pembayaran berhasil!');
                setIsPaid(true);
                setTransactionReceipt(updatedReceipt);
            }
        } catch (error) {
            console.error('Error processing payment:', error);
        }
    };

    const handleDone = () => {
        setSelectedMenu([]);
        setTransactionReceipt(null);
        setIsPaid(false);
        window.location.reload();
    };

    const calculateReceiptTotalHarga = () => {
        if (!transactionReceipt) return 0;

        return transactionReceipt.detail_transaksi.reduce((total, item) => {
            const menu = menuItems.find(menuItem => menuItem.id_menu === item.id_menu);
            return total + (menu ? menu.harga * item.quantity : 0);
        }, 0);
    };

    const fetchTransactionDetails = async (id_transaksi) => {
        try {
            const response = await axios.get(`http://localhost:8000/transaksi/detail/${id_transaksi}`);
            if (response.data.status) {
                return response.data.data;
            }
        } catch (error) {
            console.error('Error fetching transaction detail:', error);
        }
        return null;
    };

    const generatePDF = async (id_transaksi) => {
        const transaction = await fetchTransactionDetails(id_transaksi);
        if (!transaction) return;

        const doc = new jsPDF();
        const totalHarga = calculateReceiptTotalHarga();

        doc.setFontSize(18);
        doc.text("Belle Fiori CafÃ©", 105, 20, null, null, "center");
        doc.setFontSize(12);
        doc.text("Jl. Eliot No. 10, Malang", 105, 30, null, null, "center");

        const customerName = transaction.nama_pelanggan || 'Unknown Customer';
        const tableNumber = transaction.id_meja || 'Unknown Table';
        const nama_user = transaction.user?.nama_user || 'Unknown User';

        doc.text(`Tanggal: ${new Date(transactionReceipt.tgl_transaksi).toLocaleDateString()}`, 14, 50);
        doc.setFontSize(12);
        doc.text(`Username: ${nama_user}`, 10, 40);
        doc.text(`Customer: ${customerName}`, 10, 50);
        doc.text(`Table: ${tableNumber}`, 10, 60);

        doc.text("Menu", 14, 90);
        doc.text("Quantity", 100, 90);
        doc.text("Harga", 140, 90);

        let yPosition = 100;
        transactionReceipt.detail_transaksi.forEach((item, index) => {
            const menuItem = menuItems.find(menu => menu.id_menu === item.id_menu);
            const itemPrice = menuItem ? menuItem.harga * item.quantity : 0;
            doc.text(menuItem ? menuItem.nama_menu : '', 14, yPosition);
            doc.text(`${item.quantity}`, 100, yPosition);
            doc.text(`Rp ${itemPrice.toLocaleString()}`, 140, yPosition);
            yPosition += 10;
        });

        doc.text(`Total: Rp ${totalHarga.toLocaleString()}`, 140, yPosition + 10);

        doc.save(`Receipt_${transactionReceipt.id_transaksi}.pdf`);
    };

    const toggleDropdown = () => {
        setDropdownOpen(!dropdownOpen);
    };

    const handleMenuItemClick = (item) => {
        setSelectedMenuItem(item);
        setDropdownOpen(false);
    };
    return (
        <div className="flex min-h-screen bg-pastelLightPink">
            <Sidebar user={user} /> {/* Render Sidebar */}

            <div className="flex flex-col flex-1 p-4">
                <h1 className="text-3xl font-bold mb-6">Tambah Transaksi</h1>
                <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />

                <div className="flex space-x-4 mt-4">
                    <FilterButton>All</FilterButton>
                    <FilterButton>Food</FilterButton>
                    <FilterButton>Beverages</FilterButton>
                </div>

                <div className="bg-white rounded-lg shadow-lg p-6 mt-4">
                    <form onSubmit={(e) => { e.preventDefault(); }} className="space-y-4">
                        <div>
                            <label htmlFor="customerName" className="block text-gray-700">Nama Pelanggan</label>
                            <input
                                type="text"
                                className="mt-1 block w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#A6E3E9]"
                                id="customerName"
                                value={customerName}
                                onChange={(e) => setCustomerName(e.target.value)}
                                placeholder="Masukkan nama pelanggan"
                                required
                            />
                        </div>

                        <div>
                            <label htmlFor="tableNumber" className="block text-gray-700">Nomor Meja</label>
                            <select
                                className="mt-1 block w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#A6E3E9]"
                                id="tableNumber"
                                value={tableNumber}
                                onChange={(e) => handleTableCheck(e.target.value)}
                                required
                            >
                                <option value="">Pilih meja</option>
                                {tables.map((table) => (
                                    <option key={table.id_meja} value={table.id_meja}>{table.nomor_meja}</option>
                                ))}
                            </select>
                            {isTableOccupied && <p className="text-red-500">Meja sudah terisi!</p>}
                        </div>

                        <div>
                            <label htmlFor="menu" className="block text-gray-700">Menu</label>
                            <button onClick={toggleDropdown} className="bg-gray-200 p-2 rounded">
                                {selectedMenuItem ? selectedMenuItem.nama_menu : 'Select a menu item'}
                            </button>
                            {dropdownOpen && (
                                <div className="absolute mt-2 w-full bg-white border border-gray-300 rounded shadow-lg z-10">
                                    {menuItems.map(item => (
                                        <div
                                            key={item.id_menu}
                                            onClick={() => handleMenuItemClick(item)}
                                            className="flex items-center p-2 hover:bg-gray-200 cursor-pointer"
                                        >
                                            <img src={'http://localhost:8000/menu-image/' + item.gambar} alt={item.nama_menu} className="w-8 h-8 mr-2" />
                                            <span>{item.nama_menu}</span>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>

                        <div>
                            <label htmlFor="quantity" className="block text-gray-700">Kuantitas</label>
                            <div className="flex items-center">
                                <button
                                    type="button"
                                    className="bg-pastelRose text-white px-3 py-1 rounded-l-lg"
                                    onClick={() => handleQuantityChange('decrement')}
                                >-</button>
                                <input
                                    type="number"
                                    className="w-16 text-center border border-gray-300 rounded-none"
                                    id="quantity"
                                    value={quantity}
                                    onChange={(e) => setQuantity(parseInt(e.target.value))}
                                    min="1"
                                />
                                <button
                                    type="button"
                                    className="bg-pastelRose text-white px-3 py-1 rounded-r-lg"
                                    onClick={() => handleQuantityChange('increment')}
                                >+</button>
                            </div>
                        </div>

                        <button
                            type="button"
                            className="bg-pastelRose text-white px-4 py-2 rounded-lg hover:bg-[#697565]"
                            onClick={handleAddMenu}
                        >Tambah Menu</button>

                        <button
                            type="button"
                            className="bg-pastelRose text-white px-4 py-2 rounded-lg mt-4 hover:bg-[#697565]"
                            onClick={handleSubmitTransaction}
                        >Submit Transaksi</button>

                        {errorMessage && <p className="text-red-500 mt-4">{errorMessage}</p>}

                        {selectedMenu.length > 0 && (
                            <div className="mt-6">
                                <h2 className="text-xl font-semibold text-gray-700">Daftar Pesanan</h2>
                                <ul>
                                    {selectedMenu.map((item, index) => (
                                        <li key={index} className="mt-2 text-gray-700">
                                            {item.nama_menu} x {item.quantity}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        )}
                    </form>

                    {transactionReceipt && (
                        <div className="bg-white rounded-lg shadow-lg p-6 mt-7">
                            <div className="flex justify-between items-center mb-4">
                                <div className="flex items-center space-x-4">
                                    <img src={require('../assets/clogo.jpg')} alt="CafeLogo" className="w-16 h-16 rounded-full border-2 border-pastelRose" />
                                    <div>
                                        <h2 className="text-2xl font-bold text-pastelRose">Belle Fiori</h2>
                                        <p className="text-gray-500">Jl. Eliot No. 10, Malang</p>
                                    </div>
                                </div>
                                <p className="text-gray-500 text-lg">{new Date().toLocaleDateString()}</p>
                            </div>

                            <div className="border-t border-b border-gray-300 py-4">
                                <div className="flex mb-2">
                                    <p className="font-semibold">Customer Name:</p>
                                    <p className="ml-2">{transactionReceipt.nama_pelanggan}</p>
                                </div>
                                <div className="flex mb-2">
                                    <p className="font-semibold">Table:</p>
                                    <p className="ml-2">{transactionReceipt.id_meja}</p>
                                </div>
                            </div>

                            <h3 className="text-lg font-semibold mb-2">Order Details:</h3>
                            <ul>
                                {transactionReceipt.menu_items.map(item => (
                                    <li key={item.id_menu} className="flex justify-between">
                                        <span>{item.nama_menu} x {item.jumlah}</span>
                                        <span>{item.harga * item.jumlah} IDR</span>
                                    </li>
                                ))}
                            </ul>
                            <div className="flex justify-between font-bold mt-2">
                                <span>Total:</span>
                                <span>{calculateReceiptTotalHarga()} IDR</span>
                            </div>
                            <button
                                onClick={handlePayment}
                                className={`mt-4 px-4 py-2 rounded-lg ${isPaid ? 'bg-gray-400' : 'bg-pastelRose text-white hover:bg-[#697565]'}`}
                                disabled={isPaid}
                            >
                                {isPaid ? 'Paid' : 'Mark as Paid'}
                            </button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default CashierPage;
