import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';
import jsPDF from 'jspdf';
import axios from 'axios';

const CashierPage = ({ user }) => {
    const [menuItems, setMenuItems] = useState([]);
    const [tables, setTables] = useState([]);
    const [selectedMenu, setSelectedMenu] = useState([]);
    const [selectedMenuItem, setSelectedMenuItem] = useState(null);
    const [isTableOccupied, setIsTableOccupied] = useState(false);
    const [customerName, setCustomerName] = useState('');
    const [tableNumber, setTableNumber] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [transactionReceipt, setTransactionReceipt] = useState(null);
    const [isPaid, setIsPaid] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchMenuItems();
        fetchTableNumbers();
    }, []);

    const fetchMenuItems = async () => {
        try {
            const response = await fetch(`http://localhost:8000/menu`);
            const data = await response.json();
            setMenuItems(data.data || []);
        } catch (error) {
            console.error('Error fetching menu items:', error);
        }
    };

    const fetchTableNumbers = async () => {
        try {
            const response = await fetch('http://localhost:8000/meja');
            const data = await response.json();
            setTables(data.data || []);
        } catch (error) {
            console.error('Error fetching table numbers:', error);
        }
    };

    const handleTableCheck = (selectedTable) => {
        const table = tables.find((t) => t.nomor_meja === selectedTable);
        const occupied = table ? table.status === 'terisi' : false;
        setIsTableOccupied(occupied);
        setTableNumber(selectedTable);
    };

    const handleAddMenu = () => {
        if (!selectedMenuItem || quantity < 1) {
            setErrorMessage('Silakan pilih menu dan isi kuantitas terlebih dahulu.');
            return;
        }

        const existingMenu = selectedMenu.find(item => item.id_menu === selectedMenuItem.id_menu);
        if (existingMenu) {
            existingMenu.quantity += quantity;
        } else {
            const newMenu = {
                ...selectedMenuItem,
                quantity: quantity,
            };
            setSelectedMenu((prev) => [...prev, newMenu]);
        }

        setQuantity(1);
        setErrorMessage('');
    };

    const handleMenuChange = (e) => {
        const selectedId = e.target.value;
        const menuItem = menuItems.find(item => item.id_menu === parseInt(selectedId));
        setSelectedMenuItem(menuItem || null);
    };

    const handleQuantityChange = (type) => {
        if (type === 'increment') {
            setQuantity((prevQuantity) => prevQuantity + 1);
        } else if (type === 'decrement' && quantity > 1) {
            setQuantity((prevQuantity) => prevQuantity - 1);
        }
    };

    const handleSubmitTransaction = async () => {
        if (!customerName || !tableNumber) {
            setErrorMessage('Nama pelanggan dan nomor meja harus diisi!');
            return;
        }

        if (selectedMenu.length === 0) {
            setErrorMessage('Silakan tambahkan setidaknya satu menu sebelum submit!');
            return;
        }

        const newTransaction = {
            tgl_transaksi: new Date().toISOString(),
            id_user: localStorage.getItem("id_user"),
            id_meja: tableNumber,
            nama_pelanggan: customerName,
            status: isPaid ? 'lunas' : 'belum_bayar',
            detail_transaksi: selectedMenu.map(item => ({
                id_menu: item.id_menu,
                quantity: item.quantity,
            })),
        };

        try {
            let token = localStorage.getItem('token')
            const response = await fetch('http://localhost:8000/transaksi', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(newTransaction),
            });

            if (response.ok) {
                alert('Transaksi berhasil ditambahkan!');
                await updateTableStatus(tableNumber, 'terisi');
                let result = await response.json();
                let receipt = { ...newTransaction, id_transaksi: result.insertTransaksi.id_transaksi };
                setTransactionReceipt(receipt);
                setSelectedMenu([]);
                setCustomerName('');
                setTableNumber('');
            } else {
                const errorText = await response.text();
                console.error('Failed to add transaction:', errorText);
                setErrorMessage('Gagal menambahkan transaksi. Coba lagi!');
            }
        } catch (error) {
            console.error('Error submitting transaction:', error);
        }
    };

    const updateTableStatus = async (tableNumber, status) => {
        try {
            let token = localStorage.getItem('token')
            const response = await fetch(`http://localhost:8000/meja/${tableNumber}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ status }),
            });

            if (!response.ok) {
                throw new Error('Failed to update table status');
            }
        } catch (error) {
            console.error('Error updating table status:', error);
        }
    };

    const handlePayment = async () => {
        const updatedReceipt = {
            ...transactionReceipt,
            status: "lunas"
        };

        try {
            let token = localStorage.getItem('token')
            const response = await fetch(`http://localhost:8000/transaksi/${transactionReceipt.id_transaksi}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(updatedReceipt),
            });

            if (response.ok) {
                alert('Pembayaran berhasil!');
                setIsPaid(true);
                setTransactionReceipt(updatedReceipt);
            }
        } catch (error) {
            console.error('Error processing payment:', error);
        }
    };

    const handleDone = () => {
        setSelectedMenu([]);
        setTransactionReceipt(null);
        setIsPaid(false);
        window.location.reload();
    };

    const calculateReceiptTotalHarga = () => {
        if (!transactionReceipt) return 0;

        return transactionReceipt.detail_transaksi.reduce((total, item) => {
            const menu = menuItems.find(menuItem => menuItem.id_menu === item.id_menu);
            return total + (menu ? menu.harga * item.quantity : 0);
        }, 0);
    };

    const fetchTransactionDetails = async (id_transaksi) => {
        try {
            const response = await axios.get(`http://localhost:8000/transaksi/detail/${id_transaksi}`);
            if (response.data.status) {
                return response.data.data;
            }
        } catch (error) {
            console.error('Error fetching transaction detail:', error);
        }
        return null;
    };

    const generatePDF = async (id_transaksi) => {
        const transaction = await fetchTransactionDetails(id_transaksi);
        if (!transaction) return;

        const doc = new jsPDF();
        const totalHarga = calculateReceiptTotalHarga();

        doc.setFontSize(18);
        doc.text("Belle Fiori CafÃ©", 105, 20, null, null, "center");
        doc.setFontSize(12);
        doc.text("Jl. Eliot No. 10, Malang", 105, 30, null, null, "center");

        const customerName = transaction.nama_pelanggan || 'Unknown Customer';
        const tableNumber = transaction.id_meja || 'Unknown Table';
        const nama_user = transaction.user?.nama_user || 'Unknown User';

        doc.text(`Tanggal: ${new Date(transactionReceipt.tgl_transaksi).toLocaleDateString()}`, 14, 50);
        doc.setFontSize(12);
        doc.text(`Username: ${nama_user}`, 10, 40);
        doc.text(`Customer: ${customerName}`, 10, 50);
        doc.text(`Table: ${tableNumber}`, 10, 60);

        doc.text("Menu", 14, 90);
        doc.text("Quantity", 100, 90);
        doc.text("Harga", 140, 90);

        let yPosition = 100;
        transactionReceipt.detail_transaksi.forEach((item, index) => {
            const menuItem = menuItems.find(menu => menu.id_menu === item.id_menu);
            const itemPrice = menuItem ? menuItem.harga * item.quantity : 0;
            doc.text(menuItem ? menuItem.nama_menu : '', 14, yPosition);
            doc.text(`${item.quantity}`, 100, yPosition);
            doc.text(`Rp ${itemPrice.toLocaleString()}`, 140, yPosition);
            yPosition += 10;
        });

        doc.text(`Total: Rp ${totalHarga.toLocaleString()}`, 140, yPosition + 10);

        doc.save(`Receipt_${transactionReceipt.id_transaksi}.pdf`);
    };

    return (
        <div className="flex min-h-screen bg-pastelLightPink">
            <Sidebar />
            <div className="flex-grow flex flex-col p-4">
                <h1 className="text-2xl font-bold mb-4">Cashier</h1>
                <div className="flex flex-col space-y-4">
                    <input
                        type="text"
                        className="border p-2"
                        placeholder="Customer Name"
                        value={customerName}
                        onChange={(e) => setCustomerName(e.target.value)}
                    />
                    <select
                        className="border p-2"
                        value={tableNumber}
                        onChange={(e) => handleTableCheck(e.target.value)}
                    >
                        <option value="">Select Table</option>
                        {tables.map((table, index) => (
                            <option key={index} value={table.nomor_meja}>
                                Table {table.nomor_meja} ({table.status})
                            </option>
                        ))}
                    </select>
                    {isTableOccupied && <p className="text-red-500">This table is occupied.</p>}
                    <div>
                        <select className="border p-2" onChange={handleMenuChange}>
                            <option value="">Select Menu</option>
                            {menuItems.map((item, index) => (
                                <option key={index} value={item.id_menu}>
                                    {item.nama_menu} - Rp {item.harga}
                                </option>
                            ))}
                        </select>
                        <div className="flex items-center">
                            <button className="border p-2" onClick={() => handleQuantityChange('decrement')}>-</button>
                            <span className="p-2">{quantity}</span>
                            <button className="border p-2" onClick={() => handleQuantityChange('increment')}>+</button>
                        </div>
                        <button className="border p-2 bg-blue-500 text-white" onClick={handleAddMenu}>Add Menu</button>
                        {errorMessage && <p className="text-red-500">{errorMessage}</p>}
                    </div>
                    <div>
                        <button className="border p-2 bg-green-500 text-white" onClick={handleSubmitTransaction}>
                            Submit Transaction
                        </button>
                        {transactionReceipt && (
                            <div>
                                <p>Transaction Receipt:</p>
                                <button className="border p-2 bg-blue-500 text-white" onClick={() => generatePDF(transactionReceipt.id_transaksi)}>
                                    Download Receipt
                                </button>
                                {!isPaid && (
                                    <button className="border p-2 bg-red-500 text-white" onClick={handlePayment}>
                                        Mark as Paid
                                    </button>
                                )}
                            </div>
                        )}
                        <button className="border p-2 bg-gray-500 text-white" onClick={handleDone}>
                            Done
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CashierPage;
