import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import 'tailwindcss/tailwind.css';
import Profile from '../assets/profile.jpg';

const Sidebar = () => {
    const [user, setUser] = useState(null); // State for storing user data
    const navigate = useNavigate();

    // Function to fetch user data
    const fetchUser = async () => {
        try {
            const response = await fetch('http://localhost:8000/user', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include', // Include credentials if token is stored in cookie
            });
            const data = await response.json();
            setUser(data.data); // Save user data in state
        } catch (error) {
            console.error('Failed to fetch user data', error);
        }
    };

    // Call fetchUser when component is rendered
    useEffect(() => {
        fetchUser();
    }, []);

    const handleCashier = () => {
        navigate('/cashier');
    };

    const handleViewAllTransactions = () => {
        navigate('/transactions');
    };

    const handleLogout = async () => {
        try {
            await fetch('http://localhost:8000/logout', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include', // Include credentials to properly log out the session
            });

            // Clear user state and navigate to the login page
            setUser(null);
            navigate('/login');
        } catch (error) {
            console.error('Logout failed', error);
        }
    };

    return (
        <div className="bg-white w-64 h-full shadow-lg rounded-lg p-6 m-4">
            <img 
                src={Profile} 
                alt="Profile" 
                className="w-20 h-20 rounded-full mx-auto mb-4" 
            />
            <h3 className="text-xl font-semibold mb-2 text-center">{user?.username || 'User'}</h3>
            <p className="text-gray-700 text-center">Role: {user?.role || 'Guest'}</p>
            <p className="text-gray-700 text-center">Status: Online</p>
            <button 
                onClick={handleCashier} 
                className="mt-4 bg-pastelRose text-white rounded-lg px-4 py-2 w-full hover:bg-pastelLightPink transition duration-300"
            >
                Add Transaction
            </button>
            <button 
                onClick={handleViewAllTransactions} 
                className="mt-2 bg-pastelRose text-white rounded-lg px-4 py-2 w-full hover:bg-pastelLightPink transition duration-300"
            >
                View All Transactions
            </button>
            <button 
                onClick={handleLogout} 
                className="mt-4 bg-pastelRed text-white rounded-lg px-4 py-2 w-full hover:bg-pastelDarkRed transition duration-300"
            >
                Logout
            </button>
        </div>
    );
};

export default Sidebar;
