import React, { useState } from 'react';
import 'tailwindcss/tailwind.css';
imoport 

const Cashier = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Status login
  const [customerName, setCustomerName] = useState('');
  const [tableNumber, setTableNumber] = useState('');
  const [orders, setOrders] = useState([]);
  const [menuInput, setMenuInput] = useState('');
  const [quantityInput, setQuantityInput] = useState('');
  const [showReceipt, setShowReceipt] = useState(false);
  const [status, setStatus] = useState('Belum Bayar');

  const handleLogin = () => {
    setIsLoggedIn(true); // Ubah status login
  };

  const handleOrderSubmit = (e) => {
    e.preventDefault();
    const order = {
      menu: menuInput,
      quantity: parseInt(quantityInput),
    };
    setOrders([...orders, order]);
    setMenuInput('');
    setQuantityInput('');
  };

  const handlePrintReceipt = () => {
    setShowReceipt(true); // Tampilkan nota
  };

  const handlePayment = () => {
    setStatus('Lunas'); // Ubah status menjadi lunas
  };

  return (
    <div className="flex-1 p-5">
      <h1 className="text-3xl font-bold mb-5">Cofesho - Kasir</h1>

      {!isLoggedIn ? (
        <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-md mx-auto">
          <h2 className="text-2xl mb-4">Login Kasir</h2>
          <button
            onClick={handleLogin}
            className="w-full bg-pink-500 text-white py-2 rounded-lg hover:bg-pink-600 transition duration-300"
          >
            Login
          </button>
        </div>
      ) : (
        <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-md mx-auto">
          <h2 className="text-2xl mb-4">Transaksi Pemesanan</h2>

          <div className="mb-4">
            <input
              type="text"
              value={customerName}
              onChange={(e) => setCustomerName(e.target.value)}
              placeholder="Nama Pelanggan"
              className="border border-gray-300 p-2 rounded-lg w-full mb-2"
              required
            />
            <input
              type="text"
              value={tableNumber}
              onChange={(e) => setTableNumber(e.target.value)}
              placeholder="Nomor Meja"
              className="border border-gray-300 p-2 rounded-lg w-full mb-2"
              required
            />
          </div>

          <form onSubmit={handleOrderSubmit} className="mb-4">
            <input
              type="text"
              value={menuInput}
              onChange={(e) => setMenuInput(e.target.value)}
              placeholder="Masukkan menu..."
              className="border border-gray-300 p-2 rounded-lg w-full mb-2"
              required
            />
            <input
              type="number"
              value={quantityInput}
              onChange={(e) => setQuantityInput(e.target.value)}
              placeholder="Jumlah"
              className="border border-gray-300 p-2 rounded-lg w-full mb-2"
              required
              min="1"
            />
            <button
              type="submit"
              className="w-full bg-pink-500 text-white py-2 rounded-lg hover:bg-pink-600 transition duration-300"
            >
              Tambah Pesanan
            </button>
          </form>

          <h3 className="text-xl mb-2">Daftar Pesanan:</h3>
          <ul className="list-disc pl-5 mb-4">
            {orders.map((order, index) => (
              <li key={index}>{order.menu} (x{order.quantity})</li>
            ))}
          </ul>

          <button
            onClick={handlePrintReceipt}
            className="w-full bg-green-500 text-white py-2 rounded-lg hover:bg-green-600 transition duration-300"
          >
            Cetak Nota
          </button>

          <button
            onClick={handlePayment}
            className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition duration-300 mt-2"
          >
            Bayar
          </button>
        </div>
      )}

      {/* Menampilkan nota jika tombol cetak nota ditekan */}
      {showReceipt && (
        <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-md mx-auto mt-10">
          <h2 className="text-2xl mb-4">Nota Transaksi</h2>
          <p className="font-bold">Wikusama Cafe</p>
          <p>Tanggal: {new Date().toLocaleDateString()}</p>
          <p>Nama Pelanggan: {customerName}</p>
          <p>Nomor Meja: {tableNumber}</p>
          <h3 className="text-xl mb-2">Daftar Pesanan:</h3>
          <ul className="list-disc pl-5 mb-4">
            {orders.map((order, index) => (
              <li key={index}>{order.menu} (x{order.quantity})</li>
            ))}
          </ul>
          <p>Status: {status}</p>
        </div>
      )}
    </div>
  );
};

export default Cashier;
