import React, { useState } from 'react';
import 'tailwindcss/tailwind.css';


// Komponen untuk Login
const AdminLogin = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    if (username === 'admin' && password === 'admin123') {
      onLogin(true); // Jika login berhasil
    } else {
      alert('Invalid credentials');
    }
  };

  return (
    <div className="login-page">
      <h2>Admin Login</h2>
      <input 
        type="text" 
        placeholder="Username" 
        value={username} 
        onChange={(e) => setUsername(e.target.value)} 
      />
      <input 
        type="password" 
        placeholder="Password" 
        value={password} 
        onChange={(e) => setPassword(e.target.value)} 
      />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

// Komponen untuk Mengelola User
const ManageUsers = () => {
  const [users, setUsers] = useState([
    { id: 1, name: 'John Doe', role: 'Cashier' },
    { id: 2, name: 'Jane Smith', role: 'Manager' }
  ]);

  const handleDelete = (id) => {
    setUsers(users.filter(user => user.id !== id));
  };

  return (
    <div>
      <h2>Manage Users</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.role}</td>
              <td>
                <button>Edit</button>
                <button onClick={() => handleDelete(user.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Komponen untuk Mengelola Makanan & Minuman
const ManageFood = () => {
  const [menu, setMenu] = useState([
    { id: 1, name: 'Burger', price: 50000 },
    { id: 2, name: 'Pizza', price: 75000 }
  ]);

  const handleDelete = (id) => {
    setMenu(menu.filter(item => item.id !== id));
  };

  return (
    <div>
      <h2>Manage Food & Beverages</h2>
      <table>
        <thead>
          <tr>
            <th>Menu Item</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {menu.map(item => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.price}</td>
              <td>
                <button>Edit</button>
                <button onClick={() => handleDelete(item.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Komponen untuk Mengelola Meja
const ManageTables = () => {
  const [tables, setTables] = useState([
    { id: 1, number: 'Table 1', capacity: 4 },
    { id: 2, number: 'Table 2', capacity: 2 }
  ]);

  const handleDelete = (id) => {
    setTables(tables.filter(table => table.id !== id));
  };

  return (
    <div>
      <h2>Manage Tables</h2>
      <table>
        <thead>
          <tr>
            <th>Table Number</th>
            <th>Capacity</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tables.map(table => (
            <tr key={table.id}>
              <td>{table.number}</td>
              <td>{table.capacity}</td>
              <td>
                <button>Edit</button>
                <button onClick={() => handleDelete(table.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Halaman Admin utama dengan navigasi antar bagian
const AdminPage = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activePage, setActivePage] = useState('users'); // Default ke 'users'

  if (!isLoggedIn) {
    return <AdminLogin onLogin={setIsLoggedIn} />;
  }

  return (
    <div className="admin-page">
      <h2>Admin Dashboard</h2>
      <nav>
        <ul>
          <li onClick={() => setActivePage('users')}>Manage Users</li>
          <li onClick={() => setActivePage('food')}>Manage Food & Beverages</li>
          <li onClick={() => setActivePage('tables')}>Manage Tables</li>
        </ul>
      </nav>

      <div className="content">
        {activePage === 'users' && <ManageUsers />}
        {activePage === 'food' && <ManageFood />}
        {activePage === 'tables' && <ManageTables />}
      </div>
    </div>
  );
};

export default AdminPage;
