import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from './Sidebar';
import CafeLogo from '../assets/logo.jpg';

const Cashier = ({ user }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [customerName, setCustomerName] = useState('');
  const [tableNumber, setTableNumber] = useState('');
  const [orders, setOrders] = useState([]);
  const [menuInput, setMenuInput] = useState('');
  const [quantityInput, setQuantityInput] = useState('');
  const [showReceipt, setShowReceipt] = useState(false);
  const [status, setStatus] = useState('Belum Bayar');

  const [tables, setTables] = useState([]); // Initialize as empty array
  const [menuItems, setMenuItems] = useState([]); // Initialize menu items

  const total = orders.reduce((sum, order) => sum + order.quantity * 10000, 0); // Example total price
  
  useEffect(() => {
    const fetchTables = async () => {
      try {
        const response = await fetch('http://localhost:8000/meja');
        const data = await response.json();
        setTables(data.data || []); // Set tables to the fetched data
      } catch (error) {
        console.error("Error fetching tables:", error);
      }
    };

    const fetchMenuItems = async () => {
      try {
        const response = await fetch('http://localhost:8000/menu');
        const data = await response.json();

        // Log data for debugging
        console.log('Menu Items Data:', data);

        setMenuItems(data.data || []); // Set menu items to the fetched data
      } catch (error) {
        console.error("Error fetching menu items:", error);
      }
    };

    fetchTables();
    fetchMenuItems();
  }, []);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleOrderSubmit = (e) => {
    e.preventDefault();
    const order = {
      menu: menuInput,
      quantity: parseInt(quantityInput),
    };
    setOrders([...orders, order]);
    setMenuInput('');
    setQuantityInput('');
  };

  const handlePrintReceipt = () => {
    setShowReceipt(true);
  };

  const handlePayment = async () => {
    const transactionData = {
      customerName,
      tableNumber,
      orders,
      total,
      status: 'Lunas', // Update status as needed
    };

    try {
      const response = await fetch('http://localhost:8000/transaksi', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(transactionData),
      });

      if (response.ok) {
        setStatus('Lunas');
        // Optionally clear the orders and other states after successful payment
        setOrders([]);
        setCustomerName('');
        setTableNumber('');
      } else {
        console.error('Failed to post transaction');
      }
    } catch (error) {
      console.error("Error posting transaction:", error);
    }
  };

  return (
    <div className="flex bg-pastelPink min-h-screen"> {/* Updated background color */}
      <Sidebar user={user} />
      <div className="flex-1 p-5">
        {!isLoggedIn ? (
          <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-md mx-auto"> {/* Form background set to white */}
            <h2 className="text-2xl mb-4">Login Kasir</h2>
            <button
              onClick={handleLogin}
              className="w-full bg-pastelRose text-white py-2 rounded-lg hover:bg-pastelLightPink transition duration-300" // Updated button color
            >
              Login
            </button>
          </div>
        ) : (
          <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-2xl mx-auto"> {/* Form background set to white */}
            <h2 className="text-2xl font-bold mb-6">Transaksi Pemesanan</h2>

            <div className="mb-6 grid grid-cols-1 gap-4">
              <input
                type="text"
                value={customerName}
                onChange={(e) => setCustomerName(e.target.value)}
                placeholder="Nama Pelanggan"
                className="border border-gray-300 p-3 rounded-lg w-full"
                required
              />
              <select
                value={tableNumber}
                onChange={(e) => setTableNumber(e.target.value)}
                className="border border-gray-300 p-3 rounded-lg w-full"
                required
              >
                <option value="">Pilih Nomor Meja</option>
                {Array.isArray(tables) && tables.map((table) => (
                  <option key={table.id_meja} value={table.nomor_meja}>
                    {table.nomor_meja} {/* Pastikan ini adalah properti yang benar */}
                  </option>
                ))}
              </select>
            </div>

            <form onSubmit={handleOrderSubmit} className="mb-6 grid grid-cols-1 gap-4">
              <div className="flex space-x-4">
                <select
                  value={menuInput}
                  onChange={(e) => setMenuInput(e.target.value)}
                  className="border border-gray-300 p-3 rounded-lg flex-1"
                  required
                >
                  <option value="">Pilih Menu...</option>
                  {Array.isArray(menuItems) && menuItems.length > 0 ? (
                    menuItems.map((item) => (
                      <option key={item.id} value={item.name}>
                        {item.name} {/* Pastikan 'name' adalah properti yang benar */}
                      </option>
                    ))
                  ) : (
                    <option value="">Tidak ada menu tersedia</option> // Handle case with no menu
                  )}
                </select>
                <input
                  type="number"
                  value={quantityInput}
                  onChange={(e) => setQuantityInput(e.target.value)}
                  placeholder="Jumlah"
                  className="border border-gray-300 p-3 rounded-lg w-24"
                  required
                  min="1"
                />
              </div>
              <button
                type="submit"
                className="w-full bg-pastelRose text-white py-2 rounded-lg hover:bg-pastelLightPink transition duration-300" // Updated button color
              >
                Tambah Pesanan
              </button>
            </form>

            <h3 className="text-xl font-semibold mb-4">Daftar Pesanan:</h3>
            <ul className="list-disc pl-5 mb-4">
              {orders.map((order, index) => (
                <li key={index}>
                  {order.menu} (x{order.quantity})
                </li>
              ))}
            </ul>

            <button
              onClick={handlePrintReceipt}
              className="w-full bg-green-400 text-white py-2 rounded-lg hover:bg-green-500 transition duration-300"
            >
              Cetak Nota
            </button>

            <button
              onClick={handlePayment}
              className="w-full bg-blue-400 text-white py-2 rounded-lg hover:bg-blue-500 transition duration-300 mt-2"
            >
              Bayar
            </button>
          </div>
        )}

        {showReceipt && (
          <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-2xl mx-auto mt-10"> {/* Updated background color to white */}
            <div className="flex justify-between items-center mb-4">
              {/* Bagian header nota dengan logo dan nama cafe */}
              <div className="flex items-center space-x-4">
                <img src={CafeLogo} alt="Cafe Logo" className="w-16 h-16" />
                <div>
                  <h2 className="text-2xl font-bold">Belle Fiori</h2>
                  <p className="text-gray-500">112 Elliot, Riverside, Jakarta</p>
                  <p className="text-gray-500">Telp: 0819-9779-9924</p>
                </div>
              </div>
              <p className="text-gray-500">{new Date().toLocaleDateString()}</p>
            </div>

            {/* Detail nota dengan grid layout */}
            <div className="border-t border-b border-gray-300 py-4">
              <div className="flex justify-between mb-2">
                <p>Customer Name:</p>
                <p>{customerName}</p>
              </div>
              <div className="flex justify-between mb-2">
                <p>Table Number:</p>
                <p>{tableNumber}</p>
              </div>
              <div className="flex justify-between mb-2">
                <p>Status:</p>
                <p className={`${status === 'Lunas' ? 'text-green-500' : 'text-red-500'}`}>{status}</p>
              </div>
            </div>

            <h3 className="text-xl font-semibold my-4">Order Summary:</h3>
            <ul className="list-none mb-4">
              {orders.map((order, index) => (
                <li key={index} className="flex justify-between">
                  <span>{order.menu}</span>
                  <span>x{order.quantity}</span>
                </li>
              ))}
            </ul>
            <div className="flex justify-between font-bold">
              <span>Total:</span>
              <span>{total} IDR</span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cashier;
