import React, { useState } from 'react';


const Cashier = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [customerName, setCustomerName] = useState('');
  const [tableNumber, setTableNumber] = useState('');
  const [orders, setOrders] = useState([]);
  const [menuInput, setMenuInput] = useState('');
  const [quantityInput, setQuantityInput] = useState('');

  const handleLogin = () => {
    // Logic untuk login kasir
    setIsLoggedIn(true);
  };

  const handleOrderSubmit = (e) => {
    e.preventDefault();
    const order = {
      menu: menuInput,
      quantity: parseInt(quantityInput),
      status: 'Belum Bayar',
    };
    setOrders([...orders, order]);
    setMenuInput('');
    setQuantityInput('');
  };

  const handlePrintReceipt = () => {
    // Logic untuk mencetak nota
    const receipt = `Wikusama Cafe\nDate: ${new Date().toLocaleDateString()}\nCashier: [Nama Kasir]\nCustomer: ${customerName}\nTable: ${tableNumber}\nOrders:\n${orders.map(order => `${order.menu} (x${order.quantity}) - ${order.status}`).join('\n')}`;
    
    console.log(receipt); // Simulasi pencetakan nota
    alert('Nota telah dicetak ke konsol.');
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-pink-100 p-5">
      <h1 className="text-3xl font-bold mb-5">Wikusama Cafe - Kasir</h1>

      {!isLoggedIn ? (
        <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-md">
          <h2 className="text-2xl mb-4">Login Kasir</h2>
          <button
            onClick={handleLogin}
            className="w-full bg-pink-500 text-white py-2 rounded-lg hover:bg-pink-600 transition duration-300"
          >
            Login
          </button>
        </div>
      ) : (
        <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-md">
          <h2 className="text-2xl mb-4">Transaksi Pemesanan</h2>

          <div className="mb-4">
            <input
              type="text"
              value={customerName}
              onChange={(e) => setCustomerName(e.target.value)}
              placeholder="Nama Pelanggan"
              className="border border-gray-300 p-2 rounded-lg w-full mb-2"
              required
            />
            <input
              type="text"
              value={tableNumber}
              onChange={(e) => setTableNumber(e.target.value)}
              placeholder="Nomor Meja"
              className="border border-gray-300 p-2 rounded-lg w-full mb-2"
              required
            />
          </div>

          <form onSubmit={handleOrderSubmit} className="mb-4">
            <input
              type="text"
              value={menuInput}
              onChange={(e) => setMenuInput(e.target.value)}
              placeholder="Masukkan menu..."
              className="border border-gray-300 p-2 rounded-lg w-full mb-2"
              required
            />
            <input
              type="number"
              value={quantityInput}
              onChange={(e) => setQuantityInput(e.target.value)}
              placeholder="Jumlah"
              className="border border-gray-300 p-2 rounded-lg w-full mb-2"
              required
              min="1"
            />
            <button
              type="submit"
              className="w-full bg-pink-500 text-white py-2 rounded-lg hover:bg-pink-600 transition duration-300"
            >
              Tambah Pesanan
            </button>
          </form>

          <h3 className="text-xl mb-2">Daftar Pesanan:</h3>
          <ul className="list-disc pl-5 mb-4">
            {orders.map((order, index) => (
              <li key={index}>{order.menu} (x{order.quantity}) - {order.status}</li>
            ))}
          </ul>

          <button
            onClick={handlePrintReceipt}
            className="w-full bg-green-500 text-white py-2 rounded-lg hover:bg-green-600 transition duration-300"
          >
            Cetak Nota
          </button>
        </div>
      )}
    </div>
  );
};

export default Cashier;
