import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManageFoods = () => {
    const [foods, setFoods] = useState([]);
    const [newFood, setNewFood] = useState({ nama_menu: '', harga: '' });
    const [editingFood, setEditingFood] = useState(null); // null means not editing

    useEffect(() => {
        fetchFoods();
    }, []);

    const fetchFoods = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/menu', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setFoods(data.data || []);
            } else {
                console.error('Failed to fetch foods');
            }
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    const handleAddFood = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/menu', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(newFood),
            });

            if (response.ok) {
                fetchFoods();
                setNewFood({ nama_menu: '', harga: '' }); // Clear form after adding
            } else {
                console.error('Failed to add food');
            }
        } catch (error) {
            console.error('Error adding food:', error);
        }
    };

    const handleEditFood = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8000/menu/${editingFood.id_menu}`, {
                method: 'PUT',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(editingFood),
            });

            if (response.ok) {
                fetchFoods();
                setEditingFood(null); // Close editing form
            } else {
                console.error('Failed to edit food');
            }
        } catch (error) {
            console.error('Error editing food:', error);
        }
    };

    const handleDeleteFood = async (foodId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8000/menu/${foodId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.ok) {
                fetchFoods();
            } else {
                console.error('Failed to delete food');
            }
        } catch (error) {
            console.error('Error deleting food:', error);
        }
    };

    return (
        <div className="flex">
            <Sidebar />

            <div className="flex-grow p-8 bg-white shadow-lg rounded-lg mx-6 mt-4 mb-4">
                <h1 className="text-4xl font-semibold text-pastelDarkRed mb-10 text-center">Manage Foods</h1>

                {/* Form to Add New Food */}
                <form onSubmit={handleAddFood} className="mb-6">
                    <div className="flex mb-4">
                        <input
                            type="text"
                            className="w-1/2 p-2 mr-2 border border-gray-300 rounded"
                            placeholder="Food Name"
                            value={newFood.nama_menu}
                            onChange={(e) => setNewFood({ ...newFood, nama_menu: e.target.value })}
                            required
                        />
                        <input
                            type="number"
                            className="w-1/2 p-2 mr-2 border border-gray-300 rounded"
                            placeholder="Price"
                            value={newFood.harga}
                            onChange={(e) => setNewFood({ ...newFood, harga: e.target.value })}
                            required
                        />
                    </div>
                    <button
                        type="submit"
                        className="bg-green-500 text-white rounded-lg px-4 py-2"
                    >
                        Add Food
                    </button>
                </form>

                {/* Table to Manage Foods */}
                <div className="overflow-x-auto shadow-lg rounded-lg mb-6">
                    <table className="min-w-full bg-white rounded-lg shadow-lg">
                        <thead>
                            <tr className="bg-pastelRose text-white">
                                <th className="py-4 px-6 border-b">Food ID</th>
                                <th className="py-4 px-6 border-b">Food Name</th>
                                <th className="py-4 px-6 border-b">Price</th>
                                <th className="py-4 px-6 border-b">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {foods.map((food, index) => (
                                <tr
                                    key={food.id_menu}
                                    className={`${index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'} hover:bg-pastelRose transition duration-200`}
                                >
                                    <td className="py-4 px-6 border-b">{food.id_menu}</td>
                                    <td className="py-4 px-6 border-b">{food.nama_menu}</td>
                                    <td className="py-4 px-6 border-b">{food.harga.toLocaleString('id-ID', { style: 'currency', currency: 'IDR' })}</td>
                                    <td className="py-4 px-6 border-b flex space-x-2">
                                        <button
                                            className="bg-blue-500 text-white rounded-lg px-4 py-2"
                                            onClick={() => setEditingFood(food)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className="bg-red-500 text-white rounded-lg px-4 py-2"
                                            onClick={() => handleDeleteFood(food.id_menu)}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                {/* Form to Edit Food */}
                {editingFood && (
                    <form onSubmit={handleEditFood} className="mb-6">
                        <h2 className="text-2xl mb-4">Edit Food</h2>
                        <div className="flex mb-4">
                            <input
                                type="text"
                                className="w-1/2 p-2 mr-2 border border-gray-300 rounded"
                                placeholder="Food Name"
                                value={editingFood.nama_menu}
                                onChange={(e) => setEditingFood({ ...editingFood, nama_menu: e.target.value })}
                                required
                            />
                            <input
                                type="number"
                                className="w-1/2 p-2 mr-2 border border-gray-300 rounded"
                                placeholder="Price"
                                value={editingFood.harga}
                                onChange={(e) => setEditingFood({ ...editingFood, harga: e.target.value })}
                                required
                            />
                        </div>
                        <button
                            type="submit"
                            className="bg-blue-500 text-white rounded-lg px-4 py-2"
                        >
                            Save Changes
                        </button>
                        <button
                            type="button"
                            className="bg-gray-500 text-white rounded-lg px-4 py-2 ml-4"
                            onClick={() => setEditingFood(null)}
                        >
                            Cancel
                        </button>
                    </form>
                )}
            </div>
        </div>
    );
};

export default ManageFoods;
