import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';
import { useNavigate } from 'react-router-dom';

const ManagerPage = ({ user }) => {
    const [transactions, setTransactions] = useState([]);
    const [filteredTransactions, setFilteredTransactions] = useState([]);
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetchAllTransactions();
    }, []);

    const fetchAllTransactions = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/transaksi', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.ok) {
                const data = await response.json();
                setTransactions(data.data || []);
                setFilteredTransactions(data.data || []);
            } else {
                console.error('Failed to fetch transactions');
            }
        } catch (error) {
            console.error('Error fetching transactions:', error);
        }
    };

    const handleDateFilter = () => {
        if (!startDate || !endDate) {
            setErrorMessage('Both start and end date must be selected!');
            return;
        }

        const filtered = transactions.filter(transaction => {
            const transactionDate = new Date(transaction.tgl_transaksi);
            return transactionDate >= new Date(startDate) && transactionDate <= new Date(endDate);
        });

        setFilteredTransactions(filtered);
        setErrorMessage('');
    };

    const formatDate = (date) => {
        return new Date(date).toLocaleDateString();
    };

    const calculateTotalTransaction = (transaction) => {
        return transaction.detail_transaksi.reduce((total, item) => {
            return total + (item.harga * item.quantity);
        }, 0);
    };

    // Handle navigation to view reports
    const handleViewReports = () => {
        navigate('/manager/reports'); // Navigate to the reports page
    };

    return (
        <div className="flex min-h-screen bg-gray-100">
            <Sidebar user={user} />

            <div className="flex-grow p-4">
                <h1 className="text-3xl font-bold text-gray-800 mb-6 text-center">Manajemen Transaksi</h1>

                {/* Date Filtering */}
                <div className="flex items-center mb-6">
                    <label htmlFor="startDate" className="mr-4">Start Date:</label>
                    <input
                        type="date"
                        id="startDate"
                        className="border border-gray-300 rounded-lg p-2"
                        value={startDate}
                        onChange={(e) => setStartDate(e.target.value)}
                    />

                    <label htmlFor="endDate" className="ml-4 mr-4">End Date:</label>
                    <input
                        type="date"
                        id="endDate"
                        className="border border-gray-300 rounded-lg p-2"
                        value={endDate}
                        onChange={(e) => setEndDate(e.target.value)}
                    />

                    <button
                        className="ml-4 bg-blue-500 text-white px-4 py-2 rounded-lg"
                        onClick={handleDateFilter}
                    >
                        Filter
                    </button>
                </div>

                {errorMessage && <p className="text-red-500 mb-4">{errorMessage}</p>}

                {/* Button to view reports */}
                <button
                    className="bg-green-500 text-white px-4 py-2 rounded-lg mb-6"
                    onClick={handleViewReports}
                >
                    View Reports
                </button>

                {/* Transaction List */}
                {filteredTransactions.length > 0 ? (
                    <div className="overflow-x-auto">
                        <table className="min-w-full bg-white rounded-lg shadow-lg">
                            <thead>
                                <tr>
                                    <th className="py-2 px-4 border-b">Tanggal Transaksi</th>
                                    <th className="py-2 px-4 border-b">Nama Pelanggan</th>
                                    <th className="py-2 px-4 border-b">Meja</th>
                                    <th className="py-2 px-4 border-b">Karyawan</th>
                                    <th className="py-2 px-4 border-b">Total Transaksi</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filteredTransactions.map((transaction, index) => (
                                    <tr key={index}>
                                        <td className="py-2 px-4 border-b">{formatDate(transaction.tgl_transaksi)}</td>
                                        <td className="py-2 px-4 border-b">{transaction.nama_pelanggan}</td>
                                        <td className="py-2 px-4 border-b">{transaction.id_meja}</td>
                                        <td className="py-2 px-4 border-b">{transaction.id_user}</td>
                                        <td className="py-2 px-4 border-b">
                                            Rp {calculateTotalTransaction(transaction).toLocaleString()}
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                ) : (
                    <p className="text-gray-700 mt-4">Tidak ada transaksi ditemukan</p>
                )}
            </div>
        </div>
    );
};

export default ManagerPage;
