import cover_image from '../assets/cover_image.jpg'

const Login = () => {
    return (
        <div className="w-full h-[100vh] flex items-start"> {/* Mengatur tinggi menjadi 100vh untuk mengisi layar */}
            {/* Container dengan gambar sebagai background */}
            <div
                className='relative w-full h-full flex flex-col justify-center items-start'
                style={{
                    backgroundImage: `url(${cover_image})`,  // Gambar sebagai background
                    backgroundSize: 'cover',                 // Gambar memenuhi seluruh div
                    backgroundPosition: 'center',            // Gambar di posisi tengah
                    backgroundRepeat: 'no-repeat',           // Gambar tidak diulang
                }}
            >
                {/* Teks di atas background gambar dengan jarak dari kiri */}
                <div className='relative z-10 pl-[10%]'> {/* Padding kiri 10% */}
    <h1 style={{ color: 'white', fontSize: '2.5rem', fontWeight: 'bold', marginLeft: '20px', marginTop: '' }}>Turn Your Ideas into Reality</h1>
    <p style={{ color: 'white', fontSize: '1.25rem', marginLeft: '20px', marginTop: '2px' }}>Start for free and get attractive offers from the community</p>
</div>


            </div>
        </div>
    )
}

export default Login;
