import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const AdminPage = ({ user }) => {
    const [users, setUsers] = useState([]);
    const [foods, setFoods] = useState([]);
    const [tables, setTables] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchUsers();
        fetchFoods();
        fetchTables();
    }, []);

    const fetchUsers = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/user', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setUsers(data.data || []);
            } else {
                console.error('Failed to fetch users');
            }
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const fetchFoods = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/menu', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setFoods(data.data || []);
            } else {
                console.error('Failed to fetch foods');
            }
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    const fetchTables = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/meja', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setTables(data.data || []);
            } else {
                console.error('Failed to fetch tables');
            }
        } catch (error) {
            console.error('Error fetching tables:', error);
        }
    };

    const handleRoleChange = (userId, newRole) => {
        // Logic to change user role
        console.log(`Change role for user ${userId} to ${newRole}`);
    };

    return (
        <div className="flex min-h-screen bg-pastelPink">
            <Sidebar user={user} />

            <div className="flex-grow p-8 bg-white shadow-lg rounded-lg mx-6 mt-4 mb-4">
                <h1 className="text-4xl font-semibold text-pastelDarkRed mb-10 text-center">Admin Dashboard</h1>

                {/* Manage Users */}
                <section className="mb-8">
                    <h2 className="text-2xl font-medium text-pastelDarkRed mb-4">Manage Users</h2>
                    <div className="overflow-x-auto shadow-lg rounded-lg mb-6">
                        <table className="min-w-full bg-white rounded-lg shadow-lg">
                            <thead>
                                <tr className="bg-pastelRose text-white">
                                    <th className="py-4 px-6 border-b">User ID</th>
                                    <th className="py-4 px-6 border-b">Username</th>
                                    <th className="py-4 px-6 border-b">Role</th>
                                    <th className="py-4 px-6 border-b">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.map((user, index) => (
                                    <tr
                                        key={index}
                                        className={`${
                                            index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'
                                        } hover:bg-pastelRose transition duration-200`}
                                    >
                                        <td className="py-4 px-6 border-b">{user.id}</td>
                                        <td className="py-4 px-6 border-b">{user.username}</td>
                                        <td className="py-4 px-6 border-b">{user.role}</td>
                                        <td className="py-4 px-6 border-b">
                                            <select
                                                className="border border-pastelRose rounded-lg p-2 bg-pastelLightPink text-pastelDarkRed"
                                                onChange={(e) => handleRoleChange(user.id, e.target.value)}
                                            >
                                                <option value="kasir">Kasir</option>
                                                <option value="manager">Manager</option>
                                                <option value="admin">Admin</option>
                                            </select>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </section>

                {/* Manage Food & Drinks */}
                <section className="mb-8">
                    <h2 className="text-2xl font-medium text-pastelDarkRed mb-4">Manage Food & Drinks</h2>
                    <div className="overflow-x-auto shadow-lg rounded-lg mb-6">
                        <table className="min-w-full bg-white rounded-lg shadow-lg">
                            <thead>
                                <tr className="bg-pastelRose text-white">
                                    <th className="py-4 px-6 border-b">Food ID</th>
                                    <th className="py-4 px-6 border-b">Food Name</th>
                                    <th className="py-4 px-6 border-b">Price</th>
                                </tr>
                            </thead>
                            <tbody>
                                {foods.map((food, index) => (
                                    <tr
                                        key={index}
                                        className={`${
                                            index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'
                                        } hover:bg-pastelRose transition duration-200`}
                                    >
                                        <td className="py-4 px-6 border-b">{food.id}</td>
                                        <td className="py-4 px-6 border-b">{food.nama}</td>
                                        <td className="py-4 px-6 border-b">
                                            Rp {food.harga ? food.harga.toLocaleString() : '0'}
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </section>

                {/* Manage Tables */}
                <section>
                    <h2 className="text-2xl font-medium text-pastelDarkRed mb-4">Manage Tables</h2>
                    <div className="overflow-x-auto shadow-lg rounded-lg mb-6">
                        <table className="min-w-full bg-white rounded-lg shadow-lg">
                            <thead>
                                <tr className="bg-pastelRose text-white">
                                    <th className="py-4 px-6 border-b">Table ID</th>
                                    <th className="py-4 px-6 border-b">Table Name</th>
                                </tr>
                            </thead>
                            <tbody>
                                {tables.map((table, index) => (
                                    <tr
                                        key={index}
                                        className={`${
                                            index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'
                                        } hover:bg-pastelRose transition duration-200`}
                                    >
                                        <td className="py-4 px-6 border-b">{table.id}</td>
                                        <td className="py-4 px-6 border-b">{table.nama_meja}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </section>
            </div>
        </div>
    );
};

export default AdminPage;
