import React, { useState } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from './Sidebar';

const Cashier = ({ user }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Status login
  const [customerName, setCustomerName] = useState('');
  const [tableNumber, setTableNumber] = useState('');
  const [orders, setOrders] = useState([]);
  const [menuInput, setMenuInput] = useState('');
  const [quantityInput, setQuantityInput] = useState('');
  const [showReceipt, setShowReceipt] = useState(false);
  const [status, setStatus] = useState('Belum Bayar');

  const handleLogin = () => {
    setIsLoggedIn(true); // Ubah status login
  };

  const handleOrderSubmit = (e) => {
    e.preventDefault();
    const order = {
      menu: menuInput,
      quantity: parseInt(quantityInput),
    };
    setOrders([...orders, order]);
    setMenuInput('');
    setQuantityInput('');
  };

  const handlePrintReceipt = () => {
    setShowReceipt(true); // Tampilkan nota
  };

  const handlePayment = () => {
    setStatus('Lunas'); // Ubah status menjadi lunas
  };

  return (
    <div className="flex min-h-screen bg-pink-50">
      <Sidebar user={user} />
      <div className="flex-1 p-5">
        {!isLoggedIn ? (
          <div className="bg-pastel-purple shadow-lg rounded-lg p-6 w-full max-w-md mx-auto">
            <h2 className="text-2xl mb-4 text-gray-700">Login Kasir</h2>
            <button
              onClick={handleLogin}
              className="w-full bg-pastel-pink text-white py-2 rounded-lg hover:bg-pastel-pink-dark transition duration-300"
            >
              Login
            </button>
          </div>
        ) : (
          <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-xl mx-auto bg-pastel-blue">
            <h2 className="text-2xl font-bold mb-6 text-gray-700">Transaksi Pemesanan</h2>

            <div className="mb-6 grid grid-cols-1 gap-4">
              <input
                type="text"
                value={customerName}
                onChange={(e) => setCustomerName(e.target.value)}
                placeholder="Nama Pelanggan"
                className="border border-gray-300 p-3 rounded-lg w-full bg-pastel-lavender"
                required
              />
              <input
                type="text"
                value={tableNumber}
                onChange={(e) => setTableNumber(e.target.value)}
                placeholder="Nomor Meja"
                className="border border-gray-300 p-3 rounded-lg w-full bg-pastel-lavender"
                required
              />
            </div>

            <form onSubmit={handleOrderSubmit} className="mb-6 grid grid-cols-1 gap-4">
              <input
                type="text"
                value={menuInput}
                onChange={(e) => setMenuInput(e.target.value)}
                placeholder="Masukkan menu..."
                className="border border-gray-300 p-3 rounded-lg w-full bg-pastel-lavender"
                required
              />
              <input
                type="number"
                value={quantityInput}
                onChange={(e) => setQuantityInput(e.target.value)}
                placeholder="Jumlah"
                className="border border-gray-300 p-3 rounded-lg w-full bg-pastel-lavender"
                required
                min="1"
              />
              <button
                type="submit"
                className="w-full bg-pastel-pink text-white py-2 rounded-lg hover:bg-pastel-pink-dark transition duration-300"
              >
                Tambah Pesanan
              </button>
            </form>

            <h3 className="text-xl font-semibold mb-4 text-gray-700">Daftar Pesanan:</h3>
            <ul className="list-disc pl-5 mb-4 text-gray-600">
              {orders.map((order, index) => (
                <li key={index}>
                  {order.menu} (x{order.quantity})
                </li>
              ))}
            </ul>

            <button
              onClick={handlePrintReceipt}
              className="w-full bg-pastel-green text-white py-2 rounded-lg hover:bg-pastel-green-dark transition duration-300"
            >
              Cetak Nota
            </button>

            <button
              onClick={handlePayment}
              className="w-full bg-pastel-blue text-white py-2 rounded-lg hover:bg-pastel-blue-dark transition duration-300 mt-2"
            >
              Bayar
            </button>
          </div>
        )}

        {showReceipt && (
          <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-xl mx-auto mt-10 bg-pastel-yellow">
            <h2 className="text-2xl font-bold mb-4 text-gray-700">Nota Transaksi</h2>
            <p className="font-bold text-gray-700">Wikusama Cafe</p>
            <p>Tanggal: {new Date().toLocaleDateString()}</p>
            <p>Nama Pelanggan: {customerName}</p>
            <p>Nomor Meja: {tableNumber}</p>
            <h3 className="text-xl font-semibold mb-2 text-gray-700">Daftar Pesanan:</h3>
            <ul className="list-disc pl-5 mb-4 text-gray-600">
              {orders.map((order, index) => (
                <li key={index}>
                  {order.menu} (x{order.quantity})
                </li>
              ))}
            </ul>
            <p className="text-lg font-semibold text-gray-700">Status: {status}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cashier;
