import React from 'react';
import { Link } from 'react-router-dom';
import 'tailwindcss/tailwind.css';

const Sidebar = ({ role, username }) => {
  return (
    <div className="bg-pink-300 w-64 h-full shadow-lg rounded-r-lg m-4"> {/* Menambahkan margin di sini */}
      <div className="p-4 border-b border-pink-400">
        <h3 className="text-xl font-semibold text-gray-800">Hi, {username || 'User'}</h3>
        <p className="text-gray-700">Role: {role || 'Guest'}</p>
      </div>
      <ul className="mt-4">
        {role === 'kasir' && (
          <>
            <li className="p-4 hover:bg-pink-400 transition duration-200 rounded">
              <Link to="/cashier" className="text-gray-800 font-semibold">Transaksi Kasir</Link>
            </li>
            <li className="p-4 hover:bg-pink-400 transition duration-200 rounded">
              <Link to="/transactions" className="text-gray-800 font-semibold">Daftar Transaksi</Link>
            </li>
          </>
        )}
        {role === 'manajer' && (
          <>
            <li className="p-4 hover:bg-pink-400 transition duration-200 rounded">
              <Link to="/manager" className="text-gray-800 font-semibold">Monitoring Transaksi</Link>
            </li>
          </>
        )}
        {role === 'admin' && (
          <>
            <li className="p-4 hover:bg-pink-400 transition duration-200 rounded">
              <Link to="/admin" className="text-gray-800 font-semibold">Pengelolaan Data User</Link>
            </li>
            <li className="p-4 hover:bg-pink-400 transition duration-200 rounded">
              <Link to="/menu" className="text-gray-800 font-semibold">Pengelolaan Menu</Link>
            </li>
            <li className="p-4 hover:bg-pink-400 transition duration-200 rounded">
              <Link to="/tables" className="text-gray-800 font-semibold">Pengelolaan Meja</Link>
            </li>
          </>
        )}
      </ul>
    </div>
  );
};

export default Sidebar;
