import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'tailwindcss/tailwind.css';


const Manager = () => {
    const [transactions, setTransactions] = useState([]);
    const [filteredTransactions, setFilteredTransactions] = useState([]);
    const [dateFilter, setDateFilter] = useState('');
    const [loading, setLoading] = useState(true);
    const [newTransaction, setNewTransaction] = useState({
        karyawan: '',
        date: '',
        jumlah: ''
    });

    // Function to authenticate manager (login)
    const handleLogin = async () => {
        // Implement login logic here
        try {
            const response = await axios.post('http://localhost:8000/login', { /* login data */ });
            console.log("Login successful:", response.data);
        } catch (error) {
            console.error("Login failed:", error);
        }
    };

    // Fetch transaction data
    const fetchTransactions = async () => {
        try {
            const response = await axios.get('http://localhost:8000/transaksi');
            if (Array.isArray(response.data)) {
                setTransactions(response.data);
                setFilteredTransactions(response.data);
            } else {
                console.error("Expected an array but received:", response.data);
            }
            setLoading(false);
        } catch (error) {
            console.error("Error fetching transactions:", error);
            setLoading(false);
        }
    };

    // Filter transactions by date
    const handleDateFilter = () => {
        if (dateFilter) {
            const filtered = transactions.filter(transaction => 
                new Date(transaction.date).toLocaleDateString() === new Date(dateFilter).toLocaleDateString()
            );
            setFilteredTransactions(filtered);
        } else {
            setFilteredTransactions(transactions);
        }
    };

    // Handle new transaction submission
    const handleTransactionSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8000/transaksi', newTransaction);
            console.log("Transaction added:", response.data);
            // Reset form
            setNewTransaction({ karyawan: '', date: '', jumlah: '' });
            fetchTransactions(); // Refresh the transaction list
        } catch (error) {
            console.error("Error adding transaction:", error);
        }
    };

    useEffect(() => {
        fetchTransactions();
    }, []);

    return (
        <div className="flex">
            {/* Sidebar */}
            <div className="w-1/4 bg-gray-800 text-white h-screen p-4">
                <h2 className="text-xl font-bold mb-4">Manager Menu</h2>
                <ul>
                    <li className="mb-2"><a href="#" className="hover:underline">Dashboard</a></li>
                    <li className="mb-2"><a href="#" className="hover:underline">Add Transaction</a></li>
                    <li className="mb-2"><a href="#" className="hover:underline">View Transactions</a></li>
                </ul>
            </div>

            {/* Main Content */}
            <div className="flex-1 p-4">
                <h1 className="text-3xl font-bold mb-4 text-center">Manager Dashboard</h1>
                <div className="text-center mb-6">
                    <button onClick={handleLogin} className="mb-4 p-2 bg-blue-500 text-white rounded hover:bg-blue-600">Login</button>
                </div>

                <div className="mb-4 flex justify-center">
                    <input
                        type="date"
                        value={dateFilter}
                        onChange={(e) => setDateFilter(e.target.value)}
                        className="border p-2 rounded mr-2"
                    />
                    <button onClick={handleDateFilter} className="p-2 bg-green-500 text-white rounded hover:bg-green-600">Filter</button>
                </div>

                <h2 className="text-2xl font-bold mb-4 text-center">Add New Transaction</h2>
                <form onSubmit={handleTransactionSubmit} className="mb-4">
                    <input
                        type="text"
                        placeholder="Karyawan"
                        value={newTransaction.karyawan}
                        onChange={(e) => setNewTransaction({ ...newTransaction, karyawan: e.target.value })}
                        required
                        className="border p-2 rounded mr-2"
                    />
                    <input
                        type="date"
                        value={newTransaction.date}
                        onChange={(e) => setNewTransaction({ ...newTransaction, date: e.target.value })}
                        required
                        className="border p-2 rounded mr-2"
                    />
                    <input
                        type="number"
                        placeholder="Jumlah"
                        value={newTransaction.jumlah}
                        onChange={(e) => setNewTransaction({ ...newTransaction, jumlah: e.target.value })}
                        required
                        className="border p-2 rounded mr-2"
                    />
                    <button type="submit" className="p-2 bg-blue-500 text-white rounded hover:bg-blue-600">Add Transaction</button>
                </form>

                {loading ? (
                    <p className="text-center">Loading transactions...</p>
                ) : (
                    <table className="min-w-full border mt-4">
                        <thead className="bg-gray-200">
                            <tr>
                                <th className="border p-2">ID</th>
                                <th className="border p-2">Karyawan</th>
                                <th className="border p-2">Tanggal</th>
                                <th className="border p-2">Jumlah</th>
                            </tr>
                        </thead>
                        <tbody>
                            {Array.isArray(filteredTransactions) && filteredTransactions.length > 0 ? (
                                filteredTransactions.map(transaction => (
                                    <tr key={transaction.id} className="hover:bg-gray-100">
                                        <td className="border p-2">{transaction.id}</td>
                                        <td className="border p-2">{transaction.karyawan}</td>
                                        <td className="border p-2">{transaction.date}</td>
                                        <td className="border p-2">{transaction.jumlah}</td>
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="4" className="border text-center p-4">No transactions found</td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default Manager;
