import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import 'tailwindcss/tailwind.css';

const Sidebar = () => {
    const [user, setUser] = useState(null); // State for storing user data
    const navigate = useNavigate();

    // Function to fetch user data
    const fetchUser = async () => {
        try {
            const response = await fetch('http://localhost:22077/user', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include', // Include credentials if token is stored in cookie
            });
            const data = await response.json();
            setUser(data.data); // Save user data in state
        } catch (error) {
            console.error('Failed to fetch user data', error);
        }
    };

    // Call fetchUser when component is rendered
    useEffect(() => {
        fetchUser();
    }, []);

    const handleCashier = () => {
        navigate('/cashier');
    };

    const handleViewAllTransactions = () => {
        navigate('/transactions');
    };

    return (
        <div className="bg-pink-300 w-64 h-full shadow-lg rounded-r-lg p-4">
            <img src="https://png.pngtree.com/png-clipart/20200224/original/pngtree-avatar-icon-profile-icon-member-login-vector-isolated-png-image_5247852.jpg" alt="Profile" className="w-20 h-20 rounded-full mx-auto" />
            <h3 className="text-xl font-semibold mb-2 text-center">{user?.username || 'User'}</h3>
            <p className="text-gray-700">Role: {user?.role || 'Guest'}</p>
            <p className="text-gray-700">Status: Online</p>
            <button onClick={handleCashier} className="mt-4 bg-pink-400 text-white rounded-lg px-4 py-2 w-full hover:bg-pink-500 transition duration-300">Add Transaction</button>
            <button onClick={handleViewAllTransactions} className="mt-4 bg-pink-400 text-white rounded-lg px-4 py-2 w-full hover:bg-pink-500 transition duration-300">View All Transactions</button>
        </div>
    );
};

export default Sidebar;
