import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Sidebar from './Sidebar';
import 'tailwindcss/tailwind.css';

const ViewTransaction = () => {
  const [transactions, setTransactions] = useState([]); // Initialize with empty array
  const [searchText, setSearchText] = useState('');
  const [searchDate, setSearchDate] = useState('');

  useEffect(() => {
    // Fetch transactions from API
    axios.get('http://localhost:8000/transaksi')
      .then(response => {
        console.log(response.data); // Check the data structure
        if (Array.isArray(response.data)) {
          setTransactions(response.data); // If response is already an array
        } else if (Array.isArray(response.data.transactions)) {
          setTransactions(response.data.transactions); // In case data is nested
        } else {
          console.error('Unexpected data structure:', response.data);
          setTransactions([]); // Set empty array if structure is unexpected
        }
      })
      .catch(error => {
        console.error('Error fetching transactions:', error);
        setTransactions([]); // Set empty array on error
      });
  }, []);

  const handleSearchText = (e) => {
    setSearchText(e.target.value);
  };

  const handleSearchDate = (e) => {
    setSearchDate(e.target.value);
  };

  // Filter transactions by searchText and optionally by searchDate
  const filteredTransactions = transactions.filter(transaction => {
    const matchesText = transaction.name.toLowerCase().includes(searchText.toLowerCase());
    const matchesDate = searchDate ? transaction.date.includes(searchDate) : true;
    return matchesText && matchesDate;
  });

  return (
    <div className="flex min-h-screen bg-pastelPink">
      <Sidebar />
      <div className="flex-grow p-8">
        <h1 className="text-4xl font-bold mb-6 text-pastelDarkRed">Transaction List</h1>

        {/* Search Bar */}
        <div className="flex mb-6 space-x-4">
          <input
            type="text"
            placeholder="Search by text"
            value={searchText}
            onChange={handleSearchText}
            className="px-4 py-2 border border-pastelRose rounded-lg focus:outline-none focus:ring-2 focus:ring-pastelRed"
          />
          <input
            type="date"
            placeholder="Search by date"
            value={searchDate}
            onChange={handleSearchDate}
            className="px-4 py-2 border border-pastelRose rounded-lg focus:outline-none focus:ring-2 focus:ring-pastelRed"
          />
        </div>

        {/* Transactions Table */}
        <table className="min-w-full bg-cream rounded-lg shadow-md">
          <thead>
            <tr className="text-left bg-pastelLightPink">
              <th className="py-2 px-4 text-pastelDarkRed">ID</th>
              <th className="py-2 px-4 text-pastelDarkRed">Name</th>
              <th className="py-2 px-4 text-pastelDarkRed">Date</th>
              <th className="py-2 px-4 text-pastelDarkRed">Amount</th>
            </tr>
          </thead>
          <tbody>
            {filteredTransactions.map(transaction => (
              <tr key={transaction.id} className="border-t border-pastelRose">
                <td className="py-2 px-4">{transaction.id}</td>
                <td className="py-2 px-4">{transaction.name}</td>
                <td className="py-2 px-4">{transaction.date}</td>
                <td className="py-2 px-4">{transaction.amount}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ViewTransaction;
