import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Register = () => {
    const [nama_user, setNamaUser] = useState('');
    const [role, setRole] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleRegister = async (e) => {
        e.preventDefault();

        if (password.length < 8) {
            setError('Password must be at least 8 characters long');
            return;
        }

        try {
    const response = await fetch('http://localhost:8000/user', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ nama_user, role, username, password }),
    });

    const data = await response.json();

    console.log(data); // Debugging untuk melihat respons dari server

    if (
      response.ok &&
      data.message === "Data user berhasil ditambahkan"
    ) {
      navigate("/login"); // Redirect ke halaman login setelah sukses
    } else if (data.message === "Username already exists") {
      setError("Username already exists");
    } else {
      setError("Registration failed");
    }
} catch (err) {
    console.error('Error during registration:', err);
    setError('Error registering user');
}

    };

    return (
      <div className="flex items-center justify-center min-h-screen bg-[#e0f7fa]">
        <div className="bg-[#b2ebf2] p-10 rounded-lg shadow-lg w-full max-w-lg font-poppins">
          <div className="flex items-center mb-6">
            {/* <img
              src={require("./img/logo.jpg")}
              alt="Claws Longue"
              className="w-16 h-16 mr-4" */}
            {/* /> */}
            <h2 className="text-[#01579b] text-3xl font-semibold">
              Kasir Twiska
            </h2>
          </div>
          <form onSubmit={handleRegister}>
            <div className="mb-4">
              <input
                type="text"
                className="w-full px-4 py-2 rounded-lg bg-[#e0f7fa] text-[#01579b] border border-[#b2ebf2] focus:outline-none focus:ring-2 focus:ring-[#0288d1]"
                placeholder="Enter your name"
                value={nama_user}
                onChange={(e) => setNamaUser(e.target.value)}
                required
              />
            </div>
            <div className="mb-4">
              <select
                className="w-full px-4 py-2 rounded-lg bg-[#e0f7fa] text-[#01579b] border border-[#b2ebf2] focus:outline-none focus:ring-2 focus:ring-[#0288d1]"
                value={role}
                onChange={(e) => setRole(e.target.value)}
                required
              >
                <option value="">Select Role</option>
                <option value="kasir">Kasir</option>
                <option value="manager">Manager</option>
                <option value="admin">Admin</option>
              </select>
            </div>
            <div className="mb-4">
              <input
                type="text"
                className="w-full px-4 py-2 rounded-lg bg-[#e0f7fa] text-[#01579b] border border-[#b2ebf2] focus:outline-none focus:ring-2 focus:ring-[#0288d1]"
                placeholder="Enter username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="mb-6">
              <input
                type="password"
                className="w-full px-4 py-2 rounded-lg bg-[#e0f7fa] text-[#01579b] border border-[#b2ebf2] focus:outline-none focus:ring-2 focus:ring-[#0288d1]"
                placeholder="Enter password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button
              type="submit"
              className="w-full py-1 rounded-lg text-white bg-[#0288d1] hover:bg-[#0277bd] transition-colors duration-300"
            >
              Register
            </button>
          </form>
          {error && <p className="text-red-500 text-center mt-4">{error}</p>}
          <p className="text-center mt-6 text-[#01579b]">
            Already have an account?{" "}
            <a href="/" className="underline">
              Login
            </a>
          </p>
        </div>
      </div>
    );
};

export default Register;
