import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';

const ManageUsers = () => {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/user', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setUsers(data.data || []);
            } else {
                console.error('Failed to fetch users');
            }
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const handleRoleChange = (userId, newRole) => {
        // Logic to change user role
        console.log(`Change role for user ${userId} to ${newRole}`);
        // TODO: Implement API call to change user role
    };

    return (
        <div className="flex-grow p-8 bg-white shadow-lg rounded-lg mx-6 mt-4 mb-4">
            <h1 className="text-4xl font-semibold text-pastelDarkRed mb-10 text-center">Manage Users</h1>
            <div className="overflow-x-auto shadow-lg rounded-lg mb-6">
                <table className="min-w-full bg-white rounded-lg shadow-lg">
                    <thead>
                        <tr className="bg-pastelRose text-white">
                            <th className="py-4 px-6 border-b">User ID</th>
                            <th className="py-4 px-6 border-b">Username</th>
                            <th className="py-4 px-6 border-b">Role</th>
                            <th className="py-4 px-6 border-b">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user, index) => (
                            <tr
                                key={user.id}
                                className={`${index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'} hover:bg-pastelRose transition duration-200`}
                            >
                                <td className="py-4 px-6 border-b">{user.id}</td>
                                <td className="py-4 px-6 border-b">{user.username}</td>
                                <td className="py-4 px-6 border-b">{user.role}</td>
                                <td className="py-4 px-6 border-b">
                                    <select
                                        className="border border-pastelRose rounded-lg p-2 bg-pastelLightPink text-pastelDarkRed"
                                        onChange={(e) => handleRoleChange(user.id, e.target.value)}
                                    >
                                        <option value="kasir">Kasir</option>
                                        <option value="manager">Manager</option>
                                        <option value="admin">Admin</option>
                                    </select>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default ManageUsers;
