import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManageTables = () => {
    const [tables, setTables] = useState([]);

    useEffect(() => {
        fetchTables();
    }, []);

    const fetchTables = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/meja', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setTables(data.data || []);
            } else {
                console.error('Failed to fetch tables');
            }
        } catch (error) {
            console.error('Error fetching tables:', error);
        }
    };

    const handleDeleteTable = async (tableId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8000/tables/${tableId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.ok) {
                // Refresh the table list after deletion
                fetchTables();
            } else {
                console.error('Failed to delete table');
            }
        } catch (error) {
            console.error('Error deleting table:', error);
        }
    };

    const handleViewDetails = (tableId) => {
        // Handle viewing table details here
        console.log(`Viewing details for table ${tableId}`);
        // You can navigate to a new page or open a modal to show more details
    };

    return (
        <>
            <Sidebar />
            <div className="flex flex-wrap p-8 bg-gray-100">
                <h1 className="w-full text-4xl font-semibold text-pastelDarkRed mb-10 text-center">Manage Tables</h1>
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 w-full">
                    {tables.map((table) => (
                        <div key={table.id} className="bg-white shadow-lg rounded-lg p-6">
                            <h2 className="text-xl font-bold text-gray-700 mb-2">Table {table.number}</h2>
                            <p className="text-gray-600 mb-4">Capacity: {table.capacity}</p>
                            <div className="flex justify-between items-center">
                                <button
                                    className="bg-blue-500 text-white rounded-lg px-4 py-2"
                                    onClick={() => handleViewDetails(table.id)}
                                >
                                    View Details
                                </button>
                                <button
                                    className="bg-red-500 text-white rounded-lg px-4 py-2"
                                    onClick={() => handleDeleteTable(table.id)}
                                >
                                    Delete
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
};

export default ManageTables;
