import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManageFoods = () => {
    const [foods, setFoods] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const [foodData, setFoodData] = useState({ id_menu: '', nama_menu: '', harga: '', deskripsi: '', gambar: '' });
    const [showForm, setShowForm] = useState(false);
    const [showDetails, setShowDetails] = useState(false);  // Untuk mengontrol modal
    const [selectedFood, setSelectedFood] = useState(null); // Menyimpan data makanan yang dipilih
  

    useEffect(() => {
        fetchFoods();
    }, []);

    const fetchFoods = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/menu', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setFoods(data.data || []);
            } else {
                console.error('Failed to fetch foods');
            }
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    const handleDeleteFood = async (foodId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8000/menu/${foodId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.ok) {
                fetchFoods();
            } else {
                console.error('Failed to delete food');
            }
        } catch (error) {
            console.error('Error deleting food:', error);
        }
    };

    const handleSaveFood = async () => {
        try {
            const token = localStorage.getItem('token');
            const method = isEditing ? 'PUT' : 'POST';
            const url = isEditing
                ? `http://localhost:8000/menu/${foodData.id_menu}`
                : 'http://localhost:8000/menu';

            const response = await fetch(url, {
                method: method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({
                    nama_menu: foodData.nama_menu,
                    harga: parseInt(foodData.harga),
                    deskripsi: foodData.deskripsi,
                    gambar: foodData.gambar,
                }),
            });

            if (response.ok) {
                setShowForm(false);
                setFoodData({ id_menu: '', nama_menu: '', harga: '', deskripsi: '', gambar: '' });
                fetchFoods();
            } else {
                console.error('Failed to save food');
            }
        } catch (error) {
            console.error('Error saving food:', error);
        }
    };

    const openFormToEdit = (food) => {
        setIsEditing(true);
        setFoodData(food);
        setShowForm(true);
    };

    const openFormToAdd = () => {
        setIsEditing(false);
        setFoodData({ id_menu: '', nama_menu: '', harga: '', deskripsi: '', gambar: '' });
        setShowForm(true);
    };

    const handleViewDetails = (food) => {
        // Contoh: Menampilkan modal atau alert dengan detail informasi makanan
        alert(`
          Food Name: ${food.nama_menu}
          Price: ${food.harga.toLocaleString('id-ID', {
            style: 'currency',
            currency: 'IDR',
          })}
          Description: ${food.deskripsi}
        `);
      
        // Atau, Anda dapat menampilkan modal atau mengarahkan ke halaman detail makanan
        // Misalnya, setShowDetailsModal(true) untuk menampilkan modal
      };

    
      // Fungsi untuk menutup modal
      const closeModal = () => {
        setShowDetails(false);
        setSelectedFood(null);
      };
    
      return (
        <>
          <Sidebar />
          <div className="mt-4">
            <div className="flex-grow p-8 bg-white shadow-lg rounded-lg mx-6 mt-4 mb-4">
              <h1 className="text-4xl font-semibold text-pastelDarkRed mb-10 text-center">
                Manage Foods
              </h1>
    
              <button
                className="bg-green-500 text-white rounded-lg px-4 py-2 mb-4"
                onClick={openFormToAdd}
              >
                Add Food
              </button>
    
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {foods.map((food) => (
                  <div key={food.id_menu} className="bg-white p-6 rounded-lg shadow-lg">
                    <img
                      src={food.gambar}
                      alt={food.nama_menu}
                      className="w-full h-40 object-cover rounded-md mb-4"
                    />
                    <h3 className="text-2xl font-semibold mb-2">{food.nama_menu}</h3>
                    <p className="text-gray-600 mb-2">
                      {food.harga.toLocaleString('id-ID', { style: 'currency', currency: 'IDR' })}
                    </p>
                    <button
                      className="bg-blue-500 text-white rounded-lg px-4 py-2"
                      onClick={() => handleViewDetails(food)}
                    >
                      View Details
                    </button>
                  </div>
                ))}
              </div>
            </div>
    
            {/* Modal untuk menampilkan detail makanan */}
            {showDetails && selectedFood && (
              <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
                <div className="bg-white p-6 rounded-lg shadow-lg max-w-3xl w-full">
                  <div className="flex">
                    {/* Gambar di sebelah kiri */}
                    <img
                      src={selectedFood.gambar}
                      alt={selectedFood.nama_menu}
                      className="w-1/3 h-auto object-cover rounded-lg"
                    />
    
                    {/* Deskripsi di sebelah kanan */}
                    <div className="ml-6 w-2/3">
                      <h2 className="text-3xl font-semibold mb-4">{selectedFood.nama_menu}</h2>
                      <p className="text-xl text-gray-700 mb-2">
                        Price: {selectedFood.harga.toLocaleString('id-ID', { style: 'currency', currency: 'IDR' })}
                      </p>
                      <p className="text-lg text-gray-700 mb-4">{selectedFood.deskripsi}</p>
    
                      <div className="flex justify-end">
                        <button
                          className="bg-red-500 text-white rounded-lg px-4 py-2"
                          onClick={closeModal}
                        >
                          Close
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </>
      );
    };
    
    export default ManageFoods;
