import React, { useState, useEffect, useRef } from 'react';
import 'tailwindcss/tailwind.css';
import ReactToPrint from 'react-to-print';
import Receipt from './Receipt';
import { jsPDF } from "jspdf";
import Sidebar from '../page/Sidebar'

const CashierPage = ({ user }) => {
    const [menuItems, setMenuItems] = useState([]);
    const [tables, setTables] = useState([]);
    const [selectedMenu, setSelectedMenu] = useState([]);
    const [selectedMenuItem, setSelectedMenuItem] = useState(null);
    const [isTableOccupied, setIsTableOccupied] = useState(false);
    const [customerName, setCustomerName] = useState('');
    const [tableNumber, setTableNumber] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [transactionReceipt, setTransactionReceipt] = useState(null);
    const [isPaid, setIsPaid] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const componentRef = useRef(); 

    useEffect(() => {
        fetchMenuItems();
        fetchTableNumbers();
    }, []);

    const fetchMenuItems = async () => {
        try {
            const response = await fetch(`http://localhost:8000/menu`);
            const data = await response.json();
            setMenuItems(data.data || []);
        } catch (error) {
            console.error('Error fetching menu items:', error);
        }
    };

    const fetchTableNumbers = async () => {
        try {
            const response = await fetch('http://localhost:8000/meja');
            const data = await response.json();
            setTables(data.data || []);
        } catch (error) {
            console.error('Error fetching table numbers:', error);
        }
    };

    const handleTableCheck = (selectedTable) => {
        const table = tables.find((t) => t.nomor_meja === selectedTable);
        const occupied = table ? table.status === 'terisi' : false;
        setIsTableOccupied(occupied);
        setTableNumber(selectedTable);
    };
    
    const handleAddMenu = () => {
        if (!selectedMenuItem || quantity < 1) {
            setErrorMessage('Silakan pilih menu dan isi kuantitas terlebih dahulu.');
            return;
        }

        const existingMenu = selectedMenu.find(item => item.id_menu === selectedMenuItem.id_menu);
        if (existingMenu) {
            existingMenu.quantity += quantity; // Tambah kuantitas jika menu sudah ada
        } else {
            const newMenu = {
                ...selectedMenuItem,
                quantity: quantity,
            };
            setSelectedMenu((prev) => [...prev, newMenu]); // Tambahkan menu baru
        }

        setQuantity(1); // Reset kuantitas
        setErrorMessage('');
    };

    const handleMenuChange = (e) => {
        const selectedId = e.target.value;
        const menuItem = menuItems.find(item => item.id_menu === parseInt(selectedId));
        setSelectedMenuItem(menuItem || null); // Set selected menu item
    };

    const handleQuantityChange = (type) => {
        if (type === 'increment') {
            setQuantity((prevQuantity) => prevQuantity + 1);
        } else if (type === 'decrement' && quantity > 1) {
            setQuantity((prevQuantity) => prevQuantity - 1);
        }
    };

    const handleSubmitTransaction = async () => {
        if (!customerName || !tableNumber) {
            setErrorMessage('Nama pelanggan dan nomor meja harus diisi!');
            return;
        }

        if (selectedMenu.length === 0) {
            setErrorMessage('Silakan tambahkan setidaknya satu menu sebelum submit!');
            return;
        }

        const newTransaction = {
            tgl_transaksi: new Date().toISOString(),
            id_user: localStorage.getItem("id_user"),
            id_meja: tableNumber, // Misal tableNumber dari state atau props
            nama_pelanggan: customerName, // Nama pelanggan dari input field
            status: isPaid ? 'lunas' : 'belum_bayar', // Cek jika sudah dibayar, statusnya 'lunas', kalau tidak 'belum_bayar'
            detail_transaksi: selectedMenu.map(item => ({
                id_menu: item.id_menu,
                quantity: item.quantity,
            })),
        };

        try {
            let token = localStorage.getItem('token')
            const response = await fetch('http://localhost:8000/transaksi', {
                method: 'POST',
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                 },
                body: JSON.stringify(newTransaction),
            });

            if (response.ok) {
                alert('Transaksi berhasil ditambahkan!');

                // Update table status to "terisi"
                await updateTableStatus(tableNumber, 'terisi');
                let result= await response.json()
                let receipt = {...newTransaction, id_transaksi: result.insertTransaksi.id_transaksi}
                setTransactionReceipt(receipt);
                setSelectedMenu([]);
                setCustomerName('');
                setTableNumber('');
            } else {
                const errorText = await response.text(); // Ambil teks dari respon
                console.error('Failed to add transaction:', errorText); // Tampilkan pesan kesalahan di console
                setErrorMessage('Gagal menambahkan transaksi. Coba lagi!');
            }
        } catch (error) {
            console.error('Error submitting transaction:', error);
        }
    };

    const updateTableStatus = async (tableNumber, status) => {
        try {
            let token = localStorage.getItem('token')
            const response = await fetch(`http://localhost:8000/meja/${tableNumber}`, {
                method: 'PUT',
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization' : `Bearer ${token}`
                 },
                body: JSON.stringify({ status }),
            });

            if (!response.ok) {
                throw new Error('Failed to update table status');
            }
        } catch (error) {
            console.error('Error updating table status:', error);
        }
    };

    const handlePayment = async () => {
        const updatedReceipt = {
            ...transactionReceipt,
            status: "lunas"
        };

        try {
            let token = localStorage.getItem('token')
            const response = await fetch(`http://localhost:8000/transaksi/${transactionReceipt.id_transaksi}`, {
                method: 'PUT',
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                 },
                body: JSON.stringify(updatedReceipt),
            
            });

            if (response.ok) {
                alert('Pembayaran berhasil!');
                setIsPaid(true);
                setTransactionReceipt(updatedReceipt);
            }
        } catch (error) {
            console.error('Error processing payment:', error);
        }
    };

    const handleDone = () => {
        setSelectedMenu([]);
        setTransactionReceipt(null);
        setIsPaid(false);
        window.location.reload();
    };

    const calculateReceiptTotalHarga = () => {
        if (!transactionReceipt) return 0;

        return transactionReceipt.detail_transaksi.reduce((total, item) => {
            const menu = menuItems.find(menuItem => menuItem.id_menu === item.id_menu);
            return total + (menu ? menu.harga * item.quantity : 0);
        }, 0);
    };

    const handlePrint = useRef();

    const handleDownloadReceipt = () => {
        const doc = new jsPDF();
        doc.text("Receipt", 20, 20);
        doc.text(`Customer Name: ${transactionReceipt.nama_pelanggan}`, 20, 30);
        doc.text(`Table: ${transactionReceipt.id_meja}`, 20, 40);
        doc.text(`Date: ${new Date().toLocaleDateString()}`, 20, 50);
        
        transactionReceipt.detail_transaksi.forEach((item, index) => {
            const menuItem = menuItems.find(menu => menu.id_menu === item.id_menu);
            const totalHarga = menuItem?.harga * item.quantity;
            doc.text(`${menuItem?.nama_menu} x ${item.quantity} = Rp ${totalHarga.toLocaleString()}`, 20, 60 + (10 * index));
        });

        doc.text(`Total: Rp ${calculateReceiptTotalHarga().toLocaleString()}`, 20, 60 + (10 * transactionReceipt.detail_transaksi.length));
        doc.save(`Receipt_${transactionReceipt.id_transaksi}.pdf`);
    };
    // const formatDate = (date) => {
    //     return new Date(date).toLocaleString();
    // };

    return (
        <div className="flex min-h-screen bg- bg-pastelLightPink">
            <Sidebar user={user} /> {/* Render Sidebar */}

            <div className="flex-grow p-4">
                <h1 className="text-3xl font-bold text-white mb-6 text-center">Tambah Transaksi</h1>
                <form onSubmit={(e) => { e.preventDefault(); }} className="bg-white rounded-lg shadow-lg p-6">
                    <div className="mb-4">
                        <label htmlFor="customerName" className="block text-gray-700">Nama Pelanggan</label>
                        <input
                            type="text"
                            className="mt-1 block w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#A6E3E9]"
                            id="customerName"
                            value={customerName}
                            onChange={(e) => setCustomerName(e.target.value)}
                            placeholder="Masukkan nama pelanggan"
                            required
                        />
                    </div>

                    <div className="mb-4">
                        <label htmlFor="tableNumber" className="block text-gray-700">Nomor Meja</label>
                        <select
                            className="mt-1 block w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#A6E3E9]"
                            id="tableNumber"
                            value={tableNumber}
                            onChange={(e) => handleTableCheck(e.target.value)}
                            required
                        >
                            <option value="">Pilih meja</option>
                            {tables.map((table) => (
                                <option key={table.id_meja} value={table.id_meja}>{table.nomor_meja}</option>
                            ))}
                        </select>
                        {isTableOccupied && <p className="text-red-500">Meja sudah terisi!</p>}
                    </div>

                    <div className="mb-4">
                        <label htmlFor="menu" className="block text-gray-700">Menu</label>
                        <select
                            className="mt-1 block w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#A6E3E9]"
                            id="menu"
                            value={selectedMenuItem ? selectedMenuItem.id_menu : ''}
                            onChange={handleMenuChange}
                        >
                            <option value="">Pilih menu</option>
                            {menuItems.map((item) => (
                                <option key={item.id_menu} value={item.id_menu}>{item.nama_menu}</option>
                            ))}
                        </select>
                    </div>

                    <div className="mb-4">
                        <label htmlFor="quantity" className="block text-gray-700">Kuantitas</label>
                        <div className="flex items-center">
                            <button
                                type="button"
                                className="bg-pastelRose text-white px-3 py-1 rounded-l-lg"
                                onClick={() => handleQuantityChange('decrement')}
                            >-</button>
                            <input
                                type="number"
                                className="w-16 text-center border border-gray-300 rounded-none"
                                id="quantity"
                                value={quantity}
                                onChange={(e) => setQuantity(parseInt(e.target.value))}
                                min="1"
                            />
                            <button
                                type="button"
                                className="bg-pastelRose text-white px-3 py-1 rounded-r-lg"
                                onClick={() => handleQuantityChange('increment')}
                            >+</button>
                        </div>
                    </div>

                    <button
                        type="button"
                        className="bg-pastelRose text-white px-4 py-2 rounded-lg hover:bg-[#697565]" // Added margin-bottom
                        onClick={handleAddMenu}
                    >Tambah Menu</button>

                    <button
                        type="button"
                        className="bg-pastelRose text-white px-4 py-2 rounded-lg mt-8 ml-4 hover:bg-[#697565]" // Added margin-top
                        onClick={handleSubmitTransaction}
                    >Submit Transaksi</button>

                    {selectedMenu.length > 0 && (
                        <div className="mt-6">
                            <h2 className="text-xl font-semibold text-gray-700">Daftar Pesanan</h2>
                            <ul>
                                {selectedMenu.map((item, index) => (
                                    <li key={index} className="mt-2 text-gray-700">
                                        {item.nama_menu} x {item.quantity}
                                    </li>
                                ))}
                            </ul>
                        </div>
                    )}

                    {errorMessage && <p className="text-red-500 mt-4">{errorMessage}</p>}

                </form>

                {transactionReceipt && (
                    <div className="bg-white rounded-lg shadow-lg p-6 mt-7">
                        <div className="flex justify-between items-center mb-4">
                            <div className="flex items-center space-x-4">
                                <img src={require('../assets/clogo.jpg')} alt="CafeLogo" className="w-16 h-16 rounded-full border-2 border-pastelRose" />
                                <div>
                                    <h2 className="text-2xl font-bold text-pastelRose">Belle Fiori</h2>
                                    <p className="text-gray-500">Jl. Eliot No. 10, Malang</p>
                                </div>
                            </div>
                            <p className="text-gray-500 text-lg">{new Date().toLocaleDateString()}</p>
                        </div>

                        <div className="border-t border-b border-gray-300 py-4">
                            <div className="flex mb-2">
                                <p className="font-semibold">Customer Name:</p>
                                <p className="ml-2">{transactionReceipt.nama_pelanggan}</p>
                            </div>
                            <div className="flex mb-2">
                                <p className="font-semibold">Table:</p>
                                <p className="ml-2">{transactionReceipt.id_meja}</p>
                            </div>
                        </div>

                        <h3 className="text-xl font-semibold my-4">Order List:</h3>
                        <table className="min-w-full border border-gray-300">
                            <thead>
                                <tr>
                                    <th className="py-2 px-4 border-b border-gray-300">Menu</th>
                                    <th className="py-2 px-4 border-b border-gray-300">Quantity</th>
                                    <th className="py-2 px-4 border-b border-gray-300">Price</th>
                                </tr>
                            </thead>
                            <tbody>
                                {transactionReceipt.detail_transaksi.map((item, index) => {
                                    const menuItem = menuItems.find(menu => menu.id_menu === item.id_menu);
                                    const totalHarga = menuItem?.harga * item.quantity;
                                    return (
                                        <tr key={index} className="border-b border-gray-300">
                                            <td className="py-2 px-4">{menuItem?.nama_menu}</td>
                                            <td className="py-2 px-4">{item.quantity}</td>
                                            <td className="py-2 px-4">Rp {totalHarga.toLocaleString()}</td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>

                        <div className="border-t border-gray-300 pt-4">
                            <div className="flex justify-between mb-2">
                                <p className="font-semibold">Total:</p>
                                <p className="font-bold">Rp {calculateReceiptTotalHarga().toLocaleString()}</p>
                            </div>
                        </div>

                        
                        <div className="flex justify-between mb-4">
                            <button
                                type="button"
                                className="bg-pastelRose text-white px-4 py-2 rounded-lg hover:bg-[#697565] transition duration-300 ease-in-out"
                                onClick={handleDownloadReceipt}
                            >
                                Download Receipt
                            </button>

                            <ReactToPrint
                                trigger={() => <button className="bg-pastelRose text-white px-4 py-2 rounded-lg hover:bg-[#697565]">Print Receipt</button>}
                                content={() => componentRef.current}
                            />
                        </div>

                        <div style={{ display: "none" }}>
                            <div ref={componentRef}>
                                {/* Here you can render the receipt content for printing */}
                                <h2>Receipt</h2>
                                <p>Customer Name: {transactionReceipt.nama_pelanggan}</p>
                                <p>Table: {transactionReceipt.id_meja}</p>
                                <p>Date: {new Date().toLocaleDateString()}</p>
                                <table>
                                    <thead>
                                        <tr>
                                            <th>Menu</th>
                                            <th>Quantity</th>
                                            <th>Price</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {transactionReceipt.detail_transaksi.map((item, index) => {
                                            const menuItem = menuItems.find(menu => menu.id_menu === item.id_menu);
                                            const totalHarga = menuItem?.harga * item.quantity;
                                            return (
                                                <tr key={index}>
                                                    <td>{menuItem?.nama_menu}</td>
                                                    <td>{item.quantity}</td>
                                                    <td>Rp {totalHarga.toLocaleString()}</td>
                                                </tr>
                                            );
                                        })}
                                    </tbody>
                                </table>
                                <p>Total: Rp {calculateReceiptTotalHarga().toLocaleString()}</p>
                            </div>
                        </div>


                        {console.log('detail_transaksi', transactionReceipt)}

                        {isPaid ? (
                            <p className="mt-4 text-green-500 font-semibold">Status: Lunas</p>
                        ) : (
                            <button
                                type="button"
                                className="bg-pastelRose text-white px-4 py-2 rounded-lg mt-4 hover:bg-[#697565] transition duration-300 ease-in-out"
                                onClick={handlePayment}
                            >
                                Bayar
                            </button>


                        )}

                        {isPaid && (
                            <button
                                type="button"
                                className="bg-[#8b5742] text-white px-4 py-2 rounded-lg mt-4 hover:bg-[#697565] transition duration-300 ease-in-out"
                                onClick={handleDone}
                            >
                                Selesai
                            </button>
                        )}

                        <p className="text-center text-gray-500 mt-4 italic">Thank you so much for visiting Belle Fiori Caf! We hope you had a lovely time and look forward to welcoming you back soon</p>
                    </div>
                )}

            </div>
        </div>
    );
}

export default CashierPage;
