import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar'; // Assuming Sidebar is already created

const AdminPage = ({ user }) => {
    const [users, setUsers] = useState([]);
    const [foods, setFoods] = useState([]);
    const [tables, setTables] = useState([]);
    const [selectedRole, setSelectedRole] = useState('');
    const [newFood, setNewFood] = useState({ name: '', price: '' });
    const [newTable, setNewTable] = useState({ number: '' });

    useEffect(() => {
        fetchUsers();
        fetchFoods();
        fetchTables();
    }, []);

    // Fetching data from API (Users, Foods, Tables)
    const fetchUsers = async () => {
        const response = await fetch('http://localhost:8000/users');
        const data = await response.json();
        setUsers(data.data);
    };

    const fetchFoods = async () => {
        const response = await fetch('http://localhost:8000/menu');
        const data = await response.json();
        setFoods(data.data);
    };

    const fetchTables = async () => {
        const response = await fetch('http://localhost:8000/tables');
        const data = await response.json();
        setTables(data.data);
    };

    // Functions to handle adding new food, table, and updating user role
    const handleAddFood = () => {
        // Add food logic
    };

    const handleAddTable = () => {
        // Add table logic
    };

    const handleRoleChange = (userId, newRole) => {
        // Update user role logic
    };

    return (
        <div className="flex min-h-screen bg-pastelPink">
            <Sidebar user={user} />

            <div className="flex-grow p-8 bg-white shadow-lg rounded-lg mx-6 mt-4 mb-4">
                <h1 className="text-4xl font-semibold text-pastelDarkRed mb-10 text-center">
                    Admin Dashboard
                </h1>

                {/* User Management */}
                <div className="mb-8">
                    <h2 className="text-2xl text-pastelDarkRed font-semibold mb-4">Manage Users</h2>
                    <table className="min-w-full bg-white rounded-lg shadow-lg mb-6">
                        <thead>
                            <tr className="bg-pastelRose text-white">
                                <th className="py-4 px-6 border-b">Username</th>
                                <th className="py-4 px-6 border-b">Email</th>
                                <th className="py-4 px-6 border-b">Role</th>
                                <th className="py-4 px-6 border-b">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map((user, index) => (
                                <tr key={user.id} className={`${index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'} hover:bg-pastelRose transition duration-200`}>
                                    <td className="py-4 px-6 border-b">{user.username}</td>
                                    <td className="py-4 px-6 border-b">{user.email}</td>
                                    <td className="py-4 px-6 border-b">
                                        <select
                                            value={user.role}
                                            onChange={(e) => handleRoleChange(user.id, e.target.value)}
                                            className="border border-pastelRose rounded-lg p-2 bg-pastelLightPink text-pastelDarkRed"
                                        >
                                            <option value="admin">Admin</option>
                                            <option value="manager">Manager</option>
                                            <option value="cashier">Cashier</option>
                                        </select>
                                    </td>
                                    <td className="py-4 px-6 border-b">
                                        <button className="bg-pastelRose text-white px-4 py-2 rounded-lg shadow-lg hover:bg-pastelRed transition duration-300 transform hover:scale-105">
                                            Update
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                {/* Food Management */}
                <div className="mb-8">
                    <h2 className="text-2xl text-pastelDarkRed font-semibold mb-4">Manage Foods</h2>
                    <div className="flex space-x-4 mb-4">
                        <input
                            type="text"
                            placeholder="Food Name"
                            value={newFood.name}
                            onChange={(e) => setNewFood({ ...newFood, name: e.target.value })}
                            className="border border-pastelRose rounded-lg p-2 bg-pastelLightPink text-pastelDarkRed"
                        />
                        <input
                            type="number"
                            placeholder="Price"
                            value={newFood.price}
                            onChange={(e) => setNewFood({ ...newFood, price: e.target.value })}
                            className="border border-pastelRose rounded-lg p-2 bg-pastelLightPink text-pastelDarkRed"
                        />
                        <button
                            onClick={handleAddFood}
                            className="bg-pastelRose text-white px-6 py-2 rounded-lg shadow-lg hover:bg-pastelRed transition duration-300 transform hover:scale-105"
                        >
                            Add Food
                        </button>
                    </div>

                    <table className="min-w-full bg-white rounded-lg shadow-lg">
                        <thead>
                            <tr className="bg-pastelRose text-white">
                                <th className="py-4 px-6 border-b">Food Name</th>
                                <th className="py-4 px-6 border-b">Price</th>
                            </tr>
                        </thead>
                        <tbody>
                            {foods.map((food, index) => (
                                <tr key={food.id} className={`${index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'} hover:bg-pastelRose transition duration-200`}>
                                    <td className="py-4 px-6 border-b">{food.name}</td>
                                    <td className="py-4 px-6 border-b">Rp {food.price.toLocaleString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                {/* Table Management */}
                <div>
                    <h2 className="text-2xl text-pastelDarkRed font-semibold mb-4">Manage Tables</h2>
                    <div className="flex space-x-4 mb-4">
                        <input
                            type="text"
                            placeholder="Table Number"
                            value={newTable.number}
                            onChange={(e) => setNewTable({ ...newTable, number: e.target.value })}
                            className="border border-pastelRose rounded-lg p-2 bg-pastelLightPink text-pastelDarkRed"
                        />
                        <button
                            onClick={handleAddTable}
                            className="bg-pastelRose text-white px-6 py-2 rounded-lg shadow-lg hover:bg-pastelRed transition duration-300 transform hover:scale-105"
                        >
                            Add Table
                        </button>
                    </div>

                    <table className="min-w-full bg-white rounded-lg shadow-lg">
                        <thead>
                            <tr className="bg-pastelRose text-white">
                                <th className="py-4 px-6 border-b">Table Number</th>
                            </tr>
                        </thead>
                        <tbody>
                            {tables.map((table, index) => (
                                <tr key={table.id} className={`${index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'} hover:bg-pastelRose transition duration-200`}>
                                    <td className="py-4 px-6 border-b">{table.number}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default AdminPage;
