import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import 'tailwindcss/tailwind.css';
import Sidebar from './Sidebar'; // Import Sidebar
import CafeLogo from '../assets/logo.jpg';

const Cashier = ({ user }) => {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const [tables, setTables] = useState([]);
    const [selectedMenu, setSelectedMenu] = useState([]);
    const [selectedMenuItem, setSelectedMenuItem] = useState(null);
    const [isTableOccupied, setIsTableOccupied] = useState(false);
    const [customerName, setCustomerName] = useState('');
    const [tableNumber, setTableNumber] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [transactionReceipt, setTransactionReceipt] = useState(null);
    const [isPaid, setIsPaid] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchMenuItems();
        fetchTableNumbers();
    }, []);

    // Fetch functions, handleSubmitTransaction, etc.

    const renderReceipt = () => (
        <div className="mt-6 p-6 bg-white rounded-lg shadow-lg">
            <h2 className="text-2xl font-bold text-[#b7122d] text-center mb-4">Belle Fiori</h2>
            <div className="flex justify-center mb-4">
                <img src={CafeLogo} alt="Cafe Logo" className="w-24 h-24" /> {/* Replace with actual logo */}
            </div>
            <p className="text-center text-gray-700 mb-4">114 Elliot, Riverdale, Jakarta</p>
            <p className="text-center text-gray-700 mb-2">Date: {formatDate(transactionReceipt.tgl_transaksi)}</p>
            <p className="text-center text-gray-700 mb-4">Customer Name: {transactionReceipt.nama_pelanggan}</p>
            
            <table className="w-full mb-4">
                <thead>
                    <tr className="text-left">
                        <th className="border-b-2 border-gray-300 py-2">Menu</th>
                        <th className="border-b-2 border-gray-300 py-2">Quantity</th>
                        <th className="border-b-2 border-gray-300 py-2">Price</th>
                    </tr>
                </thead>
                <tbody>
                    {transactionReceipt.detail_transaksi.map((item, index) => {
                        const menu = menuItems.find(menuItem => menuItem.id_menu === item.id_menu);
                        return (
                            <tr key={index}>
                                <td className="py-2">{menu?.nama_menu}</td>
                                <td className="py-2">{item.quantity}</td>
                                <td className="py-2">{menu?.harga} IDR</td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>

            <div className="flex justify-between font-bold text-[#b7122d]">
                <span>Subtotal:</span>
                <span>{calculateReceiptTotalHarga()} IDR</span>
            </div>

            <div className="text-center mt-4">
                <p>Status: <span className={`font-bold ${isPaid ? 'text-green-500' : 'text-red-500'}`}>{isPaid ? 'Lunas' : 'Belum Bayar'}</span></p>
            </div>

            <div className="mt-6 text-center">
                <button
                    type="button"
                    onClick={handleDone}
                    className="bg-[#b7122d] text-white py-2 px-4 rounded-lg hover:bg-[#c44c5e] focus:outline-none focus:ring-2 focus:ring-[#b7122d]"
                >
                    Selesai
                </button>
            </div>
        </div>
    );

    return (
        <div className="flex min-h-screen">
            <Sidebar user={user} />

            <div className="flex-1 p-6 bg-gradient-to-r from-[#fee3e2] to-[#f5a1ac]">
                <h1 className="text-3xl font-bold text-[#b7122d] mb-6">Halaman Kasir</h1>

                <form className="bg-white p-6 rounded-lg shadow-lg">
                    <div className="mb-4">
                        <label className="block text-gray-700">Nama Pelanggan:</label>
                        <input
                            type="text"
                            value={customerName}
                            onChange={(e) => setCustomerName(e.target.value)}
                            className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-[#b7122d]"
                        />
                    </div>

                    <div className="mb-4">
                        <label className="block text-gray-700">Nomor Meja:</label>
                        <select
                            value={tableNumber}
                            onChange={(e) => handleTableCheck(e.target.value)}
                            className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-[#b7122d]"
                        >
                            <option value="">Pilih Meja</option>
                            {tables.map((table) => (
                                <option key={table.nomor_meja} value={table.nomor_meja}>
                                    {table.nomor_meja} - {table.status}
                                </option>
                            ))}
                        </select>
                    </div>

                    {isTableOccupied && (
                        <p className="text-red-500 mb-4">Meja ini sedang terisi. Silakan pilih meja lain.</p>
                    )}

                    <div className="mb-4">
                        <label className="block text-gray-700">Pilih Menu:</label>
                        <select
                            value={selectedMenuItem ? selectedMenuItem.id_menu : ''}
                            onChange={handleMenuChange}
                            className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-[#b7122d]"
                        >
                            <option value="">Pilih Menu</option>
                            {menuItems.map((item) => (
                                <option key={item.id_menu} value={item.id_menu}>
                                    {item.nama_menu} - {item.harga} IDR
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="mb-4 flex items-center space-x-4">
                        <label className="block text-gray-700">Kuantitas:</label>
                        <button
                            type="button"
                            onClick={() => handleQuantityChange('decrement')}
                            className="px-3 py-1 bg-[#da0328] text-white rounded-lg"
                        >
                            -
                        </button>
                        <span className="px-3">{quantity}</span>
                        <button
                            type="button"
                            onClick={() => handleQuantityChange('increment')}
                            className="px-3 py-1 bg-[#da0328] text-white rounded-lg"
                        >
                            +
                        </button>
                    </div>

                    <button
                        type="button"
                        onClick={handleAddMenu}
                        className="w-full bg-[#c44c5e] text-white py-2 px-4 rounded-lg hover:bg-[#da0328] focus:outline-none focus:ring-2 focus:ring-[#da0328]"
                    >
                        Tambahkan Menu
                    </button>

                    {errorMessage && <p className="mt-2 text-red-500">{errorMessage}</p>}

                    <div className="mt-6">
                        <h2 className="text-xl font-bold text-[#b7122d]">Menu yang dipilih:</h2>
                        {selectedMenu.length === 0 ? (
                            <p className="text-gray-500">Belum ada menu yang dipilih.</p>
                        ) : (
                            <ul className="mt-2">
                                {selectedMenu.map((item, index) => (
                                    <li key={index} className="mb-2">
                                        {item.nama_menu} - {item.quantity} pcs
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>

                    <div className="flex justify-between mt-6">
                        <button
                            type="button"
                            onClick={handleSubmitTransaction}
                            className="bg-[#b7122d] text-white py-2 px-4 rounded-lg hover:bg-[#c44c5e] focus:outline-none focus:ring-2 focus:ring-[#b7122d]"
                        >
                            Submit Transaksi
                        </button>
                        <button
                            type="button"
                            onClick={handlePayment}
                            className="bg-[#b7122d] text-white py-2 px-4 rounded-lg hover:bg-[#c44c5e] focus:outline-none focus:ring-2 focus:ring-[#b7122d]"
                        >
                            Bayar
                        </button>
                        <button
                            type="button"
                            onClick={() => setTransactionReceipt(transactionReceipt)}
                            className="bg-[#b7122d] text-white py-2 px-4 rounded-lg hover:bg-[#c44c5e] focus:outline-none focus:ring-2 focus:ring-[#b7122d]"
                        >
                            Cetak Nota
                        </button>
                    </div>
                </form>

                {transactionReceipt && renderReceipt()}

            </div>
        </div>
    );
};

export default Cashier;
