import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManageFoods = () => {
    const [foods, setFoods] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const [foodData, setFoodData] = useState({ id_menu: '', nama_menu: '', harga: '', deskripsi: '', gambar: '' });
    const [showForm, setShowForm] = useState(false);
    const [showModal, setShowModal] = useState(false);
    const [currentMenu, setCurrentMenu] = useState(null);
    const [selectedMenu, setSelectedMenu] = useState([]); // State untuk selectedMenu
  
    useEffect(() => {
        fetchFoods();
    }, []);

    const fetchFoods = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/menu', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setFoods(data.data || []);
            } else {
                console.error('Failed to fetch foods');
            }
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    const handleDeleteFood = async (foodId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8000/menu/${foodId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.ok) {
                fetchFoods();
            } else {
                console.error('Failed to delete food');
            }
        } catch (error) {
            console.error('Error deleting food:', error);
        }
    };

    const handleSaveFood = async () => {
        try {
            const token = localStorage.getItem('token');
            const method = isEditing ? 'PUT' : 'POST';
            const url = isEditing
                ? `http://localhost:8000/menu/${foodData.id_menu}`
                : 'http://localhost:8000/menu';

            const response = await fetch(url, {
                method: method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({
                    nama_menu: foodData.nama_menu,
                    harga: parseInt(foodData.harga),
                    deskripsi: foodData.deskripsi,
                    gambar: foodData.gambar,
                }),
            });

            if (response.ok) {
                setShowForm(false);
                setFoodData({ id_menu: '', nama_menu: '', harga: '', deskripsi: '', gambar: '' });
                fetchFoods();
            } else {
                console.error('Failed to save food');
            }
        } catch (error) {
            console.error('Error saving food:', error);
        }
    };

    const openFormToEdit = (food) => {
        setIsEditing(true);
        setFoodData(food);
        setShowForm(true);
    };

    const openFormToAdd = () => {
        setIsEditing(false);
        setFoodData({ id_menu: '', nama_menu: '', harga: '', deskripsi: '', gambar: '' });
        setShowForm(true);
    };

    const openModal = (menu) => {
        setCurrentMenu(menu);
        setShowModal(true);
      };
    
      const closeModal = () => {
        setShowModal(false);
        setCurrentMenu(null);
      };
    
      const handleIncrement = (id_menu) => {
        setSelectedMenu((prevSelectedMenu) => {
          const foundMenu = prevSelectedMenu.find(item => item.id_menu === id_menu);
          if (foundMenu) {
            return prevSelectedMenu.map(item =>
              item.id_menu === id_menu ? { ...item, quantity: item.quantity + 1 } : item
            );
          } else {
            return [...prevSelectedMenu, { id_menu, quantity: 1 }];
          }
        });
      };
    
      const handleDecrement = (id_menu) => {
        setSelectedMenu((prevSelectedMenu) => {
          const foundMenu = prevSelectedMenu.find(item => item.id_menu === id_menu);
          if (foundMenu && foundMenu.quantity > 1) {
            return prevSelectedMenu.map(item =>
              item.id_menu === id_menu ? { ...item, quantity: item.quantity - 1 } : item
            );
          } else {
            return prevSelectedMenu.filter(item => item.id_menu !== id_menu);
          }
        });
      };

    const handleViewDetails = (food) => {
        // Contoh: Menampilkan modal atau alert dengan detail informasi makanan
        alert(`
          Food Name: ${food.nama_menu}
          Price: ${food.harga.toLocaleString('id-ID', {
            style: 'currency',
            currency: 'IDR',
          })}
          Description: ${food.deskripsi}
        `);
      
        // Atau, Anda dapat menampilkan modal atau mengarahkan ke halaman detail makanan
        // Misalnya, setShowDetailsModal(true) untuk menampilkan modal
      };
      

      return (
        <>
          <Sidebar />
          <div className="mt-4">
            <div className="flex-grow p-8 bg-white shadow-lg rounded-lg mx-6 mt-4 mb-4">
              <h1 className="text-4xl font-semibold text-pastelDarkRed mb-10 text-center">
                Manage Foods
              </h1>
    
              {/* Button Tambah Menu */}
              <button
                className="bg-green-500 text-white rounded-lg px-4 py-2 mb-4"
                onClick={() => setShowForm(true)}
              >
                Add Food
              </button>
    
              <div className="overflow-x-auto shadow-lg rounded-lg mb-6">
                <table className="min-w-full bg-white rounded-lg shadow-lg">
                  <thead>
                    <tr className="bg-pastelRose text-white">
                      <th className="py-4 px-6 border-b">Food ID</th>
                      <th className="py-4 px-6 border-b">Food Name</th>
                      <th className="py-4 px-6 border-b">Price</th>
                      <th className="py-4 px-6 border-b">Description</th>
                      <th className="py-4 px-6 border-b">Image</th>
                      <th className="py-4 px-6 border-b">Action</th>
                      <th className="py-4 px-6 border-b">View Details</th> {/* Tambahkan kolom untuk View Details */}
                    </tr>
                  </thead>
                  <tbody>
                    {foods.map((food, index) => (
                      <tr
                        key={food.id_menu}
                        className={`${index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'} hover:bg-pastelRose transition duration-200`}
                      >
                        <td className="py-4 px-6 border-b">{food.id_menu}</td>
                        <td className="py-4 px-6 border-b">{food.nama_menu}</td>
                        <td className="py-4 px-6 border-b">
                          {food.harga.toLocaleString('id-ID', {
                            style: 'currency',
                            currency: 'IDR',
                          })}
                        </td>
                        <td className="py-4 px-6 border-b">{food.deskripsi}</td>
                        <td className="py-4 px-6 border-b">
                          <img
                            src={food.gambar}
                            alt={food.nama_menu}
                            className="w-20 h-20 object-cover rounded-lg"
                          />
                        </td>
                        <td className="py-4 px-6 border-b flex space-x-4">
                          <button
                            className="bg-blue-500 text-white rounded-lg px-4 py-2"
                            onClick={() => openModal(food)} // Menggunakan openModal untuk melihat detail
                          >
                            View Details
                          </button>
                          <button
                            className="bg-blue-500 text-white rounded-lg px-4 py-2"
                            onClick={() => setIsEditing(true) /* Fungsi edit */} 
                          >
                            Edit
                          </button>
                          <button
                            className="bg-red-500 text-white rounded-lg px-4 py-2"
                            onClick={() => handleDeleteFood(food.id_menu)} // Fungsi hapus
                          >
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
    
            {/* Modal untuk detail makanan */}
            {showModal && currentMenu && (
              <div className="fixed inset-0 bg-gray-600 bg-opacity-75 flex items-center justify-center">
                <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
                  <button onClick={closeModal} className="text-right text-red-600 font-bold">x</button>
                  <div className="flex">
                    <img src={'http://localhost:8000/menu-image/' + currentMenu.gambar} alt={currentMenu.nama_menu} className="w-24 h-24 object-cover" />
                    <div className="ml-4">
                      <h3 className="font-bold text-lg">{currentMenu.nama_menu}</h3>
                      <p className="text-gray-600 mb-4">{currentMenu.deskripsi}</p>
                      <p className="font-bold">Rp {currentMenu.harga ? currentMenu.harga.toLocaleString() : 'N/A'}</p>
                      
                      {/* Increment/Decrement Buttons */}
                      <div className="flex items-center mt-4">
                        <button onClick={() => handleDecrement(currentMenu.id_menu)} className="bg-gray-300 rounded-full px-3 py-1">-</button>
                        <span className="mx-2">{selectedMenu.find(item => item.id_menu === currentMenu.id_menu)?.quantity || 0}</span>
                        <button onClick={() => handleIncrement(currentMenu.id_menu)} className="bg-gray-300 rounded-full px-3 py-1">+</button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
    
            {/* Modal untuk Add/Edit Form */}
            {showForm && (
              <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
                <div className="bg-white p-6 rounded-lg shadow-lg">
                  <h2 className="text-2xl font-semibold mb-4">
                    {isEditing ? 'Edit Food' : 'Add Food'}
                  </h2>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">Food Name</label>
                    <input
                      type="text"
                      className="border border-gray-300 p-2 rounded-lg w-full"
                      value={foodData.nama_menu}
                      onChange={(e) => setFoodData({ ...foodData, nama_menu: e.target.value })}
                    />
                  </div>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">Price</label>
                    <input
                      type="number"
                      className="border border-gray-300 p-2 rounded-lg w-full"
                      value={foodData.harga}
                      onChange={(e) => setFoodData({ ...foodData, harga: e.target.value })}
                    />
                  </div>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">Description</label>
                    <textarea
                      className="border border-gray-300 p-2 rounded-lg w-full"
                      value={foodData.deskripsi}
                      onChange={(e) => setFoodData({ ...foodData, deskripsi: e.target.value })}
                    />
                  </div>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">Image URL</label>
                    <input
                      type="text"
                      className="border border-gray-300 p-2 rounded-lg w-full"
                      value={foodData.gambar}
                      onChange={(e) => setFoodData({ ...foodData, gambar: e.target.value })}
                    />
                  </div>
                  <div className="flex justify-end">
                    <button
                      className="bg-blue-500 text-white rounded-lg px-4 py-2 mr-2"
                      onClick={handleSaveFood} // Simpan data makanan
                    >
                      {isEditing ? 'Update' : 'Add'}
                    </button>
                    <button
                      className="bg-red-500 text-white rounded-lg px-4 py-2"
                      onClick={() => setShowForm(false)}
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </>
      );
    };
    
    export default ManageFoods;
