import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import 'tailwindcss/tailwind.css';
import Sidebar from './Sidebar'; // Import Sidebar

const Cashier = ({ user }) => {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const [tables, setTables] = useState([]);
    const [selectedMenu, setSelectedMenu] = useState([]);
    const [selectedMenuItem, setSelectedMenuItem] = useState(null);
    const [isTableOccupied, setIsTableOccupied] = useState(false);
    const [customerName, setCustomerName] = useState('');
    const [tableNumber, setTableNumber] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [transactionReceipt, setTransactionReceipt] = useState(null);
    const [isPaid, setIsPaid] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchMenuItems();
        fetchTableNumbers();
    }, []);

    const fetchMenuItems = async () => {
        try {
            const response = await fetch('http://localhost:8000/menu');
            const data = await response.json();
            setMenuItems(data.data || []);
        } catch (error) {
            console.error('Error fetching menu items:', error);
        }
    };

    const fetchTableNumbers = async () => {
        try {
            const response = await fetch('http://localhost:8000/meja');
            const data = await response.json();
            setTables(data.data || []);
        } catch (error) {
            console.error('Error fetching table numbers:', error);
        }
    };

    const handleTableCheck = (selectedTable) => {
        const table = tables.find((t) => t.nomor_meja === selectedTable);
        const occupied = table ? table.status === 'terisi' : false;
        setIsTableOccupied(occupied);
        setTableNumber(selectedTable);
    };

    const handleAddMenu = () => {
        if (!selectedMenuItem || quantity < 1) {
            setErrorMessage('Silakan pilih menu dan isi kuantitas terlebih dahulu.');
            return;
        }

        const existingMenu = selectedMenu.find(item => item.id_menu === selectedMenuItem.id_menu);
        if (existingMenu) {
            setSelectedMenu(prev => prev.map(item =>
                item.id_menu === selectedMenuItem.id_menu
                    ? { ...item, quantity: item.quantity + quantity }
                    : item
            ));
        } else {
            setSelectedMenu(prev => [...prev, { ...selectedMenuItem, quantity }]);
        }

        setQuantity(1); // Reset kuantitas
        setErrorMessage('');
    };

    const handleMenuChange = (e) => {
        const selectedId = e.target.value;
        const menuItem = menuItems.find(item => item.id_menu === parseInt(selectedId));
        setSelectedMenuItem(menuItem || null);
    };

    const handleQuantityChange = (type) => {
        if (type === 'increment') {
            setQuantity((prevQuantity) => prevQuantity + 1);
        } else if (type === 'decrement' && quantity > 1) {
            setQuantity((prevQuantity) => prevQuantity - 1);
        }
    };

    const handleSubmitTransaction = async () => {
        if (!customerName || !tableNumber) {
            setErrorMessage('Nama pelanggan dan nomor meja harus diisi!');
            return;
        }

        if (selectedMenu.length === 0) {
            setErrorMessage('Silakan tambahkan setidaknya satu menu sebelum submit!');
            return;
        }

        const newTransaction = {
            tgl_transaksi: new Date().toISOString(),
            id_meja: tableNumber,
            nama_pelanggan: customerName,
            status: 'belum_bayar',
            detail_transaksi: selectedMenu.map(item => ({
                id_menu: item.id_menu,
                quantity: item.quantity,
            })),
        };

        try {
            const response = await fetch('http://localhost:22077/transaksi', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(newTransaction),
            });

            if (response.ok) {
                alert('Transaksi berhasil ditambahkan!');
                await updateTableStatus(tableNumber, 'terisi');
                setTransactionReceipt(newTransaction);
                setSelectedMenu([]);
                setCustomerName('');
                setTableNumber('');
            } else {
                const errorText = await response.text();
                console.error('Failed to add transaction:', errorText);
                setErrorMessage('Gagal menambahkan transaksi. Coba lagi!');
            }
        } catch (error) {
            console.error('Error submitting transaction:', error);
        }
    };

    const updateTableStatus = async (tableNumber, status) => {
        try {
            const response = await fetch(`http://localhost:22077/meja/${tableNumber}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ status }),
            });

            if (!response.ok) {
                throw new Error('Failed to update table status');
            }
        } catch (error) {
            console.error('Error updating table status:', error);
        }
    };

    const handlePayment = async () => {
        if (!transactionReceipt) {
            setErrorMessage('Belum ada transaksi untuk dibayar!');
            return;
        }

        const updatedReceipt = {
            ...transactionReceipt,
            status: "lunas"
        };

        try {
            const response = await fetch(`http://localhost:22077/transaksi/${transactionReceipt.id_meja}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(updatedReceipt),
            });

            if (response.ok) {
                alert('Pembayaran berhasil!');
                setIsPaid(true);
                setTransactionReceipt(updatedReceipt);
            } else {
                const errorText = await response.text();
                console.error('Error processing payment:', errorText);
                setErrorMessage('Gagal memproses pembayaran!');
            }
        } catch (error) {
            console.error('Error processing payment:', error);
            setErrorMessage('Terjadi kesalahan saat memproses pembayaran.');
        }
    };

    const handleDone = () => {
        setSelectedMenu([]);
        setTransactionReceipt(null);
        setIsPaid(false);
        setCustomerName('');
        setTableNumber('');
        window.location.reload();
    };

    const calculateReceiptTotalHarga = () => {
        if (!transactionReceipt) return 0;

        return transactionReceipt.detail_transaksi.reduce((total, item) => {
            const menu = menuItems.find(menuItem => menuItem.id_menu === item.id_menu);
            return total + (menu ? menu.harga * item.quantity : 0);
        }, 0);
    };

    const formatDate = (date) => {
        return new Date(date).toLocaleString();
    };

    return (
        <div className="flex min-h-screen bg-gradient-to-r from-[#b7122d] via-[#c44c5e] to-[#f5a1ac]">
            <Sidebar user={user} /> {/* Render Sidebar */}

            <div className="flex-grow p-4">
                <h1 className="text-3xl font-bold text-white mb-6 text-center">Tambah Transaksi</h1>
                <form onSubmit={(e) => { e.preventDefault(); }} className="bg-white rounded-lg shadow-lg p-6">
                    <div className="mb-4">
                        <label htmlFor="customerName" className="block text-gray-700">Nama Pelanggan</label>
                        <input
                            type="text"
                            className="mt-1 block w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#c44c5e]"
                            id="customerName"
                            value={customerName}
                            onChange={(e) => setCustomerName(e.target.value)}
                            placeholder="Masukkan nama pelanggan"
                            required
                        />
                    </div>

                    <div className="mb-4">
                        <label htmlFor="tableNumber" className="block text-gray-700">Nomor Meja</label>
                        <select
                            className="mt-1 block w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#c44c5e]"
                            id="tableNumber"
                            value={tableNumber}
                            onChange={(e) => handleTableCheck(e.target.value)}
                            required
                        >
                            <option value="">Pilih meja</option>
                            {tables.map((table) => (
                                <option key={table.id_meja} value={table.id_meja}>{table.nomor_meja}</option>
                            ))}
                        </select>
                        {isTableOccupied && <p className="text-red-500">Meja ini sudah terisi!</p>}
                    </div>

                    <div className="mb-4">
                        <label htmlFor="menu" className="block text-gray-700">Pilih Menu</label>
                        <select
                            className="mt-1 block w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#c44c5e]"
                            id="menu"
                            onChange={handleMenuChange}
                            required
                        >
                            <option value="">Pilih menu</option>
                            {menuItems.map((menuItem) => (
                                <option key={menuItem.id_menu} value={menuItem.id_menu}>{menuItem.nama_menu} - Rp{menuItem.harga}</option>
                            ))}
                        </select>
                    </div>

                    <div className="mb-4">
                        <label className="block text-gray-700">Kuantitas</label>
                        <div className="flex items-center space-x-2">
                            <button
                                type="button"
                                className="px-3 py-1 bg-[#c44c5e] text-white rounded-lg"
                                onClick={() => handleQuantityChange('decrement')}
                            >
                                -
                            </button>
                            <span>{quantity}</span>
                            <button
                                type="button"
                                className="px-3 py-1 bg-[#c44c5e] text-white rounded-lg"
                                onClick={() => handleQuantityChange('increment')}
                            >
                                +
                            </button>
                        </div>
                    </div>

                    <div className="mb-4">
                        <button
                            type="button"
                            className="w-full px-4 py-2 bg-[#b7122d] text-white rounded-lg hover:bg-[#c44c5e] focus:ring-2 focus:ring-[#c44c5e]"
                            onClick={handleAddMenu}
                        >
                            Tambah Menu
                        </button>
                    </div>

                    {errorMessage && <p className="text-red-500">{errorMessage}</p>}

                    <div className="mb-4">
                        <h3 className="text-lg font-bold text-gray-700">Pesanan</h3>
                        <ul className="list-disc ml-5">
                            {selectedMenu.map((item) => (
                                <li key={item.id_menu}>
                                    {item.nama_menu} - {item.quantity} pcs
                                </li>
                            ))}
                        </ul>
                    </div>

                    <div className="mb-4">
                        <button
                            type="button"
                            className="w-full px-4 py-2 bg-[#b7122d] text-white rounded-lg hover:bg-[#c44c5e] focus:ring-2 focus:ring-[#c44c5e]"
                            onClick={handleSubmitTransaction}
                        >
                            Submit Transaksi
                        </button>
                    </div>
                </form>

                {transactionReceipt && (
                    <div className="bg-white rounded-lg shadow-lg p-6 mt-6">
                        <h2 className="text-2xl font-bold mb-4">Nota Transaksi</h2>
                        <p><strong>Nama Pelanggan:</strong> {transactionReceipt.nama_pelanggan}</p>
                        <p><strong>Nomor Meja:</strong> {transactionReceipt.id_meja}</p>
                        <p><strong>Tanggal:</strong> {formatDate(transactionReceipt.tgl_transaksi)}</p>
                        <p><strong>Status:</strong> {transactionReceipt.status}</p>

                        <h3 className="text-lg font-bold mt-4">Detail Pesanan</h3>
                        <ul className="list-disc ml-5">
                            {transactionReceipt.detail_transaksi.map((item, index) => (
                                <li key={index}>
                                    {menuItems.find(menu => menu.id_menu === item.id_menu)?.nama_menu} - {item.quantity} pcs
                                </li>
                            ))}
                        </ul>

                        <p className="text-lg font-bold mt-4">Total: Rp{calculateReceiptTotalHarga()}</p>

                        {!isPaid ? (
                            <button
                                type="button"
                                className="w-full px-4 py-2 mt-4 bg-[#b7122d] text-white rounded-lg hover:bg-[#c44c5e] focus:ring-2 focus:ring-[#c44c5e]"
                                onClick={handlePayment}
                            >
                                Bayar
                            </button>
                        ) : (
                            <button
                                type="button"
                                className="w-full px-4 py-2 mt-4 bg-[#c44c5e] text-white rounded-lg focus:ring-2 focus:ring-[#c44c5e]"
                                onClick={handleDone}
                            >
                                Selesai
                            </button>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Cashier;
