import React, { useState } from 'react';
import 'tailwindcss/tailwind.css';
import coffeeImage from '../assets/pinky.jpg'; 
import { useNavigate } from 'react-router-dom';

const RegisterPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const register = async (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      setError("Passwords don't match");
      return;
    }

    try {
      const response = await fetch('http://localhost:8000/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (data.message === 'Registration Success') {
        navigate('/login'); // Redirect to login page after successful registration
      } else {
        setError(data.message || 'Registration failed');
      }
    } catch (err) {
      setError('Error registering');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-pink-100">
      <div className="bg-white shadow-md rounded-lg flex flex-col md:flex-row w-full max-w-4xl overflow-hidden">
        {/* Left Side - Image & Text */}
        <div className="relative hidden md:flex md:w-1/2 p-8 bg-pink-50 items-center justify-center">
          <div className="absolute inset-0 bg-cover bg-center" style={{ backgroundImage: `url(${coffeeImage})` }}></div>
          <div className="relative z-10 text-center">
            <h2 className="text-3xl font-semibold text-pink-500">Join Cofesho!</h2>
            <p className="mt-2 text-pink-500">Become part of our coffee community and enjoy your favorite brews!</p>
          </div>
        </div>

        {/* Right Side - Registration Form */}
        <div className="w-full md:w-1/2 p-8">
          <h3 className="text-2xl font-semibold text-center text-gray-700">Register</h3>
          <p className="text-center text-gray-500">Create your account.</p>
          
          {/* Input Fields */}
          <form onSubmit={register} className="mt-8 space-y-4">
            <input
              type='text'
              className="w-full p-3 border border-pink-200 rounded-lg focus:ring-2 focus:ring-pink-400"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder='Username'
              required 
            />
            <input
              type='password'
              className="w-full p-3 border border-pink-200 rounded-lg focus:ring-2 focus:ring-pink-400"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder='Password'
              required 
            />
            <input
              type='password'
              className="w-full p-3 border border-pink-200 rounded-lg focus:ring-2 focus:ring-pink-400"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder='Confirm Password'
              required 
            />

            {/* Error Message */}
            {error && <p className='text-red-500 text-center mt-3'>{error}</p>}

            {/* Register Button */}
            <div className="mt-6">
              <button 
                type="submit"
                className="w-full bg-pink-500 text-white py-3 rounded-lg hover:bg-pink-600 transition"
              >
                Register
              </button>
            </div>
          </form>

          {/* Login Link */}
          <p className='text-center mt-3 text-pink-500'>
            Already have an account? <a href='/login' className="text-pink-600 hover:underline">Log in</a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Register;
