import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar'; // Import Sidebar

const SearchBar = ({ searchTerm, setSearchTerm }) => (
    <div className="flex items-center bg-white rounded-full shadow-md p-2 w-full md:w-1/2">
        <i className="fas fa-search text-gray-400 ml-2"></i>
        <input
            type="text"
            placeholder="Search..."
            className="ml-2 w-full outline-none"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
        />
    </div>
);

const FilterButton = ({ children }) => (
    <button className="bg-orange-400 text-white rounded-full px-4 py-2">{children}</button>
);

const MenuItem = ({ item, handleQuantityChange, quantity, addToCart }) => (
    <div className="bg-white rounded-lg shadow-md p-4">
        <img src="https://placehold.co/100x150" alt={item.nama_menu} className="w-full h-40 object-cover rounded-lg mb-4" />
        <h3 className="text-xl font-bold">{item.nama_menu} <span className="text-orange-400">Rp. {item.harga}</span></h3>
        <p className="text-gray-600 mb-4">{item.description}</p>
        <div className="flex items-center justify-between">
            <div className="flex items-center">
                <button
                    onClick={() => handleQuantityChange(item, 'decrement')}
                    className="bg-gray-200 text-gray-600 rounded-full px-2 py-1"
                >
                    -
                </button>
                <span className="mx-2">{quantity}</span>
                <button
                    onClick={() => handleQuantityChange(item, 'increment')}
                    className="bg-gray-200 text-gray-600 rounded-full px-2 py-1"
                >
                    +
                </button>
            </div>
            <button
                onClick={() => addToCart(item)}
                className="bg-orange-400 text-white rounded-full px-4 py-2"
            >
                Added to cart
            </button>
        </div>
    </div>
);

const Cart = ({ selectedMenu }) => {
    const totalAmount = selectedMenu.reduce((total, item) => total + item.harga * item.quantity, 0);
    return (
        <div className="w-full md:w-1/4 bg-white p-6">
            <h2 className="text-2xl font-bold mb-4">Cart</h2>
            {selectedMenu.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <div>
                    <ul>
                        {selectedMenu.map((item, index) => (
                            <li key={index} className="flex justify-between">
                                <span>{item.nama_menu} (x{item.quantity})</span>
                                <span>Rp. {item.harga * item.quantity}</span>
                            </li>
                        ))}
                    </ul>
                    <div className="flex justify-between font-bold text-lg mt-4">
                        <span>Total</span>
                        <span>Rp. {totalAmount}</span>
                    </div>
                    <button className="bg-orange-400 text-white rounded-full w-full py-2 mt-4">Place an order</button>
                </div>
            )}
        </div>
    );
};

const App = ({ user }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedMenu, setSelectedMenu] = useState([]);
    const [menuItems, setMenuItems] = useState([]);
    const [tables, setTables] = useState([]);
    const [selectedMenuItem, setSelectedMenuItem] = useState(null);
    const [isTableOccupied, setIsTableOccupied] = useState(false);
    const [customerName, setCustomerName] = useState('');
    const [tableNumber, setTableNumber] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [transactionReceipt, setTransactionReceipt] = useState(null);
    const [isPaid, setIsPaid] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [selectedItemId, setSelectedItemId] = useState(null);
    
    
    useEffect(() => {
        fetchMenuItems();
        fetchTableNumbers();
    }, []);

    const fetchMenuItems = async () => {
        try {
            const response = await fetch(`http://localhost:8000/menu`);
            const data = await response.json();
            setMenuItems(data.data || []);
        } catch (error) {
            console.error('Error fetching menu items:', error);
        }
    };

    const fetchTableNumbers = async () => {
        try {
            const response = await fetch('http://localhost:8000/meja');
            const data = await response.json();
            setTables(data.data || []);
        } catch (error) {
            console.error('Error fetching table numbers:', error);
        }
    };

    const handleTableCheck = (selectedTable) => {
        const table = tables.find((t) => t.nomor_meja === selectedTable);
        const occupied = table ? table.status === 'terisi' : false;
        setIsTableOccupied(occupied);
        setTableNumber(selectedTable);
    };
    
    const handleAddMenu = () => {
        if (!selectedMenuItem || quantity < 1) {
            setErrorMessage('Silakan pilih menu dan isi kuantitas terlebih dahulu.');
            return;
        }

        const existingMenu = selectedMenu.find(item => item.id_menu === selectedMenuItem.id_menu);
        if (existingMenu) {
            existingMenu.quantity += quantity; // Tambah kuantitas jika menu sudah ada
        } else {
            const newMenu = {
                ...selectedMenuItem,
                quantity: quantity,
            };
            setSelectedMenu((prev) => [...prev, newMenu]); // Tambahkan menu baru
        }

        setQuantity(1); // Reset kuantitas
        setErrorMessage('');
    };

    const handleMenuChange = (e) => {
        const selectedId = e.target.value;
        const menuItem = menuItems.find(item => item.id_menu === parseInt(selectedId));
        setSelectedMenuItem(menuItem || null); // Set selected menu item
    };

    // const handleQuantityChange = (type) => {
    //     if (type === 'increment') {
    //         setQuantity((prevQuantity) => prevQuantity + 1);
    //     } else if (type === 'decrement' && quantity > 1) {
    //         setQuantity((prevQuantity) => prevQuantity - 1);
    //     }
    // };

    const handleQuantityChange = (action) => {
        if (selectedItemId === null) return; // Don't do anything if no item is selected
        setQuantity((prev) => {
            const currentQuantity = prev[selectedItemId] || 0;
            return {
                ...prev,
                [selectedItemId]: action === 'increment' ? currentQuantity + 1 : Math.max(0, currentQuantity - 1),
            };
        });
    };

    const handleSubmitTransaction = async () => {
        if (!customerName || !tableNumber) {
            setErrorMessage('Nama pelanggan dan nomor meja harus diisi!');
            return;
        }

        if (selectedMenu.length === 0) {
            setErrorMessage('Silakan tambahkan setidaknya satu menu sebelum submit!');
            return;
        }

        const newTransaction = {
            tgl_transaksi: new Date().toISOString(),
            id_user: localStorage.getItem("id_user"),
            id_meja: tableNumber, // Misal tableNumber dari state atau props
            nama_pelanggan: customerName, // Nama pelanggan dari input field
            status: isPaid ? 'lunas' : 'belum_bayar', // Cek jika sudah dibayar, statusnya 'lunas', kalau tidak 'belum_bayar'
            detail_transaksi: selectedMenu.map(item => ({
                id_menu: item.id_menu,
                quantity: item.quantity,
            })),
        };

        try {
            let token = localStorage.getItem('token')
            const response = await fetch('http://localhost:8000/transaksi', {
                method: 'POST',
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                 },
                body: JSON.stringify(newTransaction),
            });

            if (response.ok) {
                alert('Transaksi berhasil ditambahkan!');

                // Update table status to "terisi"
                await updateTableStatus(tableNumber, 'terisi');
                let result= await response.json()
                let receipt = {...newTransaction, id_transaksi: result.insertTransaksi.id_transaksi}
                setTransactionReceipt(receipt);
                setSelectedMenu([]);
                setCustomerName('');
                setTableNumber('');
            } else {
                const errorText = await response.text(); // Ambil teks dari respon
                console.error('Failed to add transaction:', errorText); // Tampilkan pesan kesalahan di console
                setErrorMessage('Gagal menambahkan transaksi. Coba lagi!');
            }
        } catch (error) {
            console.error('Error submitting transaction:', error);
        }
    };

    const updateTableStatus = async (tableNumber, status) => {
        try {
            let token = localStorage.getItem('token')
            const response = await fetch(`http://localhost:8000/meja/${tableNumber}`, {
                method: 'PUT',
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization' : `Bearer ${token}`
                 },
                body: JSON.stringify({ status }),
            });

            if (!response.ok) {
                throw new Error('Failed to update table status');
            }
        } catch (error) {
            console.error('Error updating table status:', error);
        }
    };

    const handlePayment = async () => {
        const updatedReceipt = {
            ...transactionReceipt,
            status: "lunas"
        };

        try {
            let token = localStorage.getItem('token')
            const response = await fetch(`http://localhost:8000/transaksi/${transactionReceipt.id_transaksi}`, {
                method: 'PUT',
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                 },
                body: JSON.stringify(updatedReceipt),
            
            });

            if (response.ok) {
                alert('Pembayaran berhasil!');
                setIsPaid(true);
                setTransactionReceipt(updatedReceipt);
            }
        } catch (error) {
            console.error('Error processing payment:', error);
        }
    };

    const handleDone = () => {
        setSelectedMenu([]);
        setTransactionReceipt(null);
        setIsPaid(false);
        window.location.reload();
    };

    const calculateReceiptTotalHarga = () => {
        if (!transactionReceipt) return 0;

        return transactionReceipt.detail_transaksi.reduce((total, item) => {
            const menu = menuItems.find(menuItem => menuItem.id_menu === item.id_menu);
            return total + (menu ? menu.harga * item.quantity : 0);
        }, 0);
    };
    // const menuItems = [
    //     { id_menu: 1, nama_menu: 'Cappuccino', harga: 498, description: 'The combination of coffee, milk, and palm sugar makes this drink have a delicious' },
    //     { id_menu: 2, nama_menu: 'Latte', harga: 450, description: 'A smooth blend of espresso with steamed milk' }
    //     // Tambahkan item lainnya sesuai kebutuhan
    // ];

    // const handleQuantityChange = (item, action) => {
    //     setQuantity((prevQuantities) => {
    //         const currentQuantity = prevQuantities[item.id_menu] || 0;
    //         const newQuantity = action === 'increment' ? currentQuantity + 1 : currentQuantity - 1;
    //         return { ...prevQuantities, [item.id_menu]: Math.max(newQuantity, 0) };
    //     });
    // };

    const addToCart = (item) => {
        const itemInCart = selectedMenu.find(menuItem => menuItem.id_menu === item.id_menu);
        if (itemInCart) {
            setSelectedMenu(selectedMenu.map(menuItem =>
                menuItem.id_menu === item.id_menu ? { ...menuItem, quantity: quantity[item.id_menu] } : menuItem
            ));
        } else {
            setSelectedMenu([...selectedMenu, { ...item, quantity: quantity[item.id_menu] || 1 }]);
        }
    };

    const filteredMenuItems = menuItems.filter(item =>
        item.nama_menu.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="flex">
            <Sidebar /> {/* Deklarasi Sidebar di sebelah kiri */}
            <div className="flex flex-col md:flex-row w-full">
                <div className="w-full md:w-3/4 p-6">
                    {/* Form Nama Pelanggan dan Nomor Meja */}
                    <div className="mb-6">
                        <label className="block mb-2 font-bold">Nama Pelanggan:</label>
                        <input
                            type="text"
                            className="border rounded-md p-2 w-full"
                            value={customerName}
                            onChange={(e) => setCustomerName(e.target.value)}
                        />
                    </div>
                    <div className="mb-6">
                        <label className="block mb-2 font-bold">Nomor Meja:</label>
                        <select
                            className="border rounded-md p-2 w-full"
                            value={tableNumber}
                            onChange={(e) => handleTableCheck(e.target.value)}
                        >
                            <option value="">Pilih Meja</option>
                            {tables.map((table) => (
                                <option key={table.nomor_meja} value={table.nomor_meja}>
                                    {table.nomor_meja}
                                </option>
                            ))}
                        </select>
                    </div>
    
                    <div className="flex items-center justify-between mb-6">
                        <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
                        <FilterButton>Filter</FilterButton>
                    </div>
    
                {/* Menu List */}
<div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-6">
    {filteredMenuItems.map((item) => (
        <MenuItem
            key={item.id_menu}
            item={item}
            handleQuantityChange={handleQuantityChange}
            quantity={quantity[item.id_menu] || 0} // Pass the current quantity
            addToCart={addToCart}
            selectItem={() => setSelectedItemId(item.id_menu)} // Function to select item
        />
    ))}
</div>

{/* Quantity Control Form */}
<div className="mt-6">
    <form className="flex items-center w-full mb-4">
        <label htmlFor="quantity" className="block text-[#1d3c34] mr-4">Kuantitas</label>
        <button
            type="button"
            className="bg-[#29473f] text-white px-4 py-1 rounded-l-lg"
            onClick={() => handleQuantityChange('decrement')}
        >-</button>
        <input
            type="number"
            className="w-full text-center border border-gray-300 rounded-none font-poppins py-1 mx-2"
            id="quantity"
            value={quantity[selectedItemId] || 0} // Use selectedItemId for current item
            onChange={(e) => setQuantity(selectedItemId, parseInt(e.target.value) || 0)} // Set quantity for selected item
            min="1"
        />
        <button
            type="button"
            className="bg-[#29473f] text-white px-4 py-1 rounded-r-lg"
            onClick={() => handleQuantityChange('increment')}
        >+</button>
    </form>
</div>

    
                    {/* Submit Order Button */}
                    <button
                        className="bg-green-400 text-white rounded-full px-4 py-2 mt-6"
                        onClick={handleSubmitTransaction}
                    >
                        Submit Order
                    </button>
    
                    {/* Error Message */}
                    {errorMessage && <p className="text-red-500 mt-4">{errorMessage}</p>}
    
                    {/* Transaction Receipt */}
                    {transactionReceipt && (
                        <div className="mt-6 p-6 bg-white rounded-md shadow-md">
                            <h2 className="text-2xl font-bold">Transaction Receipt</h2>
                            <p>Nama Pelanggan: {transactionReceipt.nama_pelanggan}</p>
                            <p>Nomor Meja: {transactionReceipt.id_meja}</p>
                            <p>Status: {transactionReceipt.status}</p>
    
                            <h3 className="mt-4 font-bold">Detail Transaksi</h3>
                            <ul>
                                {transactionReceipt.detail_transaksi.map((item, index) => {
                                    const menu = menuItems.find((menuItem) => menuItem.id_menu === item.id_menu);
                                    return (
                                        <li key={index}>
                                            {menu?.nama_menu} - {item.quantity} pcs @ Rp. {menu?.harga} = Rp. {menu?.harga * item.quantity}
                                        </li>
                                    );
                                })}
                            </ul>
                            <div className="font-bold text-lg mt-4">
                                Total: Rp. {calculateReceiptTotalHarga()}
                            </div>
    
                            {!isPaid && (
                                <button
                                    className="bg-blue-400 text-white rounded-full w-full py-2 mt-4"
                                    onClick={handlePayment}
                                >
                                    Bayar Sekarang
                                </button>
                            )}
    
                            {isPaid && (
                                <div className="bg-green-100 text-green-700 p-4 mt-4 rounded-md">
                                    Transaksi telah dibayar!
                                </div>
                            )}
    
                            <button
                                className="bg-gray-400 text-white rounded-full w-full py-2 mt-4"
                                onClick={handleDone}
                            >
                                Selesai
                            </button>
                        </div>
                    )}
                </div>
                <Cart selectedMenu={selectedMenu} />
            </div>
        </div>
    );
}
    
export default App;
