import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from './Sidebar';
import CafeLogo from '../assets/logo.jpg';
import Logout from './Logout';

const Cashier = ({ user }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [customerName, setCustomerName] = useState('');
  const [tableNumber, setTableNumber] = useState('');
  const [orders, setOrders] = useState([]);
  const [menuInput, setMenuInput] = useState('');
  const [quantityInput, setQuantityInput] = useState('');
  const [showReceipt, setShowReceipt] = useState(false);
  const [status, setStatus] = useState('Belum Bayar');

  const [tables, setTables] = useState([]);
  const [menuItems, setMenuItems] = useState([]);

  const total = orders.reduce((sum, order) => sum + (order.harga * order.quantity || 0), 0);

  useEffect(() => {
    const fetchTables = async () => {
      try {
        const response = await fetch('http://localhost:8000/meja');
        const data = await response.json();
        setTables(data.data || []);
      } catch (error) {
        console.error("Error fetching tables:", error);
      }
    };

    const fetchMenuItems = async () => {
      try {
        const response = await fetch('http://localhost:8000/menu');
        const data = await response.json();
        setMenuItems(data.data || []);
      } catch (error) {
        console.error("Error fetching menu items:", error);
      }
    };

    fetchTables();
    fetchMenuItems();
  }, []);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleOrderSubmit = (e) => {
    e.preventDefault();
    const selectedMenu = menuItems.find(item => item.nama_menu === menuInput);
    if (selectedMenu) {
      const order = {
        menu: menuInput,
        quantity: parseInt(quantityInput),
        harga: selectedMenu.harga,
      };
      setOrders([...orders, order]);
      setMenuInput('');
      setQuantityInput('');
    } else {
      console.error('Menu tidak ditemukan');
    }
  };

  const handlePrintReceipt = () => {
    setShowReceipt(true);
  };

  const handlePayment = async () => {
    // Update status only without refreshing the page
    setStatus('Lunas'); // Change status to 'Lunas'

    // If you want to save this status in the database, you can uncomment the following lines
    /*
    const transactionData = {
      customerName,
      tableNumber,
      orders,
      total,
      status: 'Lunas',
    };

    try {
      const response = await fetch('http://localhost:8000/transaksi', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(transactionData),
      });

      if (response.ok) {
        // Optionally clear the orders and other states after successful payment
        // setOrders([]);
        // setCustomerName('');
        // setTableNumber('');
      } else {
        console.error('Failed to post transaction');
      }
    } catch (error) {
      console.error("Error posting transaction:", error);
    }
    */
  };

  return (
    <div className="flex bg-pastelPink min-h-screen">
      <Sidebar user={user} />
      <div className="flex-1 p-5">
        {!isLoggedIn ? (
          <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-md mx-auto">
            <h2 className="text-2xl mb-4">Login Kasir</h2>
            <button
              onClick={handleLogin}
              className="w-full bg-pastelRose text-white py-2 rounded-lg hover:bg-pastelLightPink transition duration-300"
            >
              Login
            </button>
          </div>
        ) : (
          <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-2xl mx-auto">
            <h2 className="text-2xl font-bold mb-6">Transaksi Pemesanan</h2>

            <div className="mb-6 grid grid-cols-1 gap-4">
              <input
                type="text"
                value={customerName}
                onChange={(e) => setCustomerName(e.target.value)}
                placeholder="Nama Pelanggan"
                className="border border-gray-300 p-3 rounded-lg w-full"
                required
              />
              <select
                value={tableNumber}
                onChange={(e) => setTableNumber(e.target.value)}
                className="border border-gray-300 p-3 rounded-lg w-full"
                required
              >
                <option value="">Pilih Nomor Meja</option>
                {Array.isArray(tables) && tables.map((table) => (
                  <option key={table.id_meja} value={table.nomor_meja}>
                    {table.nomor_meja}
                  </option>
                ))}
              </select>
            </div>

            <form onSubmit={handleOrderSubmit} className="mb-6 grid grid-cols-1 gap-4">
              <div className="flex space-x-4">
                <select
                  value={menuInput}
                  onChange={(e) => setMenuInput(e.target.value)}
                  className="border border-gray-300 p-3 rounded-lg flex-1"
                  required
                >
                  <option value="">Pilih Menu...</option>
                  {menuItems.map((item) => (
                    <option key={item.id_menu} value={item.nama_menu}>
                      {item.nama_menu}
                    </option>
                  ))}
                </select>
                <input
                  type="number"
                  value={quantityInput}
                  onChange={(e) => setQuantityInput(e.target.value)}
                  placeholder="Jumlah"
                  className="border border-gray-300 p-3 rounded-lg w-24"
                  required
                  min="1"
                />
              </div>
              <button
                type="submit"
                className="w-full bg-pastelRose text-white py-2 rounded-lg hover:bg-pastelLightPink transition duration-300"
              >
                Tambah Pesanan
              </button>
            </form>

            <h3 className="text-xl font-semibold mb-4">Daftar Pesanan:</h3>
            <ul className="list-disc pl-5 mb-4">
              {orders.map((order, index) => (
                <li key={index}>
                  {order.menu} (x{order.quantity}) - Rp{(order.harga * order.quantity).toLocaleString()}
                </li>
              ))}
            </ul>

            <h3 className="text-xl font-semibold my-4">Total: Rp{total.toLocaleString() || 0}</h3>

            <button
              onClick={handlePrintReceipt}
              className="w-full bg-green-400 text-white py-2 rounded-lg hover:bg-green-500 transition duration-300"
            >
              Cetak Nota
            </button>

            <button
              onClick={handlePayment}
              className="w-full bg-blue-400 text-white py-2 rounded-lg hover:bg-blue-500 transition duration-300 mt-2"
            >
              Bayar
            </button>
          </div>
        )}

{showReceipt && (
  <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-2xl mx-auto mt-10">
    <div className="flex justify-between items-center mb-4">
      <div className="flex items-center space-x-4">
        <img src={CafeLogo} alt="Cafe Logo" className="w-16 h-16" />
        <div>
          <h2 className="text-2xl font-bold">Belle Fiori</h2>
          <p className="text-gray-500">112 Elliot, Riverside, Jakarta</p>
          <p className="text-gray-500">Telp: 0819-9779-9924</p>
        </div>
      </div>
      <p className="text-gray-500">{new Date().toLocaleDateString()}</p>
    </div>

    <div className="border-t border-b border-gray-300 py-4">
      <div className="flex justify-between mb-2">
        <p>Customer Name:</p>
        <p>{customerName}</p>
      </div>
      <div className="flex justify-between mb-2">
        <p>Table Number:</p>
        <p>{tableNumber}</p>
      </div>
      <div className="flex justify-between mb-2">
        <p>Status:</p>
        <p className={`${status === 'Lunas' ? 'text-green-500' : 'text-red-500'}`}>{status}</p>
      </div>
    </div>

    <h3 className="text-xl font-semibold my-4">Daftar Pesanan:</h3>
    <div className="mb-4">
      <div className="flex font-bold border-b pb-2">
        <span className="flex-1">Menu</span>
        <span className="w-20 text-center">Quantity</span>
        <span className="w-32 text-right">Price (Rp)</span>
      </div>
      {orders.map((order, index) => (
        <div key={index} className="flex py-2">
          <span className="flex-1">{order.menu}</span>
          <span className="w-20 flex justify-center">{order.quantity}</span> {/* Center quantity */}
          <span className="w-32 text-right">{order.harga.toLocaleString()}</span>
        </div>
      ))}
    </div>

    <h3 className="text-xl font-semibold my-4">Total: Rp{total.toLocaleString() || 0}</h3>

    <button
      onClick={() => {
        setShowReceipt(false);
        window.location.reload(); // Auto refresh on this button click
      }}
      className="w-full bg-pastelRose text-white py-2 rounded-lg hover:bg-pastelLightPink transition duration-300 mt-4"
    >
      Selesai
    </button>
  </div>
)}

      </div>
    </div>
  );
};

export default Cashier;
