import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManageFoods = () => {
    const [foods, setFoods] = useState([]);
    const [isEditing, setIsEditing] = useState(false); // Status untuk mengetahui apakah sedang edit
    const [foodData, setFoodData] = useState({ id_menu: '', nama_menu: '', harga: '' }); // Data form
    const [showForm, setShowForm] = useState(false); // Tampilkan form modal

    useEffect(() => {
        fetchFoods();
    }, []);

    const fetchFoods = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/menu', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setFoods(data.data || []);
            } else {
                console.error('Failed to fetch foods');
            }
        } catch (error) {
            console.error('Error fetching foods:', error);
        }
    };

    const handleDeleteFood = async (foodId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8000/menu/${foodId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.ok) {
                fetchFoods();
            } else {
                console.error('Failed to delete food');
            }
        } catch (error) {
            console.error('Error deleting food:', error);
        }
    };

    const handleSaveFood = async () => {
        try {
            const token = localStorage.getItem('token');
            const method = isEditing ? 'PUT' : 'POST'; // Jika editing, gunakan PUT, jika tambah, gunakan POST
            const url = isEditing
                ? `http://localhost:8000/menu/${foodData.id_menu}`
                : 'http://localhost:8000/menu';

            const response = await fetch(url, {
                method: method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({
                    nama_menu: foodData.nama_menu,
                    harga: parseInt(foodData.harga),
                }),
            });

            if (response.ok) {
                setShowForm(false);
                setFoodData({ id_menu: '', nama_menu: '', harga: '' }); // Reset form
                fetchFoods(); // Refresh data makanan
            } else {
                console.error('Failed to save food');
            }
        } catch (error) {
            console.error('Error saving food:', error);
        }
    };

    const openFormToEdit = (food) => {
        setIsEditing(true);
        setFoodData(food); // Isi form dengan data makanan yang ingin di-edit
        setShowForm(true);
    };

    const openFormToAdd = () => {
        setIsEditing(false);
        setFoodData({ id_menu: '', nama_menu: '', harga: '' }); // Kosongkan form
        setShowForm(true);
    };

    return (
        <div className="flex">
            <Sidebar />

            <div className="flex-grow p-8 bg-white shadow-lg rounded-lg mx-6 mt-4 mb-4">
                <h1 className="text-4xl font-semibold text-pastelDarkRed mb-10 text-center">Manage Foods</h1>
                
                {/* Button Tambah Menu */}
                <button
                    className="bg-green-500 text-white rounded-lg px-4 py-2 mb-4"
                    onClick={openFormToAdd}
                >
                    Add Food
                </button>

                <div className="overflow-x-auto shadow-lg rounded-lg mb-6">
                    <table className="min-w-full bg-white rounded-lg shadow-lg">
                        <thead>
                            <tr className="bg-pastelRose text-white">
                                <th className="py-4 px-6 border-b">Food ID</th>
                                <th className="py-4 px-6 border-b">Food Name</th>
                                <th className="py-4 px-6 border-b">Price</th>
                                <th className="py-4 px-6 border-b">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {foods.map((food, index) => (
                                <tr
                                    key={food.id_menu}
                                    className={`${index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'} hover:bg-pastelRose transition duration-200`}
                                >
                                    <td className="py-4 px-6 border-b">{food.id_menu}</td>
                                    <td className="py-4 px-6 border-b">{food.nama_menu}</td>
                                    <td className="py-4 px-6 border-b">{food.harga.toLocaleString('id-ID', { style: 'currency', currency: 'IDR' })}</td>
                                    <td className="py-4 px-6 border-b flex space-x-4">
                                        <button
                                            className="bg-blue-500 text-white rounded-lg px-4 py-2"
                                            onClick={() => openFormToEdit(food)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className="bg-red-500 text-white rounded-lg px-4 py-2"
                                            onClick={() => handleDeleteFood(food.id_menu)}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>

            {/* Modal for Add/Edit Form */}
            {showForm && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
                    <div className="bg-white p-6 rounded-lg shadow-lg">
                        <h2 className="text-2xl font-semibold mb-4">{isEditing ? 'Edit Food' : 'Add Food'}</h2>
                        <div className="mb-4">
                            <label className="block text-gray-700 text-sm font-bold mb-2">Food Name</label>
                            <input
                                type="text"
                                className="border border-gray-300 p-2 rounded-lg w-full"
                                value={foodData.nama_menu}
                                onChange={(e) => setFoodData({ ...foodData, nama_menu: e.target.value })}
                            />
                        </div>
                        <div className="mb-4">
                            <label className="block text-gray-700 text-sm font-bold mb-2">Price</label>
                            <input
                                type="number"
                                className="border border-gray-300 p-2 rounded-lg w-full"
                                value={foodData.harga}
                                onChange={(e) => setFoodData({ ...foodData, harga: e.target.value })}
                            />
                        </div>
                        <div className="flex justify-end space-x-4">
                            <button
                                className="bg-gray-500 text-white rounded-lg px-4 py-2"
                                onClick={() => setShowForm(false)}
                            >
                                Cancel
                            </button>
                            <button
                                className="bg-blue-500 text-white rounded-lg px-4 py-2"
                                onClick={handleSaveFood}
                            >
                                {isEditing ? 'Save Changes' : 'Add Food'}
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ManageFoods;
