import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';
import Sidebar from '../page/Sidebar';

const ManageMenu = () => {
    const [menuItems, setMenuItems] = useState([]);

    useEffect(() => {
        fetchMenuItems();
    }, []);

    const fetchMenuItems = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/menu', {
                headers: { 'Authorization': `Bearer ${token}` },
            });

            if (response.ok) {
                const data = await response.json();
                setMenuItems(data.data || []);
            } else {
                console.error('Failed to fetch menu items');
            }
        } catch (error) {
            console.error('Error fetching menu items:', error);
        }
    };

    const handleDeleteMenuItem = async (menuId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8000/menu/${id.menu}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (response.ok) {
                // Refresh the menu list after deletion
                fetchMenuItems();
            } else {
                console.error('Failed to delete menu item');
            }
        } catch (error) {
            console.error('Error deleting menu item:', error);
        }
    };

    return (
        <div className="flex">
            {/* Sidebar */}
            <Sidebar />

            {/* Main Content */}
            <div className="flex-grow p-8 bg-white shadow-lg rounded-lg mx-6 mt-4 mb-4">
                <h1 className="text-4xl font-semibold text-pastelDarkRed mb-10 text-center">Manage Menu</h1>
                <div className="overflow-x-auto shadow-lg rounded-lg mb-6">
                    <table className="min-w-full bg-white rounded-lg shadow-lg">
                        <thead>
                            <tr className="bg-pastelRose text-white">
                                <th className="py-4 px-6 border-b">Menu ID</th>
                                <th className="py-4 px-6 border-b">Menu Name</th>
                                <th className="py-4 px-6 border-b">Price</th>
                                <th className="py-4 px-6 border-b">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {menuItems.map((menuItem, index) => (
                                <tr
                                    key={menuItem.id}
                                    className={`${index % 2 === 0 ? 'bg-pastelPink' : 'bg-pastelLightPink'} hover:bg-pastelRose transition duration-200`}
                                >
                                    <td className="py-4 px-6 border-b">{menuItem.id}</td>
                                    <td className="py-4 px-6 border-b">{menuItem.name}</td>
                                    <td className="py-4 px-6 border-b">{menuItem.price.toLocaleString('id-ID', { style: 'currency', currency: 'IDR' })}</td>
                                    <td className="py-4 px-6 border-b">
                                        <button
                                            className="bg-red-500 text-white rounded-lg px-4 py-2"
                                            onClick={() => handleDeleteMenuItem(menuItem.id)}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default ManageMenu;
