import React, { useState, useEffect } from 'react';
import 'tailwindcss/tailwind.css';

const Cashier = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [customerName, setCustomerName] = useState('');
  const [tableNumber, setTableNumber] = useState('');
  const [orders, setOrders] = useState([]);
  const [menuInput, setMenuInput] = useState('');
  const [quantityInput, setQuantityInput] = useState('');
  const [showReceipt, setShowReceipt] = useState(false);
  const [isPaid, setIsPaid] = useState(false); // State untuk status pembayaran
  const [tables, setTables] = useState([]); // State untuk menyimpan data meja
  const [menuItems, setMenuItems] = useState([]); // State untuk menyimpan data menu

  useEffect(() => {
    // Fetch data meja dan menu dari database
    const fetchData = async () => {
      try {
        const tablesResponse = await fetch('http://localhost:8000/tables');
        const tablesData = await tablesResponse.json();
        setTables(tablesData);

        const menuResponse = await fetch('http://localhost:8000/menu');
        const menuData = await menuResponse.json();
        setMenuItems(menuData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleOrderSubmit = (e) => {
    e.preventDefault();
    const order = {
      menu: menuInput,
      quantity: parseInt(quantityInput),
    };
    setOrders([...orders, order]);
    setMenuInput('');
    setQuantityInput('');
  };

  const handlePrintReceipt = () => {
    setShowReceipt(true);
  };

  const handlePayment = () => {
    setIsPaid(true); // Mengubah status pembayaran menjadi lunas
  };

  return (
    <div className="flex min-h-screen bg-pink-100">
      {/* Sidebar */}
      <div className="bg-white shadow-md w-1/4 p-5">
        <h2 className="text-xl font-bold mb-4">Menu</h2>
        <select
          className="border border-gray-300 p-2 rounded-lg w-full mb-4"
          value={menuInput}
          onChange={(e) => setMenuInput(e.target.value)}
        >
          <option value="" disabled>Pilih Menu</option>
          {menuItems.map((item, index) => (
            <option key={index} value={item.name}>{item.name}</option>
          ))}
        </select>

        <h2 className="text-xl font-bold mb-4">Pilih Meja</h2>
        <select
          className="border border-gray-300 p-2 rounded-lg w-full"
          value={tableNumber}
          onChange={(e) => setTableNumber(e.target.value)}
        >
          <option value="" disabled>Pilih Meja</option>
          {tables.map((table, index) => (
            <option key={index} value={table.number}>{table.number}</option>
          ))}
        </select>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col items-center justify-center p-5">
        <h1 className="text-3xl font-bold mb-5">Wikusama Cafe - Kasir</h1>

        {!isLoggedIn ? (
          <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-md">
            <h2 className="text-2xl mb-4">Login Kasir</h2>
            <button
              onClick={handleLogin}
              className="w-full bg-pink-500 text-white py-2 rounded-lg hover:bg-pink-600 transition duration-300"
            >
              Login
            </button>
          </div>
        ) : (
          <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-md">
            <h2 className="text-2xl mb-4">Transaksi Pemesanan</h2>

            <div className="mb-4">
              <input
                type="text"
                value={customerName}
                onChange={(e) => setCustomerName(e.target.value)}
                placeholder="Nama Pelanggan"
                className="border border-gray-300 p-2 rounded-lg w-full mb-2"
                required
              />
            </div>

            <form onSubmit={handleOrderSubmit} className="mb-4">
              <input
                type="number"
                value={quantityInput}
                onChange={(e) => setQuantityInput(e.target.value)}
                placeholder="Jumlah"
                className="border border-gray-300 p-2 rounded-lg w-full mb-2"
                required
                min="1"
              />
              <button
                type="submit"
                className="w-full bg-pink-500 text-white py-2 rounded-lg hover:bg-pink-600 transition duration-300"
              >
                Tambah Pesanan
              </button>
            </form>

            <h3 className="text-xl mb-2">Daftar Pesanan:</h3>
            <ul className="list-disc pl-5 mb-4">
              {orders.map((order, index) => (
                <li key={index}>{order.menu} (x{order.quantity})</li>
              ))}
            </ul>

            <button
              onClick={handlePrintReceipt}
              className="w-full bg-green-500 text-white py-2 rounded-lg hover:bg-green-600 transition duration-300"
            >
              Cetak Nota
            </button>

            {/* Tombol Bayar untuk mengubah status menjadi lunas */}
            <button
              onClick={handlePayment}
              className={`w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition duration-300 mt-4 ${isPaid ? 'opacity-50 cursor-not-allowed' : ''}`}
              disabled={isPaid} // Menonaktifkan tombol jika sudah dibayar
            >
              Bayar
            </button>
          </div>
        )}

        {/* Menampilkan nota jika tombol cetak nota ditekan */}
        {showReceipt && (
          <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-md mt-10">
            <h2 className="text-2xl mb-4">Nota Transaksi</h2>
            <p className="font-bold">Wikusama Cafe</p>
            <p>Tanggal: {new Date().toLocaleDateString()}</p>
            <p>Nama Pelanggan: {customerName}</p>
            <p>Nomor Meja: {tableNumber}</p>
            <h3 className="text-xl mb-2">Daftar Pesanan:</h3>
            <ul className="list-disc pl-5 mb-4">
              {orders.map((order, index) => (
                <li key={index}>{order.menu} (x{order.quantity})</li>
              ))}
            </ul>
            <p>Status: {isPaid ? 'Lunas' : 'Belum Bayar'}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cashier;
