import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import LoginPage from './page/LoginPage';
import Cashier from './page/Cashier';

const App = () => {
  const [username, setUsername] = useState(''); // Awalnya kosong, diisi saat login
  const [role, setRole] = useState(''); // Awalnya kosong, diisi saat login

  const handleLogin = (user, userRole) => {
    setUsername(user);
    setRole(userRole);
  };

  return (
    <Router>
      <div className="flex">
        <Sidebar username={username} role={role} />
        <div className="flex-1">
          <Routes>
            <Route path="/" element={<LoginPage onLogin={handleLogin} />} />
            <Route path="/cashier" element={<Cashier username={username} />} />
            {/* Tambahkan route lain di sini */}
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
